import React, { createContext, useState, useContext, useEffect } from 'react';

const CartContext = createContext();

export const useCart = () => {
  return useContext(CartContext);
};

export const CartProvider = ({ children }) => {
  const [cartItems, setCartItems] = useState([]);

  useEffect(() => {
    const savedCart = localStorage.getItem('cart');
    if (savedCart) {
      setCartItems(JSON.parse(savedCart));
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('cart', JSON.stringify(cartItems));
  }, [cartItems]);

  const addToCart = (product) => {
    setCartItems(prevItems => {
      // Tạo unique key với size để phân biệt cùng sản phẩm khác size
      const productKey = `${product.id}_${product.selectedSize || 'no-size'}`;
      const existingItemIndex = prevItems.findIndex(
        item => item.id === product.id && item.selectedSize === product.selectedSize
      );

      if (existingItemIndex > -1) {
        // Update quantity if same product with same size exists
        const updatedItems = [...prevItems];
        updatedItems[existingItemIndex].quantity += product.quantity || 1;
        return updatedItems;
      } else {
        // Add new item with size info
        return [...prevItems, {
          ...product,
          quantity: product.quantity || 1,
          selectedSize: product.selectedSize || null,
          cartItemId: productKey // unique identifier for cart item
        }];
      }
    });
  };

  const removeFromCart = (cartItemId) => {
    setCartItems(prevItems => 
      prevItems.filter(item => 
        (item.cartItemId || item.id) !== cartItemId
      )
    );
  };

  const updateQuantity = (cartItemId, quantity) => {
    if (quantity <= 0) {
      removeFromCart(cartItemId);
      return;
    }
    
    setCartItems(prevItems =>
      prevItems.map(item =>
        (item.cartItemId || item.id) === cartItemId
          ? { ...item, quantity }
          : item
      )
    );
  };

  const clearCart = () => {
    setCartItems([]);
  };

  const getCartTotal = () => {
    return cartItems.reduce((total, item) => total + (item.price * item.quantity), 0);
  };

  const getCartItemsCount = () => {
    return cartItems.reduce((total, item) => total + item.quantity, 0);
  };

  const value = {
    cartItems,
    addToCart,
    removeFromCart,
    updateQuantity,
    clearCart,
    getCartTotal,
    getCartItemsCount
  };

  return (
    <CartContext.Provider value={{
      cartItems,
      addToCart,
      removeFromCart,
      updateQuantity,
      clearCart,
      getCartItemsCount,
      getCartTotal
    }}>
      {children}
    </CartContext.Provider>
  );
};
