import React, { useEffect, useState } from 'react';
import AdminLayout from '../../components/AdminLayout';
import axios from 'axios';

const AdminNews = () => {
  const [news, setNews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [form, setForm] = useState({ title: '', summary: '', image: '', date: '' });
  const [editingId, setEditingId] = useState(null);

  useEffect(() => {
    fetchNews();
  }, []);

  const fetchNews = async () => {
    setLoading(true);
    try {
      const res = await axios.get('/api/news');
      setNews(res.data);
    } catch {
      setNews([]);
    } finally {
      setLoading(false);
    }
  };

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    if (!form.title || !form.summary || !form.date) return;
    try {
      if (editingId) {
        await axios.put(`/api/news/${editingId}`, form);
      } else {
        await axios.post('/api/news', form);
      }
      setForm({ title: '', summary: '', image: '', date: '' });
      setEditingId(null);
      fetchNews();
    } catch {}
  };

  const handleEdit = item => {
    setForm({
      title: item.title,
      summary: item.summary,
      image: item.image || '',
      date: item.date
    });
    setEditingId(item.id);
  };

  const handleDelete = async id => {
    if (!window.confirm('Xóa tin tức này?')) return;
    try {
      await axios.delete(`/api/news/${id}`);
      fetchNews();
    } catch {}
  };

  return (
    <AdminLayout>
      <div className="container my-5">
        <h2 className="fw-bold mb-4 text-success">Quản lý tin tức</h2>
        <form className="card p-3 mb-4" onSubmit={handleSubmit}>
          <div className="row g-3">
            <div className="col-md-6">
              <label className="form-label">Tiêu đề</label>
              <input className="form-control" name="title" value={form.title} onChange={handleChange} required />
            </div>
            <div className="col-md-6">
              <label className="form-label">Ngày đăng</label>
              <input className="form-control" name="date" type="date" value={form.date} onChange={handleChange} required />
            </div>
            <div className="col-md-12">
              <label className="form-label">Tóm tắt</label>
              <textarea className="form-control" name="summary" value={form.summary} onChange={handleChange} rows={2} required />
            </div>
            <div className="col-md-12">
              <label className="form-label">Ảnh (URL)</label>
              <input className="form-control" name="image" value={form.image} onChange={handleChange} />
            </div>
          </div>
          <div className="mt-3">
            <button className="btn btn-success" type="submit">
              {editingId ? 'Cập nhật' : 'Thêm mới'}
            </button>
            {editingId && (
              <button className="btn btn-secondary ms-2" type="button" onClick={() => { setForm({ title: '', summary: '', image: '', date: '' }); setEditingId(null); }}>
                Hủy
              </button>
            )}
          </div>
        </form>
        <div className="table-responsive">
          <table className="table table-bordered align-middle">
            <thead>
              <tr>
                <th>Tiêu đề</th>
                <th>Ngày đăng</th>
                <th>Tóm tắt</th>
                <th>Ảnh</th>
                <th style={{width: 120}}>Thao tác</th>
              </tr>
            </thead>
            <tbody>
              {loading ? (
                <tr>
                  <td colSpan={5} className="text-center text-muted py-4">Đang tải...</td>
                </tr>
              ) : news.length === 0 ? (
                <tr>
                  <td colSpan={5} className="text-center text-muted py-4">Chưa có tin tức nào.</td>
                </tr>
              ) : (
                news.map(item => (
                  <tr key={item.id}>
                    <td>{item.title}</td>
                    <td>{item.date}</td>
                    <td>{item.summary}</td>
                    <td>
                      {item.image && <img src={item.image} alt="" style={{maxWidth: 80, maxHeight: 60, objectFit: 'cover'}} />}
                    </td>
                    <td>
                      <button className="btn btn-sm btn-outline-primary me-2" onClick={() => handleEdit(item)}>Sửa</button>
                      <button className="btn btn-sm btn-outline-danger" onClick={() => handleDelete(item.id)}>Xóa</button>
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>
      </div>
    </AdminLayout>
  );
};

export default AdminNews;
