import React, { useEffect, useState } from 'react';
import AdminLayout from '../../components/AdminLayout';
import axios from 'axios';

const formatDate = (dateStr) => {
  if (!dateStr) return '';
  const d = new Date(dateStr);
  return d.toLocaleDateString('vi-VN');
};

const AdminNews = () => {
  const [news, setNews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [form, setForm] = useState({ title: '', summary: '', image: '', date: '' });
  const [editingId, setEditingId] = useState(null);
  const [message, setMessage] = useState('');

  useEffect(() => {
    fetchNews();
  }, []);

  const fetchNews = async () => {
    setLoading(true);
    try {
      const res = await axios.get('/api/news');
      setNews(res.data);
    } catch {
      setNews([]);
    } finally {
      setLoading(false);
    }
  };

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    if (!form.title || !form.summary || !form.date) {
      setMessage('Vui lòng nhập đầy đủ thông tin.');
      return;
    }
    try {
      if (editingId) {
        await axios.put(`/api/news/${editingId}`, form);
        setMessage('Cập nhật tin tức thành công!');
      } else {
        await axios.post('/api/news', form);
        setMessage('Thêm tin tức thành công!');
      }
      setForm({ title: '', summary: '', image: '', date: '' });
      setEditingId(null);
      fetchNews();
    } catch {
      setMessage('Có lỗi xảy ra, vui lòng thử lại.');
    }
  };

  const handleEdit = item => {
    setForm({
      title: item.title,
      summary: item.summary,
      image: item.image || '',
      date: item.date
    });
    setEditingId(item.id);
    setMessage('');
  };

  const handleDelete = async id => {
    if (!window.confirm('Xóa tin tức này?')) return;
    try {
      await axios.delete(`/api/news/${id}`);
      setMessage('Đã xóa tin tức.');
      fetchNews();
    } catch {
      setMessage('Có lỗi khi xóa.');
    }
  };

  const handleCancel = () => {
    setForm({ title: '', summary: '', image: '', date: '' });
    setEditingId(null);
    setMessage('');
  };

  return (
    <AdminLayout>
      <div className="admin-card mb-4">
        <div className="card-header d-flex align-items-center">
          <i className="bi bi-newspaper me-2 fs-4 text-success"></i>
          <h4 className="mb-0">{editingId ? 'Cập nhật tin tức' : 'Thêm tin tức mới'}</h4>
        </div>
        <div className="card-body">
          {message && <div className="alert alert-info py-2">{message}</div>}
          <form className="row g-3" onSubmit={handleSubmit}>
            <div className="col-md-6">
              <label className="form-label">Tiêu đề</label>
              <input className="form-control" name="title" value={form.title} onChange={handleChange} required />
            </div>
            <div className="col-md-6">
              <label className="form-label">Ngày đăng</label>
              <input className="form-control" name="date" type="date" value={form.date} onChange={handleChange} required />
            </div>
            <div className="col-md-12">
              <label className="form-label">Tóm tắt</label>
              <textarea className="form-control" name="summary" value={form.summary} onChange={handleChange} rows={2} required />
            </div>
            <div className="col-md-12">
              <label className="form-label">Ảnh (URL)</label>
              <input className="form-control" name="image" value={form.image} onChange={handleChange} />
            </div>
            <div className="col-12 mt-2">
              <button className="btn btn-success" type="submit">
                {editingId ? 'Cập nhật' : 'Thêm mới'}
              </button>
              {editingId && (
                <button className="btn btn-secondary ms-2" type="button" onClick={handleCancel}>
                  Hủy
                </button>
              )}
            </div>
          </form>
        </div>
      </div>
      <div className="admin-card">
        <div className="card-header d-flex justify-content-between align-items-center">
          <h5 className="mb-0"><i className="bi bi-list-ul me-2"></i>Danh sách tin tức</h5>
        </div>
        <div className="card-body p-0">
          <div className="table-responsive">
            <table className="table table-bordered align-middle mb-0">
              <thead className="table-light">
                <tr>
                  <th style={{width: 40}}>#</th>
                  <th>Tiêu đề</th>
                  <th>Ngày đăng</th>
                  <th>Tóm tắt</th>
                  <th>Ảnh</th>
                  <th style={{width: 120}}>Thao tác</th>
                </tr>
              </thead>
              <tbody>
                {loading ? (
                  <tr>
                    <td colSpan={6} className="text-center text-muted py-4">Đang tải...</td>
                  </tr>
                ) : news.length === 0 ? (
                  <tr>
                    <td colSpan={6} className="text-center text-muted py-4">Chưa có tin tức nào.</td>
                  </tr>
                ) : (
                  news.map((item, idx) => (
                    <tr key={item.id}>
                      <td>{idx + 1}</td>
                      <td className="fw-bold">{item.title}</td>
                      <td>{formatDate(item.date)}</td>
                      <td>{item.summary}</td>
                      <td>
                        {item.image && <img src={item.image} alt="" style={{maxWidth: 80, maxHeight: 60, objectFit: 'cover', borderRadius: 4}} />}
                      </td>
                      <td>
                        <button className="btn btn-sm btn-outline-primary me-2" onClick={() => handleEdit(item)}>Sửa</button>
                        <button className="btn btn-sm btn-outline-danger" onClick={() => handleDelete(item.id)}>Xóa</button>
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </AdminLayout>
  );
};

export default AdminNews;
