import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const Home = () => {
  const [featuredProducts, setFeaturedProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchFeaturedProducts();
  }, []);

  const fetchFeaturedProducts = async () => {
    try {
      const response = await axios.get('/api/products');
      setFeaturedProducts(response.data.slice(0, 8));
    } catch (error) {
      console.error('Error fetching products:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      {/* Hero Section */}
      <div className="bg-primary text-white py-5">
        <div className="container">
          <div className="row align-items-center">
            <div className="col-md-6">
              <h1 className="display-4 fw-bold">Badminton Shop</h1>
              <p className="lead">Cửa hàng thiết bị cầu lông chất lượng cao</p>
              <Link to="/products" className="btn btn-light btn-lg">
                Xem sản phẩm
              </Link>
            </div>
            <div className="col-md-6">
              <img 
                src="https://via.placeholder.com/500x300/007bff/ffffff?text=Badminton" 
                alt="Badminton" 
                className="img-fluid rounded"
              />
            </div>
          </div>
        </div>
      </div>

      {/* Featured Products */}
      <div className="container my-5">
        <h2 className="text-center mb-4">Sản phẩm nổi bật</h2>
        
        {loading ? (
          <div className="text-center">
            <div className="spinner-border" role="status">
              <span className="visually-hidden">Loading...</span>
            </div>
          </div>
        ) : (
          <div className="row">
            {featuredProducts.map(product => (
              <div key={product.id} className="col-md-3 mb-4">
                <div className="card product-card h-100">
                  <img 
                    src={product.image_url || 'https://via.placeholder.com/300x200'} 
                    className="card-img-top" 
                    alt={product.name}
                    style={{ height: '200px', objectFit: 'cover' }}
                  />
                  <div className="card-body d-flex flex-column">
                    <h5 className="card-title">{product.name}</h5>
                    <p className="card-text text-muted">{product.category_name}</p>
                    <p className="card-text fw-bold text-primary fs-5">
                      {new Intl.NumberFormat('vi-VN', { 
                        style: 'currency', 
                        currency: 'VND' 
                      }).format(product.price)}
                    </p>
                    <Link 
                      to={`/products/${product.id}`} 
                      className="btn btn-primary mt-auto"
                    >
                      Xem chi tiết
                    </Link>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
        
        <div className="text-center mt-4">
          <Link to="/products" className="btn btn-outline-primary btn-lg">
            Xem tất cả sản phẩm
          </Link>
        </div>
      </div>

      {/* Features */}
      <div className="bg-light py-5">
        <div className="container">
          <div className="row">
            <div className="col-md-4 text-center">
              <i className="bi bi-truck fs-1 text-primary"></i>
              <h4>Giao hàng nhanh</h4>
              <p>Giao hàng toàn quốc trong 24h</p>
            </div>
            <div className="col-md-4 text-center">
              <i className="bi bi-shield-check fs-1 text-primary"></i>
              <h4>Chất lượng đảm bảo</h4>
              <p>Sản phẩm chính hãng 100%</p>
            </div>
            <div className="col-md-4 text-center">
              <i className="bi bi-headset fs-1 text-primary"></i>
              <h4>Hỗ trợ 24/7</h4>
              <p>Tư vấn và hỗ trợ mọi lúc</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
