import React, { useEffect, useState } from "react";
import AdminLayout from "../../components/AdminLayout";
import { Table, Button, Modal, Form, Badge } from "react-bootstrap";
import axios from "axios";
import { showSuccess, showError } from "../../utils/notify";
import { getAuthHeaders } from "../../utils/auth";

const AdminSizes = () => {
  const [sizes, setSizes] = useState([]);
  const [products, setProducts] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [showProductModal, setShowProductModal] = useState(false);
  const [editingSize, setEditingSize] = useState(null);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [form, setForm] = useState({
    name: "",
    description: "",
    category_type: "shoes",
    sort_order: 0,
    status: 1,
  });

  // L·∫•y danh s√°ch size
  const fetchSizes = async () => {
    try {
      const res = await axios.get("/api/sizes");
      setSizes(res.data);
    } catch (err) {
      showError("L·ªói t·∫£i danh s√°ch size");
    }
  };

  // L·∫•y danh s√°ch s·∫£n ph·∫©m
  const fetchProducts = async () => {
    try {
      const res = await axios.get("/api/products");
      setProducts(res.data);
    } catch (err) {
      showError("L·ªói t·∫£i danh s√°ch s·∫£n ph·∫©m");
    }
  };

  useEffect(() => {
    fetchSizes();
    fetchProducts();
  }, []);

  // M·ªü modal th√™m/s·ª≠a
  const handleShowModal = (size = null) => {
    setEditingSize(size);
    setForm(
      size || {
        name: "",
        description: "",
        category_type: "shoes",
        sort_order: 0,
        status: 1,
      }
    );
    setShowModal(true);
  };

  const handleCloseModal = () => setShowModal(false);

  // M·ªü modal ch·ªçn s·∫£n ph·∫©m
  const handleShowProductModal = () => setShowProductModal(true);

  const handleCloseProductModal = () => setShowProductModal(false);

  // Ch·ªçn s·∫£n ph·∫©m
  const handleSelectProduct = (product) => {
    setSelectedProduct(product);
    handleCloseProductModal();
  };

  // Submit form
  const handleSubmit = async () => {
    try {
      if (editingSize) {
        await axios.put(`/api/sizes/${editingSize.id}`, form, {
          headers: getAuthHeaders(),
        });
        showSuccess("C·∫≠p nh·∫≠t size th√†nh c√¥ng!");
      } else {
        await axios.post(
          "/api/sizes",
          { ...form, product_id: selectedProduct?.id },
          {
            headers: getAuthHeaders(),
          }
        );
        showSuccess("Th√™m size th√†nh c√¥ng!");
      }
      fetchSizes();
      handleCloseModal();
    } catch (err) {
