import React, { useState } from 'react';
import { useAuth } from '../contexts/AuthContext';
import axios from 'axios';

const Profile = () => {
  const { currentUser, logout } = useAuth();
  const [formData, setFormData] = useState({
    name: currentUser?.name || '',
    username: currentUser?.username || '',
    email: currentUser?.email || '',
    password: '',
    confirmPassword: '',
    confirmEmail: currentUser?.email || '',
    facebook: currentUser?.facebook || '',
    twitter: currentUser?.twitter || '',
  });
  const [avatar, setAvatar] = useState(currentUser?.avatar || '');
  const [avatarFile, setAvatarFile] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleAvatarChange = e => {
    if (e.target.files && e.target.files[0]) {
      setAvatarFile(e.target.files[0]);
      setAvatar(URL.createObjectURL(e.target.files[0]));
    }
  };

  const handleAvatarUpload = async () => {
    if (!avatarFile) return;
    const data = new FormData();
    data.append('avatar', avatarFile);
    try {
      const res = await axios.post(`/api/users/${currentUser.id}/avatar`, data);
      setAvatar(res.data.avatarUrl);
      alert('Cập nhật ảnh đại diện thành công!');
    } catch {
      alert('Lỗi khi cập nhật ảnh đại diện!');
    }
  };

  const handleSave = async e => {
    e.preventDefault();
    if (formData.password && formData.password !== formData.confirmPassword) {
      alert('Mật khẩu xác nhận không khớp!');
      return;
    }
    if (formData.email !== formData.confirmEmail) {
      alert('Email xác nhận không khớp!');
      return;
    }
    setLoading(true);
    try {
      await axios.put(`/api/users/${currentUser.id}`, {
        ...formData,
        avatar
      });
      alert('Cập nhật thông tin thành công!');
      window.location.reload();
    } catch {
      alert('Cập nhật thất bại!');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container my-5">
      <div className="row justify-content-center g-4">
        {/* Avatar & Info Card */}
        <div className="col-lg-4">
          <div className="card p-4 text-center">
            <h4 className="fw-bold mb-1">{formData.name}</h4>
            <div className="text-muted mb-2">@{formData.username}</div>
            <div className="mb-3">
              <div className="position-relative d-inline-block">
                <img
                  src={avatar || 'https://via.placeholder.com/120x120?text=Avatar'}
                  alt="Avatar"
                  className="rounded-circle"
                  style={{ width: 120, height: 120, objectFit: 'cover', border: '2px solid #eee' }}
                />
                <label
                  htmlFor="avatar-upload"
                  className="position-absolute bottom-0 end-0 btn btn-sm btn-light border"
                  style={{ cursor: 'pointer' }}
                  title="Đổi ảnh"
                >
                  <i className="bi bi-pencil"></i>
                </label>
                <input
                  id="avatar-upload"
                  type="file"
                  accept="image/*"
                  style={{ display: 'none' }}
                  onChange={handleAvatarChange}
                />
              </div>
            </div>
            <button
              className="btn btn-danger w-100 mb-2"
              type="button"
              onClick={handleAvatarUpload}
              disabled={!avatarFile}
            >
              Upload New Photo
            </button>
            <div className="mb-3 text-muted small">
              Upload a new avatar. Larger image will be resized automatically.<br />
              Maximum upload size is 1 MB.
            </div>
            <div className="text-muted small">
              Member Since: {currentUser?.created_at ? new Date(currentUser.created_at).toLocaleDateString('vi-VN') : '-'}
            </div>
          </div>
        </div>
        {/* Edit Profile Form */}
        <div className="col-lg-8">
          <div className="card p-4">
            <h3 className="fw-bold mb-4">Edit Profile</h3>
            <form onSubmit={handleSave}>
              <div className="row mb-3">
                <div className="col-md-6 mb-3 mb-md-0">
                  <label className="form-label">Full Name</label>
                  <input
                    type="text"
                    name="name"
                    className="form-control"
                    value={formData.name}
                    onChange={handleChange}
                    required
                  />
                </div>
                <div className="col-md-6">
                  <label className="form-label">Username</label>
                  <input
                    type="text"
                    name="username"
                    className="form-control"
                    value={formData.username}
                    onChange={handleChange}
                    required
                  />
                </div>
              </div>
              <div className="row mb-3">
                <div className="col-md-6 mb-3 mb-md-0">
                  <label className="form-label">Password</label>
                  <input
                    type="password"
                    name="password"
                    className="form-control"
                    value={formData.password}
                    onChange={handleChange}
                  />
                </div>
                <div className="col-md-6">
                  <label className="form-label">Confirm Password</label>
                  <input
                    type="password"
                    name="confirmPassword"
                    className="form-control"
                    value={formData.confirmPassword}
                    onChange={handleChange}
                  />
                </div>
              </div>
              <div className="row mb-3">
                <div className="col-md-6 mb-3 mb-md-0">
                  <label className="form-label">Email Address</label>
                  <input
                    type="email"
                    name="email"
                    className="form-control"
                    value={formData.email}
                    onChange={handleChange}
                    required
                  />
                </div>
                <div className="col-md-6">
                  <label className="form-label">Confirm Email Address</label>
                  <input
                    type="email"
                    name="confirmEmail"
                    className="form-control"
                    value={formData.confirmEmail}
                    onChange={handleChange}
                    required
                  />
                </div>
              </div>
              <div className="row mb-3">
                <div className="col-md-6 mb-3 mb-md-0">
                  <label className="form-label">Facebook Username</label>
                  <input
                    type="text"
                    name="facebook"
                    className="form-control"
                    value={formData.facebook}
                    onChange={handleChange}
                  />
                </div>
                <div className="col-md-6">
                  <label className="form-label">Twitter Username</label>
                  <input
                    type="text"
                    name="twitter"
                    className="form-control"
                    value={formData.twitter}
                    onChange={handleChange}
                  />
                </div>
              </div>
              <button type="submit" className="btn btn-danger px-4" disabled={loading}>
                {loading ? 'Đang cập nhật...' : 'Update info'}
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
