import React, { useEffect, useState } from "react";
import AdminLayout from "../../components/AdminLayout";
import Modal from "../../components/Modal";
import axios from "axios";
import { useModal } from "../../hooks/useModal";
import { getAuthHeaders } from "../../utils/auth";

const AdminSizes = () => {
  const [sizes, setSizes] = useState([]);
  const [products, setProducts] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [editingSize, setEditingSize] = useState(null);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [formData, setFormData] = useState({
    name: "",
    description: "",
    category_type: "shoes",
    sort_order: 0,
    status: 1,
    product_id: null,
  });

  const { modal, hideModal, showSuccess, showError, showConfirm } = useModal();

  useEffect(() => {
    fetchSizes();
    fetchProducts();
  }, []);

  const fetchSizes = async () => {
    try {
      const res = await axios.get("/api/sizes");
      setSizes(res.data);
    } catch (err) {
      showError("Lỗi tải danh sách size!");
    }
  };

  const fetchProducts = async () => {
    try {
      const res = await axios.get("/api/products");
      setProducts(res.data);
    } catch (err) {
      showError("Lỗi tải danh sách sản phẩm!");
    }
  };

  const resetForm = () => {
    setFormData({
      name: "",
      description: "",
      category_type: "shoes",
      sort_order: 0,
      status: 1,
      product_id: null,
    });
    setSelectedProduct(null);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.name) {
      showError("Tên size không được để trống!");
      return;
    }

    try {
      if (editingSize) {
        await axios.put(`/api/sizes/${editingSize.id}`, formData, {
          headers: getAuthHeaders(),
        });
        showSuccess("Cập nhật size thành công!");
      } else {
        await axios.post("/api/sizes", formData, {
          headers: getAuthHeaders(),
        });
        showSuccess("Thêm size thành công!");
      }
      setShowModal(false);
      setEditingSize(null);
      resetForm();
      fetchSizes();
    } catch (err) {
      showError("Lỗi khi lưu size!");
    }
  };

  const handleEdit = (size) => {
    setEditingSize(size);
    setFormData(size);
    setSelectedProduct(products.find((p) => p.id === size.product_id) || null);
    setShowModal(true);
  };

  const handleDelete = (id) => {
    showConfirm("Bạn có chắc muốn xóa size này?", async () => {
      try {
        await axios.delete(`/api/sizes/${id}`, {
          headers: getAuthHeaders(),
        });
        showSuccess("Xóa size thành công!");
        fetchSizes();
      } catch (err) {
        showError("Lỗi khi xóa size!");
      }
    });
  };

  const handleModalClose = () => {
    setShowModal(false);
    setEditingSize(null);
    resetForm();
  };

  return (
    <AdminLayout>
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>📏 Quản lý Size</h2>
        <button
          className="btn btn-admin-primary"
          onClick={() => setShowModal(true)}
        >
          ➕ Thêm Size
        </button>
      </div>

      <div className="admin-table">
        <table className="table">
          <thead>
            <tr>
              <th>#</th>
              <th>Tên</th>
              <th>Mô tả</th>
              <th>Loại</th>
              <th>Thứ tự</th>
              <th>Sản phẩm</th>
              <th>Trạng thái</th>
              <th>Thao tác</th>
            </tr>
          </thead>
          <tbody>
            {sizes.map((size, idx) => (
              <tr key={size.id}>
                <td>{idx + 1}</td>
                <td>{size.name}</td>
                <td>{size.description}</td>
                <td>{size.category_type}</td>
                <td>{size.sort_order}</td>
                <td>
                  {products.find((p) => p.id === size.product_id)?.name ||
                    "Chưa chọn"}
                </td>
                <td>
                  {size.status === 1 ? (
                    <span className="badge bg-success">Kích hoạt</span>
                  ) : (
                    <span className="badge bg-secondary">Không kích hoạt</span>
                  )}
                </td>
                <td>
                  <button
                    className="btn btn-sm btn-outline-primary me-2"
                    onClick={() => handleEdit(size)}
                  >
                    Sửa
                  </button>
                  <button
                    className="btn btn-sm btn-outline-danger"
                    onClick={() => handleDelete(size.id)}
                  >
                    Xóa
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Modal thêm/sửa */}
      {showModal && (
        <div className="modal show d-block admin-modal" tabIndex="-1">
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">
                  {editingSize ? "Sửa Size" : "Thêm Size"}
                </h5>
                <button
                  type="button"
                  className="btn-close"
                  onClick={handleModalClose}
                ></button>
              </div>
              <form onSubmit={handleSubmit}>
                <div className="modal-body">
                  <div className="mb-3">
                    <label className="form-label">Tên Size:</label>
                    <input
                      type="text"
                      className="form-control"
                      value={formData.name}
                      onChange={(e) =>
                        setFormData({ ...formData, name: e.target.value })
                      }
                      required
                    />
                  </div>

                  <div className="mb-3">
                    <label className="form-label">Mô tả:</label>
                    <textarea
                      className="form-control"
                      rows="2"
                      value={formData.description}
                      onChange={(e) =>
                        setFormData({
                          ...formData,
                          description: e.target.value,
                        })
                      }
                    />
                  </div>

                  <div className="mb-3">
                    <label className="form-label">Loại:</label>
                    <select
                      className="form-select"
                      value={formData.category_type}
                      onChange={(e) =>
                        setFormData({
                          ...formData,
                          category_type: e.target.value,
                        })
                      }
                    >
                      <option value="shoes">Giày</option>
                      <option value="clothing">Quần áo</option>
                    </select>
                  </div>

                  <div className="mb-3">
                    <label className="form-label">Thứ tự sắp xếp:</label>
                    <input
                      type="number"
                      className="form-control"
                      value={formData.sort_order}
                      onChange={(e) =>
                        setFormData({
                          ...formData,
                          sort_order: parseInt(e.target.value),
                        })
                      }
                    />
                  </div>

                  <div className="mb-3">
                    <label className="form-label">Sản phẩm:</label>
                    <select
                      className="form-select"
                      value={formData.product_id || ""}
                      onChange={(e) =>
                        setFormData({
                          ...formData,
                          product_id: parseInt(e.target.value),
                        })
                      }
                    >
                      <option value="">Chọn sản phẩm</option>
                      {products.map((p) => (
                        <option key={p.id} value={p.id}>
                          {p.name}
                        </option>
                      ))}
                    </select>
                  </div>

                  <div className="mb-3">
                    <label className="form-label">Trạng thái:</label>
                    <select
                      className="form-select"
                      value={formData.status}
                      onChange={(e) =>
                        setFormData({
                          ...formData,
                          status: parseInt(e.target.value),
                        })
                      }
                    >
                      <option value={1}>Kích hoạt</option>
                      <option value={0}>Không kích hoạt</option>
                    </select>
                  </div>
                </div>

                <div className="modal-footer">
                  <button
                    type="button"
                    className="btn btn-secondary"
                    onClick={handleModalClose}
                  >
                    Hủy
                  </button>
                  <button type="submit" className="btn btn-primary">
                    {editingSize ? "Cập nhật" : "Thêm"}
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}

      <Modal
        show={modal.show}
        onClose={hideModal}
        title={modal.title}
        message={modal.message}
        type={modal.type}
        onConfirm={modal.onConfirm}
        confirmText={modal.confirmText}
        cancelText={modal.cancelText}
        showCancel={modal.showCancel}
      />
    </AdminLayout>
  );
};

export default AdminSizes;
