import React, { useEffect, useState } from "react";
import { fetchOrders, updateOrderStatus } from "../services/orderService";

const AdminOrders = () => {
  const [orders, setOrders] = useState([]);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [showStatusModal, setShowStatusModal] = useState(false);

  useEffect(() => {
    const loadOrders = async () => {
      const data = await fetchOrders();
      setOrders(data);
    };
    loadOrders();
  }, []);

  const handleStatusChange = async (newStatus) => {
    if (!selectedOrder) return;
    try {
      const response = await updateOrderStatus(selectedOrder.id, newStatus);

      // Cập nhật trực tiếp order trong state
      setOrders((prev) =>
        prev.map((order) =>
          order.id === selectedOrder.id ? { ...order, ...response.order } : order
        )
      );

      setShowStatusModal(false);
      setSelectedOrder(null);
    } catch (error) {
      console.error("Failed to update order status:", error);
    }
  };

  return (
    <div>
      <h2>Danh sách đơn hàng</h2>
      <table>
        <thead>
          <tr>
            <th>Mã đơn</th>
            <th>Người đặt</th>
            <th>Trạng thái</th>
            <th>Hành động</th>
          </tr>
        </thead>
        <tbody>
          {orders.map((order) => (
            <tr key={order.id}>
              <td>{order.id}</td>
              <td>{order.user_id}</td>
              <td>{order.status}</td>
              <td>
                <button
                  onClick={() => {
                    setSelectedOrder(order);
                    setShowStatusModal(true);
                  }}
                >
                  Đổi trạng thái
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Modal đổi trạng thái */}
      {showStatusModal && selectedOrder && (
        <div className="modal">
          <h3>Cập nhật trạng thái đơn hàng #{selectedOrder.id}</h3>
          <select
            defaultValue={selectedOrder.status}
            onChange={(e) => handleStatusChange(e.target.value)}
          >
            <option value="pending">Chờ xử lý</option>
            <option value="confirmed">Đã xác nhận</option>
            <option value="shipped">Đã giao hàng</option>
            <option value="cancelled">Đã hủy</option>
          </select>
          <button onClick={() => setShowStatusModal(false)}>Đóng</button>
        </div>
      )}
    </div>
  );
};

export default AdminOrders;
