import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useCart } from '../contexts/CartContext';
import Modal from '../components/Modal';
import { useModal } from '../hooks/useModal';
import axios from 'axios';

const ProductDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { addToCart } = useCart();
  const { modal, hideModal, showSuccess, showError } = useModal();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [quantity, setQuantity] = useState(1);
  const [selectedSize, setSelectedSize] = useState('');
  const [availableSizes, setAvailableSizes] = useState([]);
  const [sizeRequired, setSizeRequired] = useState(false);

  useEffect(() => {
    fetchProduct();
  }, [id]);

  useEffect(() => {
    if (product) {
      fetchProductSizes();
      checkIfSizeRequired();
    }
  }, [product]);

  const fetchProduct = async () => {
    try {
      const response = await axios.get(`/api/products/${id}`);
      setProduct(response.data);
    } catch (error) {
      console.error('Error fetching product:', error);
      if (error.response?.status === 404) {
        navigate('/products');
      }
    } finally {
      setLoading(false);
    }
  };

  const fetchProductSizes = async () => {
    try {
      const response = await axios.get(`/api/sizes/product/${id}`);
      setAvailableSizes(response.data);
    } catch (error) {
      console.error('Error fetching sizes:', error);
    }
  };

  const checkIfSizeRequired = () => {
    if (!product) return;
    
    const sizeRequiredCategories = [
      'Giày cầu lông', 'Áo cầu lông', 'Quần cầu lông', 'Váy cầu lông'
    ];
    
    setSizeRequired(sizeRequiredCategories.includes(product.category_name));
  };

  const getAvailableStock = () => {
    if (!sizeRequired || !selectedSize) {
      return product?.stock_quantity || 0;
    }
    
    const sizeData = availableSizes.find(size => size.id === parseInt(selectedSize));
    return sizeData?.stock_quantity || 0;
  };

  const handleAddToCart = () => {
    if (!product) return;
    
    if (sizeRequired && !selectedSize) {
      showError('Vui lòng chọn size!');
      return;
    }
    
    const availableStock = getAvailableStock();
    if (availableStock === 0) {
      showError('Size này hiện đã hết hàng!');
      return;
    }
    
    if (quantity > availableStock) {
      showError(`Chỉ còn ${availableStock} sản phẩm trong kho!`);
      return;
    }

    const cartItem = {
      ...product,
      quantity: quantity
    };
    
    if (sizeRequired && selectedSize) {
      const sizeData = availableSizes.find(size => size.id === parseInt(selectedSize));
      cartItem.selectedSize = {
        id: sizeData.id,
        name: sizeData.name
      };
    }
    
    addToCart(cartItem);
    showSuccess('Đã thêm sản phẩm vào giỏ hàng!');
  };

  if (loading) {
    return (
      <div className="container my-5">
        <div className="text-center py-5">
          <div className="spinner-border spinner-border-lg" role="status">
            <span className="visually-hidden">Đang tải...</span>
          </div>
        </div>
      </div>
    );
  }

  if (!product) {
    return (
      <div className="container my-5">
        <div className="text-center py-5">
          <h3>Không tìm thấy sản phẩm</h3>
          <button 
            className="btn btn-dark mt-3"
            onClick={() => navigate('/products')}
          >
            Quay lại danh sách sản phẩm
          </button>
        </div>
      </div>
    );
  }

  const currentPrice = product.sale_price || product.price;
  const originalPrice = product.price;
  const discountPercentage = product.sale_price 
    ? Math.round(((originalPrice - product.sale_price) / originalPrice) * 100)
    : 0;

  const availableStock = getAvailableStock();

  return (
    <>
      <div className="container my-5">
        {/* Breadcrumb */}
        <nav aria-label="breadcrumb" className="mb-4">
          <ol className="breadcrumb">
            <li className="breadcrumb-item">
              <button 
                className="btn btn-link p-0"
                onClick={() => navigate('/')}
                style={{ color: '#00a61eff', textDecoration: 'underline', fontWeight: 500 }}
              >
                Trang chủ
              </button>
            </li>
            <li className="breadcrumb-item">
              <button 
                className="btn btn-link p-0"
                onClick={() => navigate('/products')}
                style={{ color: '#00a61eff', textDecoration: 'underline', fontWeight: 500 }}
              >
                Sản phẩm
              </button>
            </li>
            <li className="breadcrumb-item active">{product.name}</li>
          </ol>
        </nav>

        <div className="row g-5">
          {/* Product Image */}
          <div className="col-lg-5">
            <div className="position-relative">
              <img 
                src={product.image_url || 'https://via.placeholder.com/600x400/f8f9fa/6c757d?text=No+Image'} 
                alt={product.name}
                className="img-fluid rounded shadow-lg w-100"
                style={{ maxHeight: '500px', objectFit: 'cover' }}
              />
              {availableStock === 0 && (
                <div className="position-absolute top-0 start-0 m-3">
                  <span className="badge bg-danger fs-6 p-2" style={{fontWeight:600}}>Hết hàng</span>
                </div>
              )}
              {discountPercentage > 0 && (
                <div className="position-absolute top-0 end-0 m-3">
                  <span className="badge bg-warning text-dark fs-6 p-2">
                    SALE {discountPercentage}%
                  </span>
                </div>
              )}
            </div>
          </div>

          {/* Product Info */}
          <div className="col-lg-7">
            <h1 className="fw-bold text-dark mb-2" style={{fontSize:'2rem'}}>{product.name}</h1>
            <div className="mb-2">
              <span className="badge bg-primary me-2">{product.category_name}</span>
              {product.brand_name && (
                <span className="badge bg-success">{product.brand_name}</span>
              )}
            </div>
            <div className="mb-3">
              <span className="fw-bold text-danger" style={{fontSize:'2rem'}}>
                {new Intl.NumberFormat('vi-VN', { 
                  style: 'currency', 
                  currency: 'VND' 
                }).format(currentPrice)}
              </span>
              {product.sale_price && (
                <span className="fs-5 text-decoration-line-through text-muted ms-3">
                  {new Intl.NumberFormat('vi-VN', { 
                    style: 'currency', 
                    currency: 'VND' 
                  }).format(originalPrice)}
                </span>
              )}
            </div>
            
            {/* Size Selection */}
            {sizeRequired && availableSizes.length > 0 && (
              <div className="mb-3">
                <label className="form-label fw-medium">
                  Chọn size: {selectedSize && (
                    <span className="text-muted">
                      (Còn {getAvailableStock()} sản phẩm)
                    </span>
                  )}
                </label>
                <div className="d-flex flex-wrap gap-2">
                  {availableSizes.map(size => (
                    <button
                      key={size.id}
                      className={`btn ${selectedSize === size.id.toString() ? 'btn-success' : 'btn-outline-secondary'} ${size.stock_quantity === 0 ? 'disabled' : ''}`}
                      onClick={() => setSelectedSize(size.id.toString())}
                      disabled={size.stock_quantity === 0}
                      style={{ minWidth: '50px' }}
                    >
                      {size.name}
                      {size.stock_quantity === 0 && (
                        <small className="d-block text-muted">Hết</small>
                      )}
                    </button>
                  ))}
                </div>
              </div>
            )}
            
            <div className="mb-3">
              {availableStock > 0 ? (
                <span className="text-success fw-bold">
                  Còn hàng ({availableStock})
                </span>
              ) : (
                <span className="text-danger fw-bold">Hết hàng</span>
              )}
            </div>
            
            {product.short_description && (
              <div className="mb-3">
                <strong>Đặc điểm:</strong>
                <div className="text-muted">{product.short_description}</div>
              </div>
            )}
            
            {/* Add to Cart */}
            {availableStock > 0 && (
              <div className="mb-4">
                <div className="row g-3 align-items-end">
                  <div className="col-auto">
                    <label className="form-label fw-medium">Số lượng:</label>
                    <div className="input-group" style={{width: '150px'}}>
                      <button 
                        className="btn btn-outline-success"
                        onClick={() => setQuantity(Math.max(1, quantity - 1))}
                      >
                        <i className="bi bi-dash"></i>
                      </button>
                      <input 
                        type="number" 
                        className="form-control text-center"
                        value={quantity}
                        onChange={(e) => setQuantity(Math.max(1, Math.min(availableStock, parseInt(e.target.value) || 1)))}
                        min="1"
                        max={availableStock}
                      />
                      <button 
                        className="btn btn-outline-success"
                        onClick={() => setQuantity(Math.min(availableStock, quantity + 1))}
                      >
                        <i className="bi bi-plus"></i>
                      </button>
                    </div>
                  </div>
                  <div className="col">
                    <button 
                      className="btn btn-outline-success fw-bold btn-lg px-3 fs-6" 
                      style={{height:'38px'}}
                      onClick={handleAddToCart}
                    >
                      <i className="bi bi-cart-plus me-2"></i>
                      Thêm vào giỏ hàng
                    </button>
                  </div>
                </div>
              </div>
            )}
            
            {/* Info Table */}
            <div className="mb-4">
              <table className="table table-bordered">
                <tbody>
                  <tr>
                    <th style={{width:'160px'}}>Mã sản phẩm</th>
                    <td>{product.sku}</td>
                  </tr>
                  {product.weight && (
                    <tr>
                      <th>Trọng lượng</th>
                      <td>{product.weight}g</td>
                    </tr>
                  )}
                  <tr>
                    <th>Danh mục</th>
                    <td>{product.category_name}</td>
                  </tr>
                  {product.brand_name && (
                    <tr>
                      <th>Thương hiệu</th>
                      <td>{product.brand_name}</td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
            
            {/* Features */}
            <div className="row mb-2">
              <div className="col-md-4 mb-2">
                <div className="border rounded p-2 d-flex align-items-center gap-2">
                  <span className="badge bg-success rounded-circle" style={{width:'32px',height:'32px', paddingTop: '10px'}}>1</span>
                  <span>Cam kết chính hãng 100%</span>
                </div>
              </div>
              <div className="col-md-4 mb-2">
                <div className="border rounded p-2 d-flex align-items-center gap-2">
                  <span className="badge bg-success rounded-circle" style={{width:'32px',height:'32px', paddingTop: '10px'}}>2</span>
                  <span>Bảo hành theo chính sách công ty</span>
                </div>
              </div>
              <div className="col-md-4 mb-2">
                <div className="border rounded p-2 d-flex align-items-center gap-2">
                  <span className="badge bg-success rounded-circle" style={{width:'32px',height:'32px', paddingTop: '10px'}}>3</span>
                  <span>Giao hàng nhanh toàn quốc</span>
                </div>
              </div>
            </div>
          </div>
        </div>
        
        {/* Product Description */}
        {product.description && (
          <div className="row mt-5">
            <div className="col-12">
              <div className="card">
                <div className="card-header">
                  <h5 className="mb-0 text-success">
                    <i className="bi bi-file-text me-2"></i>
                    Mô tả sản phẩm
                  </h5>
                </div>
                <div className="card-body">
                  <div className="product-description" style={{whiteSpace: 'pre-line'}}>
                    {product.description}
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
      
      <Modal
        show={modal.show}
        onClose={hideModal}
        title={modal.title}
        message={modal.message}
        type={modal.type}
        onConfirm={modal.onConfirm}
        confirmText={modal.confirmText}
        cancelText={modal.cancelText}
        showCancel={modal.showCancel}
      />
    </>
  );
};

export default ProductDetail;
