import React, { useEffect, useState } from 'react';
import AdminLayout from '../../components/AdminLayout';
import axios from 'axios';

const formatDate = (dateStr) => {
  if (!dateStr) return '';
  const d = new Date(dateStr);
  return d.toLocaleDateString('vi-VN');
};

const initialForm = { title: '', summary: '', image: '', date: '' };

const AdminNews = () => {
  const [news, setNews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [form, setForm] = useState(initialForm);
  const [editingId, setEditingId] = useState(null);
  const [showForm, setShowForm] = useState(false);
  const [message, setMessage] = useState('');

  useEffect(() => {
    fetchNews();
  }, []);

  const fetchNews = async () => {
    setLoading(true);
    try {
      const res = await axios.get('/api/news');
      setNews(res.data);
    } catch {
      setNews([]);
    } finally {
      setLoading(false);
    }
  };

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    if (!form.title || !form.summary || !form.date) {
      setMessage('Vui lòng nhập đầy đủ thông tin.');
      return;
    }
    try {
      if (editingId) {
        await axios.put(`/api/news/${editingId}`, form);
        setMessage('Cập nhật tin tức thành công!');
      } else {
        await axios.post('/api/news', form);
        setMessage('Thêm tin tức thành công!');
      }
      setForm(initialForm);
      setEditingId(null);
      setShowForm(false);
      fetchNews();
    } catch {
      setMessage('Có lỗi xảy ra, vui lòng thử lại.');
    }
  };

  const handleEdit = item => {
    setForm({
      title: item.title,
      summary: item.summary,
      image: item.image || '',
      date: item.date
    });
    setEditingId(item.id);
    setShowForm(true);
    setMessage('');
  };

  const handleDelete = async id => {
    if (!window.confirm('Xóa tin tức này?')) return;
    try {
      await axios.delete(`/api/news/${id}`);
      setMessage('Đã xóa tin tức.');
      fetchNews();
    } catch {
      setMessage('Có lỗi khi xóa.');
    }
  };

  const handleCancel = () => {
    setForm(initialForm);
    setEditingId(null);
    setShowForm(false);
    setMessage('');
  };

  const handleAddClick = () => {
    setForm(initialForm);
    setEditingId(null);
    setShowForm(true);
    setMessage('');
  };

  return (
    <AdminLayout>
      <div className="admin-card">
        <div className="card-header d-flex justify-content-between align-items-center">
          <div className="d-flex align-items-center">
            <i className="bi bi-newspaper me-2 fs-4 text-success"></i>
            <h4 className="mb-0">Quản lý tin tức</h4>
          </div>
          <button className="btn btn-primary" onClick={handleAddClick}>
            <i className="bi bi-plus-lg me-1"></i> Thêm tin tức
          </button>
        </div>
        <div className="card-body p-0">
          {showForm && (
            <div className="border-bottom px-3 py-3 bg-light">
              <form className="row g-3 align-items-end" onSubmit={handleSubmit}>
                <div className="col-md-3">
                  <label className="form-label mb-1">Tiêu đề</label>
                  <input className="form-control" name="title" value={form.title} onChange={handleChange} required />
                </div>
                <div className="col-md-2">
                  <label className="form-label mb-1">Ngày đăng</label>
                  <input className="form-control" name="date" type="date" value={form.date} onChange={handleChange} required />
                </div>
                <div className="col-md-3">
                  <label className="form-label mb-1">Tóm tắt</label>
                  <input className="form-control" name="summary" value={form.summary} onChange={handleChange} required />
                </div>
                <div className="col-md-2">
                  <label className="form-label mb-1">Ảnh (URL)</label>
                  <input className="form-control" name="image" value={form.image} onChange={handleChange} />
                </div>
                <div className="col-md-2 d-flex gap-2">
                  <button className="btn btn-success" type="submit">
                    {editingId ? 'Cập nhật' : 'Thêm mới'}
                  </button>
                  <button className="btn btn-secondary" type="button" onClick={handleCancel}>
                    Hủy
                  </button>
                </div>
                {message && (
                  <div className="col-12">
                    <div className="alert alert-info py-2 mb-0">{message}</div>
                  </div>
                )}
              </form>
            </div>
          )}
          <div className="table-responsive">
            <table className="table table-bordered align-middle mb-0">
              <thead className="table-light">
                <tr>
                  <th style={{width: 40}}>ID</th>
                  <th>Tiêu đề</th>
                  <th>Tóm tắt</th>
                  <th>Ảnh</th>
                  <th>Ngày đăng</th>
                  <th style={{width: 120}}>Thao tác</th>
                </tr>
              </thead>
              <tbody>
                {loading ? (
                  <tr>
                    <td colSpan={6} className="text-center text-muted py-4">Đang tải...</td>
                  </tr>
                ) : news.length === 0 ? (
                  <tr>
                    <td colSpan={6} className="text-center text-muted py-4">Chưa có tin tức nào.</td>
                  </tr>
                ) : (
                  news.map((item) => (
                    <tr key={item.id}>
                      <td>{item.id}</td>
                      <td className="fw-bold">{item.title}</td>
                      <td>{item.summary}</td>
                      <td>
                        {item.image && <img src={item.image} alt="" style={{maxWidth: 60, maxHeight: 40, objectFit: 'cover', borderRadius: 4}} />}
                      </td>
                      <td>{formatDate(item.date)}</td>
                      <td>
                        <button className="btn btn-sm btn-outline-primary me-2" onClick={() => handleEdit(item)}>Sửa</button>
                        <button className="btn btn-sm btn-outline-danger" onClick={() => handleDelete(item.id)}>Xóa</button>
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </AdminLayout>
  );
};

export default AdminNews;
