import React, { useEffect, useState } from "react";
import AdminLayout from "../../components/AdminLayout";
import { Table, Button, Modal, Form, Badge } from "react-bootstrap";
import axios from "axios";
import { showSuccess, showError } from "../../utils/notify";
import { getAuthHeaders } from "../../utils/auth";

const AdminSizes = () => {
  const [sizes, setSizes] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [editingSize, setEditingSize] = useState(null);
  const [form, setForm] = useState({
    name: "",
    description: "",
    category_type: "shoes",
    sort_order: 0,
    status: 1,
  });

  // L·∫•y danh s√°ch size
  const fetchSizes = async () => {
    try {
      const res = await axios.get("/api/sizes");
      setSizes(res.data);
    } catch (err) {
      showError("L·ªói t·∫£i danh s√°ch size");
    }
  };

  useEffect(() => {
    fetchSizes();
  }, []);

  // M·ªü modal th√™m/s·ª≠a
  const handleShowModal = (size = null) => {
    setEditingSize(size);
    setForm(
      size || {
        name: "",
        description: "",
        category_type: "shoes",
        sort_order: 0,
        status: 1,
      }
    );
    setShowModal(true);
  };

  const handleCloseModal = () => setShowModal(false);

  // Submit form
  const handleSubmit = async () => {
    try {
      if (editingSize) {
        await axios.put(`/api/sizes/${editingSize.id}`, form, {
          headers: getAuthHeaders(),
        });
        showSuccess("C·∫≠p nh·∫≠t size th√†nh c√¥ng!");
      } else {
        await axios.post("/api/sizes", form, { headers: getAuthHeaders() });
        showSuccess("Th√™m size th√†nh c√¥ng!");
      }
      fetchSizes();
      handleCloseModal();
    } catch (err) {
      showError("L·ªói khi l∆∞u size");
    }
  };

  // X√≥a size
  const handleDelete = async (id) => {
    if (!window.confirm("B·∫°n c√≥ ch·∫Øc mu·ªën x√≥a size n√†y?")) return;
    try {
      await axios.delete(`/api/sizes/${id}`, { headers: getAuthHeaders() });
      showSuccess("X√≥a size th√†nh c√¥ng!");
      fetchSizes();
    } catch (err) {
      showError("L·ªói khi x√≥a size");
    }
  };

  return (
    <AdminLayout>
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>Qu·∫£n l√Ω Size</h2>
        <Button variant="success" onClick={() => handleShowModal()}>
          ‚ûï Th√™m Size
        </Button>
      </div>

      <Table striped bordered hover>
        <thead>
          <tr>
            <th>#</th>
            <th>T√™n</th>
            <th>M√¥ t·∫£</th>
            <th>Lo·∫°i</th>
            <th>Th·ª© t·ª±</th>
            <th>Tr·∫°ng th√°i</th>
            <th>H√†nh ƒë·ªông</th>
          </tr>
        </thead>
        <tbody>
          {sizes.map((size, idx) => (
            <tr key={size.id}>
              <td>{idx + 1}</td>
              <td>{size.name}</td>
              <td>{size.description}</td>
              <td>{size.category_type}</td>
              <td>{size.sort_order}</td>
              <td>
                <Badge bg={size.status === 1 ? "success" : "secondary"}>
                  {size.status === 1 ? "K√≠ch ho·∫°t" : "Kh√¥ng k√≠ch ho·∫°t"}
                </Badge>
              </td>
              <td>
                <Button
                  variant="primary"
                  size="sm"
                  onClick={() => handleShowModal(size)}
                >
                  ‚úèÔ∏è S·ª≠a
                </Button>{" "}
                <Button
                  variant="danger"
                  size="sm"
                  onClick={() => handleDelete(size.id)}
                >
                  üóëÔ∏è X√≥a
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>

      {/* Modal th√™m/s·ª≠a */}
      <Modal show={showModal} onHide={handleCloseModal}>
        <Modal.Header closeButton>
          <Modal.Title>{editingSize ? "S·ª≠a Size" : "Th√™m Size"}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group className="mb-2">
              <Form.Label>T√™n</Form.Label>
              <Form.Control
                type="text"
                value={form.name}
                onChange={(e) => setForm({ ...form, name: e.target.value })}
              />
            </Form.Group>
            <Form.Group className="mb-2">
              <Form.Label>M√¥ t·∫£</Form.Label>
              <Form.Control
                as="textarea"
                value={form.description}
                onChange={(e) =>
                  setForm({ ...form, description: e.target.value })
                }
              />
            </Form.Group>
            <Form.Group className="mb-2">
              <Form.Label>Lo·∫°i s·∫£n ph·∫©m</Form.Label>
              <Form.Select
                value={form.category_type}
                onChange={(e) =>
                  setForm({ ...form, category_type: e.target.value })
                }
              >
                <option value="shoes">Gi√†y</option>
                <option value="clothing">Qu·∫ßn √°o</option>
              </Form.Select>
            </Form.Group>
            <Form.Group className="mb-2">
              <Form.Label>Th·ª© t·ª± s·∫Øp x·∫øp</Form.Label>
              <Form.Control
                type="number"
                value={form.sort_order}
                onChange={(e) =>
                  setForm({ ...form, sort_order: parseInt(e.target.value) })
                }
              />
            </Form.Group>
            <Form.Group>
              <Form.Label>Tr·∫°ng th√°i</Form.Label>
              <Form.Select
                value={form.status}
                onChange={(e) =>
                  setForm({ ...form, status: parseInt(e.target.value) })
                }
              >
                <option value={1}>K√≠ch ho·∫°t</option>
                <option value={0}>Kh√¥ng k√≠ch ho·∫°t</option>
              </Form.Select>
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseModal}>
            ƒê√≥ng
          </Button>
          <Button variant="primary" onClick={handleSubmit}>
            L∆∞u
          </Button>
        </Modal.Footer>
      </Modal>
    </AdminLayout>
  );
};

export default AdminSizes;
