import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import axios from 'axios';

const Profile = () => {
  const { currentUser, logout, refreshUser } = useAuth();
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    address: '',
    avatar: '',
    date_of_birth: '',
    gender: '',
    facebook: '',
    twitter: '',
    status: '',
    password: '',
    confirmPassword: ''
  });
  const [avatarFile, setAvatarFile] = useState(null);
  const [loading, setLoading] = useState(false);

  // Lấy thông tin user đầy đủ từ backend khi vào trang
  useEffect(() => {
    const fetchUser = async () => {
      try {
        const res = await axios.get('/api/users/me');
        setFormData({
          name: res.data.name || '',
          email: res.data.email || '',
          phone: res.data.phone || '',
          address: res.data.address || '',
          avatar: res.data.avatar || '',
          date_of_birth: res.data.date_of_birth || '',
          gender: res.data.gender || '',
          facebook: res.data.facebook || '',
          twitter: res.data.twitter || '',
          status: res.data.status || '',
          password: '',
          confirmPassword: ''
        });
      } catch {
        // fallback nếu lỗi
        setFormData({
          name: currentUser?.name || '',
          email: currentUser?.email || '',
          phone: currentUser?.phone || '',
          address: currentUser?.address || '',
          avatar: currentUser?.avatar || '',
          date_of_birth: currentUser?.date_of_birth || '',
          gender: currentUser?.gender || '',
          facebook: currentUser?.facebook || '',
          twitter: currentUser?.twitter || '',
          status: currentUser?.status || '',
          password: '',
          confirmPassword: ''
        });
      }
    };
    fetchUser();
    if (typeof refreshUser === 'function') refreshUser();
  }, []);

  const handleChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleAvatarChange = e => {
    if (e.target.files && e.target.files[0]) {
      setAvatarFile(e.target.files[0]);
      setFormData({ ...formData, avatar: URL.createObjectURL(e.target.files[0]) });
    }
  };

  const handleAvatarUpload = async () => {
    if (!avatarFile) return;
    const data = new FormData();
    data.append('avatar', avatarFile);
    try {
      const res = await axios.post(`/api/users/${currentUser.id}/avatar`, data);
      setFormData({ ...formData, avatar: res.data.avatarUrl });
      alert('Cập nhật ảnh đại diện thành công!');
      if (typeof refreshUser === 'function') refreshUser();
    } catch {
      alert('Lỗi khi cập nhật ảnh đại diện!');
    }
  };

  const handleSave = async e => {
    e.preventDefault();
    if (formData.password && formData.password !== formData.confirmPassword) {
      alert('Mật khẩu xác nhận không khớp!');
      return;
    }
    setLoading(true);
    try {
      await axios.put(`/api/users/${currentUser.id}`, {
        ...formData,
        password: formData.password ? formData.password : undefined
      });
      alert('Cập nhật thông tin thành công!');
      if (typeof refreshUser === 'function') refreshUser();
      window.location.reload();
    } catch {
      alert('Cập nhật thất bại!');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container my-5">
      <div className="row justify-content-center g-4">
        <div className="col-lg-4">
          <div className="card p-4 text-center">
            <h4 className="fw-bold mb-1">{formData.name}</h4>
            <div className="mb-3">
              <div className="position-relative d-inline-block">
                <img
                  src={formData.avatar || 'https://via.placeholder.com/120x120?text=Avatar'}
                  alt="Avatar"
                  className="rounded-circle"
                  style={{ width: 120, height: 120, objectFit: 'cover', border: '2px solid #eee' }}
                />
                <label
                  htmlFor="avatar-upload"
                  className="position-absolute bottom-0 end-0 btn btn-sm btn-light border"
                  style={{ cursor: 'pointer' }}
                  title="Đổi ảnh"
                >
                  <i className="bi bi-pencil"></i>
                </label>
                <input
                  id="avatar-upload"
                  type="file"
                  accept="image/*"
                  style={{ display: 'none' }}
                  onChange={handleAvatarChange}
                />
              </div>
            </div>
            <button
              className="btn btn-danger w-100 mb-2"
              type="button"
              onClick={handleAvatarUpload}
              disabled={!avatarFile}
            >
              Upload New Photo
            </button>
            <div className="mb-3 text-muted small">
              Member Since: {currentUser?.created_at ? new Date(currentUser.created_at).toLocaleDateString('vi-VN') : '-'}
            </div>
            <div className="mb-2">
              <span className={`badge ${currentUser?.role === 'admin' ? 'bg-warning' : currentUser?.role === 'staff' ? 'bg-info' : 'bg-primary'}`}>
                {currentUser?.role === 'admin' ? 'Quản trị viên' : currentUser?.role === 'staff' ? 'Nhân viên' : 'Khách hàng'}
              </span>
            </div>
            <div className="mb-2">
              <span className={`badge ${formData.status === 'active' ? 'bg-success' : formData.status === 'inactive' ? 'bg-secondary' : 'bg-danger'}`}>
                {formData.status === 'active' ? 'Hoạt động' : formData.status === 'inactive' ? 'Chưa kích hoạt' : 'Bị khóa'}
              </span>
            </div>
          </div>
        </div>
        <div className="col-lg-8">
          <div className="card p-4">
            <h3 className="fw-bold mb-4">Chỉnh sửa thông tin cá nhân</h3>
            <form onSubmit={handleSave}>
              <div className="mb-3">
                <label className="form-label">Họ tên</label>
                <input
                  type="text"
                  name="name"
                  className="form-control"
                  value={formData.name}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="mb-3">
                <label className="form-label">Email</label>
                <input
                  type="email"
                  name="email"
                  className="form-control"
                  value={formData.email}
                  disabled
                />
              </div>
              <div className="mb-3">
                <label className="form-label">Số điện thoại</label>
                <input
                  type="text"
                  name="phone"
                  className="form-control"
                  value={formData.phone}
                  onChange={handleChange}
                />
              </div>
              <div className="mb-3">
                <label className="form-label">Địa chỉ</label>
                <input
                  type="text"
                  name="address"
                  className="form-control"
                  value={formData.address}
                  onChange={handleChange}
                />
              </div>
              <div className="mb-3">
                <label className="form-label">Ngày sinh</label>
                <input
                  type="date"
                  name="date_of_birth"
                  className="form-control"
                  value={formData.date_of_birth}
                  onChange={handleChange}
                />
              </div>
              <div className="mb-3">
                <label className="form-label">Giới tính</label>
                <select
                  name="gender"
                  className="form-select"
                  value={formData.gender}
                  onChange={handleChange}
                >
                  <option value="">Chưa cập nhật</option>
                  <option value="male">Nam</option>
                  <option value="female">Nữ</option>
                  <option value="other">Khác</option>
                </select>
              </div>
              <div className="mb-3">
                <label className="form-label">Facebook</label>
                <input
                  type="text"
                  name="facebook"
                  className="form-control"
                  value={formData.facebook}
                  onChange={handleChange}
                />
              </div>
              <div className="mb-3">
                <label className="form-label">Twitter</label>
                <input
                  type="text"
                  name="twitter"
                  className="form-control"
                  value={formData.twitter}
                  onChange={handleChange}
                />
              </div>
              <div className="mb-3">
                <label className="form-label">Mật khẩu mới</label>
                <input
                  type="password"
                  name="password"
                  className="form-control"
                  value={formData.password}
                  onChange={handleChange}
                  autoComplete="new-password"
                />
              </div>
              <div className="mb-3">
                <label className="form-label">Xác nhận mật khẩu</label>
                <input
                  type="password"
                  name="confirmPassword"
                  className="form-control"
                  value={formData.confirmPassword}
                  onChange={handleChange}
                  autoComplete="new-password"
                />
              </div>
              <button type="submit" className="btn btn-success px-4" disabled={loading}>
                {loading ? 'Đang cập nhật...' : 'Lưu thông tin'}
              </button>
            </form>
            <div className="mt-4 text-muted small">
              Email xác thực: {currentUser?.email_verified_at
                ? <span className="text-success">Đã xác thực</span>
                : <span className="text-danger">Chưa xác thực</span>
              }
              <br />
              Ngày tạo: {currentUser?.created_at ? new Date(currentUser.created_at).toLocaleString('vi-VN') : '-'}
              <br />
              Cập nhật lần cuối: {currentUser?.updated_at ? new Date(currentUser.updated_at).toLocaleString('vi-VN') : '-'}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
