import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useCart } from '../contexts/CartContext';
import axios from 'axios';

const ProductDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { addToCart } = useCart();
  const [product, setProduct] = useState(null);
  const [quantity, setQuantity] = useState(1);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchProduct();
  }, [id]);

  const fetchProduct = async () => {
    try {
      const response = await axios.get(`/api/products/${id}`);
      setProduct(response.data);
    } catch (error) {
      console.error('Error fetching product:', error);
      if (error.response?.status === 404) {
        navigate('/products');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleAddToCart = () => {
    if (product && quantity > 0) {
      addToCart(product, quantity);
      alert('Đã thêm sản phẩm vào giỏ hàng!');
    }
  };

  if (loading) {
    return (
      <div className="container my-5">
        <div className="text-center">
          <div className="spinner-border" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
        </div>
      </div>
    );
  }

  if (!product) {
    return (
      <div className="container my-5">
        <div className="alert alert-danger">
          Không tìm thấy sản phẩm.
        </div>
      </div>
    );
  }

  return (
    <div className="container my-5">
      <button className="btn btn-outline-secondary mb-3" onClick={() => navigate(-1)}>
        ← Quay lại
      </button>
      
      <div className="row">
        <div className="col-md-6">
          <img 
            src={product.image_url || 'https://via.placeholder.com/500x400'} 
            alt={product.name}
            className="img-fluid rounded"
          />
        </div>
        <div className="col-md-6">
          <h1>{product.name}</h1>
          <p className="text-muted fs-5">{product.category_name}</p>
          <h2 className="text-primary fw-bold">
            {new Intl.NumberFormat('vi-VN', { 
              style: 'currency', 
              currency: 'VND' 
            }).format(product.price)}
          </h2>
          
          <div className="my-4">
            <h5>Mô tả sản phẩm:</h5>
            <p>{product.description || 'Chưa có mô tả chi tiết.'}</p>
          </div>
          
          <div className="my-4">
            <p><strong>Tồn kho:</strong> {product.stock_quantity} sản phẩm</p>
          </div>
          
          {product.stock_quantity > 0 ? (
            <div className="row align-items-center">
              <div className="col-auto">
                <label htmlFor="quantity" className="form-label">Số lượng:</label>
              </div>
              <div className="col-auto">
                <input
                  type="number"
                  id="quantity"
                  className="form-control"
                  value={quantity}
                  onChange={(e) => setQuantity(Math.max(1, parseInt(e.target.value) || 1))}
                  min="1"
                  max={product.stock_quantity}
                  style={{ width: '80px' }}
                />
              </div>
              <div className="col">
                <button 
                  className="btn btn-primary btn-lg"
                  onClick={handleAddToCart}
                >
                  🛒 Thêm vào giỏ hàng
                </button>
              </div>
            </div>
          ) : (
            <div className="alert alert-danger">
              Sản phẩm hiện đang hết hàng.
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default ProductDetail;
