import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';

const CATEGORY_IDS = {
  'giay-cau-long': 2,
  'vot-cau-long': 1,
  'ao-quan': 3,
  'phu-kien': 4
};

const Home = () => {
  const [categories, setCategories] = useState([]);
  const [productsByCategory, setProductsByCategory] = useState({});
  const [bestSellers, setBestSellers] = useState([]);
  const [favoriteProducts, setFavoriteProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [carouselIndex, setCarouselIndex] = useState({});

  useEffect(() => {
    fetchCategories();
    fetchProducts();
  }, []);

  const fetchCategories = async () => {
    try {
      const res = await axios.get('/api/categories');
      setCategories(res.data);
    } catch (error) {
      setCategories([]);
    }
  };

  const fetchProducts = async () => {
    try {
      const res = await axios.get('/api/products');
      // Phân loại sản phẩm theo danh mục
      const byCat = {};
      Object.values(CATEGORY_IDS).forEach(cid => {
        byCat[cid] = res.data.filter(p => p.category_id === cid);
      });
      setProductsByCategory(byCat);
      // Sản phẩm bán chạy: lấy 8 sản phẩm đầu (demo)
      setBestSellers(res.data.slice(0, 8));
      // Sản phẩm yêu thích: lấy 8 sản phẩm tiếp theo (demo)
      setFavoriteProducts(res.data.slice(8, 16));
    } catch (error) {
      setProductsByCategory({});
      setBestSellers([]);
      setFavoriteProducts([]);
    } finally {
      setLoading(false);
    }
  };

  // Xử lý chuyển slide cho từng danh mục
  const handlePrev = (catId) => {
    setCarouselIndex(idx => ({
      ...idx,
      [catId]: Math.max(0, (idx[catId] || 0) - 1)
    }));
  };
  const handleNext = (catId, max) => {
    setCarouselIndex(idx => ({
      ...idx,
      [catId]: Math.min(max, (idx[catId] || 0) + 1)
    }));
  };

  // Hiển thị sản phẩm dạng card theo mẫu hình
  const renderProductCard = (product) => (
    <div className="home-product-card shadow-sm rounded-3 bg-white d-flex flex-column align-items-center justify-content-between mx-2 mb-3" key={product.id} style={{width:220, minWidth:220, maxWidth:220, padding:'18px 12px'}}>
      <div className="position-relative w-100">
        <img
          src={product.image_url || 'https://via.placeholder.com/300x200?text=No+Image'}
          alt={product.name}
          className="w-100 rounded-3"
          style={{height: '160px', objectFit: 'cover', background: '#fff'}}
        />
        {product.stock_quantity === 0 && (
          <span className="badge badge-het-hang position-absolute top-0 start-0 m-2">Hết hàng</span>
        )}
      </div>
      <div className="mt-3 w-100 text-center">
        <div className="fw-bold mb-1" style={{minHeight: '48px', fontSize:'1.05rem'}}>{product.name}</div>
        <div className="fw-bold text-danger mb-2" style={{fontSize: '1.25rem'}}>
          {new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(product.sale_price || product.price)}
        </div>
        <div className="mb-2">
          {product.stock_quantity === 0 ? (
            <Link to={`/products/${product.id}`} className="btn btn-light w-100 home-btn rounded-pill border">
              <i className="bi bi-cart3 me-2"></i> Xem chi tiết
            </Link>
          ) : (
            <Link to={`/products/${product.id}`} className="btn btn-dark w-100 home-btn rounded-pill">
              <i className="bi bi-cart3 me-2"></i> Thêm vào giỏ
            </Link>
          )}
        </div>
      </div>
    </div>
  );

  // Hiển thị carousel sản phẩm theo danh mục
  const renderCategoryBlock = (catId, title, link) => {
    const products = productsByCategory[catId] || [];
    const idx = carouselIndex[catId] || 0;
    const visible = products.slice(idx, idx + 5);
    return (
      <div className="home-category-block mb-5">
        <div className="d-flex align-items-center mb-3">
          <div>
            <h2 className="home-category-title text-danger" style={{fontWeight:700, fontSize:'2rem'}}>{title}</h2>
            <div className="home-category-underline bg-danger"></div>
          </div>
          <div className="ms-4 d-flex gap-2">
            <button className="home-carousel-btn rounded-circle border-0 bg-light" onClick={() => handlePrev(catId)} disabled={idx === 0}>
              <i className="bi bi-chevron-left"></i>
            </button>
            <button className="home-carousel-btn rounded-circle border-0 bg-light" onClick={() => handleNext(catId, Math.max(0, products.length - 5))} disabled={idx + 5 >= products.length}>
              <i className="bi bi-chevron-right"></i>
            </button>
          </div>
        </div>
        <div className="d-flex flex-wrap justify-content-center gap-3">
          {visible.map(renderProductCard)}
        </div>
        <div className="mt-4 text-center">
          <Link to={link} className="btn btn-dark home-btn-all rounded-pill px-4">
            Xem tất cả <i className="bi bi-arrow-right"></i>
          </Link>
        </div>
      </div>
    );
  };

  // Hiển thị khối sản phẩm bán chạy/top yêu thích
  const renderBestSellerBlock = () => (
    <div className="home-category-block mb-5" style={{background:'#fafafa',padding:'32px 0', borderRadius:'18px'}}>
      <h2 className="home-category-title text-danger" style={{fontWeight:700, fontSize:'2rem'}}>TOP SẢN PHẨM BÁN CHẠY</h2>
      <div className="home-category-underline bg-danger"></div>
      <div className="d-flex flex-wrap justify-content-center gap-3 mt-4">
        {bestSellers.map(renderProductCard)}
      </div>
    </div>
  );

  // Hiển thị khối sản phẩm yêu thích (carousel ngang)
  const renderFavoriteBlock = () => (
    <div className="home-category-block mb-5">
      <h2 className="home-category-title text-danger" style={{fontWeight:700, fontSize:'2rem'}}>SẢN PHẨM BẠN YÊU THÍCH</h2>
      <div className="home-category-underline bg-danger"></div>
      <div className="d-flex flex-wrap justify-content-center gap-3 mt-4">
        {favoriteProducts.map((product, idx) => (
          <div key={product.id} className="home-favorite-card shadow-sm rounded-3 bg-white d-flex flex-column align-items-center justify-content-center mx-2 mb-3" style={{width:'160px',minWidth:'160px',maxWidth:'160px',padding:'18px 8px'}}>
            <img src={product.image_url || 'https://via.placeholder.com/120x120?text=Favorite'} alt={product.name} style={{width:'120px',height:'120px',objectFit:'cover',background:'#f8f9fa',borderRadius:'12px'}} />
            <div className="fw-bold mt-2" style={{minHeight:'40px'}}>{product.name}</div>
            <div className="text-muted">Xem chi tiết</div>
          </div>
        ))}
      </div>
    </div>
  );

  // Hiển thị khối thương hiệu nổi bật
  const renderBrandsBlock = () => (
    <div className="container my-5">
      <h2 className="fw-bold text-danger mb-4 text-center" style={{fontSize:'2rem'}}>THƯƠNG HIỆU NỔI BẬT</h2>
      <div className="d-flex flex-wrap gap-4 justify-content-center align-items-center">
        <img src="/images/brands/yonex.png" alt="Yonex" style={{height:'60px',borderRadius:'50%',boxShadow:'0 2px 8px rgba(0,0,0,0.07)'}} />
        <img src="/images/brands/lining.png" alt="Li-Ning" style={{height:'60px',borderRadius:'50%',boxShadow:'0 2px 8px rgba(0,0,0,0.07)'}} />
        <img src="/images/brands/mizuno.png" alt="Mizuno" style={{height:'60px',borderRadius:'50%',boxShadow:'0 2px 8px rgba(0,0,0,0.07)'}} />
        <img src="/images/brands/victor.png" alt="Victor" style={{height:'60px',borderRadius:'50%',boxShadow:'0 2px 8px rgba(0,0,0,0.07)'}} />
        <img src="/images/brands/fzforza.png" alt="FZ Forza" style={{height:'60px',borderRadius:'50%',boxShadow:'0 2px 8px rgba(0,0,0,0.07)'}} />
      </div>
    </div>
  );

  // Danh mục sản phẩm đẹp như hình
  const renderCategoryCards = () => (
    <div className="container my-5">
      <div className="text-center mb-5">
        <h1 className="fw-bold text-primary" style={{fontSize:'2.5rem'}}>Danh mục sản phẩm</h1>
        <p className="lead text-muted">Khám phá các nhóm sản phẩm nổi bật tại Badminton Shop</p>
      </div>
      <div className="row justify-content-center g-4">
        {categories.map(category => (
          <div key={category.id} className="col-lg-3 col-md-4 col-sm-6 d-flex">
            <div className="category-card shadow-sm rounded-4 bg-white w-100 d-flex flex-column align-items-center justify-content-between p-4">
              <div className="mb-3">
                <img
                  src={category.image || 'https://via.placeholder.com/60x60?text=' + encodeURIComponent(category.name)}
                  alt={category.name}
                  style={{width:'60px',height:'60px',objectFit:'cover',borderRadius:'12px',background:'#f8f9fa'}}
                />
              </div>
              <h4 className="fw-bold text-dark mb-2">{category.name}</h4>
              <div className="text-muted mb-3 text-center" style={{minHeight:'40px'}}>{category.description}</div>
              <Link
                to={`/products?category=${category.id}`}
                className="btn category-btn-gradient w-100"
                style={{fontWeight:600, fontSize:'1rem', borderRadius:'12px'}}
              >
                Xem sản phẩm
              </Link>
            </div>
          </div>
        ))}
      </div>
    </div>
  );

  return (
    <div>
      {/* Banner */}
      <div className="home-banner mb-5">
        <img src="/images/slides/slide1.jpg" alt="Banner" className="w-100" style={{maxHeight:'320px',objectFit:'cover'}} />
        <div className="home-banner-text">
          <h1 className="fw-bold" style={{fontSize:'2.5rem'}}>Play your style!</h1>
          <div className="fs-4">MY THO</div>
          <div className="mt-2">Since 2018 - www.tusport.vn</div>
        </div>
      </div>

      {/* Danh mục sản phẩm */}
      {renderCategoryCards()}

      {/* Sản phẩm theo danh mục */}
      <div className="container">
        {renderCategoryBlock(CATEGORY_IDS['giay-cau-long'], 'GIÀY CẦU LÔNG', '/products?category=2')}
        {renderCategoryBlock(CATEGORY_IDS['vot-cau-long'], 'VỢT CẦU LÔNG', '/products?category=1')}
      </div>

      {/* Top bán chạy */}
      <div className="container">
        {renderBestSellerBlock()}
      </div>

      {/* Sản phẩm yêu thích */}
      <div className="container">
        {renderFavoriteBlock()}
      </div>

      {/* Thương hiệu nổi bật */}
      {renderBrandsBlock()}

      {/* Features Section */}
      <div className="features-section">
        <div className="container">
          <div className="text-center mb-5">
            <h2 className="display-5 fw-bold text-primary mb-3">Tại sao chọn chúng tôi?</h2>
            <p className="lead text-muted">Cam kết mang đến trải nghiệm mua sắm tốt nhất</p>
          </div>
          <div className="row g-4">
            <div className="col-lg-4 col-md-6">
              <div className="feature-item">
                <div className="feature-icon">
                  <i className="bi bi-truck"></i>
                </div>
                <h4>Giao hàng nhanh chóng</h4>
                <p className="text-muted">
                  Giao hàng toàn quốc trong 24-48h. Miễn phí vận chuyển cho đơn hàng từ 500.000đ
                </p>
              </div>
            </div>
            <div className="col-lg-4 col-md-6">
              <div className="feature-item">
                <div className="feature-icon">
                  <i className="bi bi-shield-check"></i>
                </div>
                <h4>Chất lượng đảm bảo</h4>
                <p className="text-muted">
                  100% sản phẩm chính hãng từ các thương hiệu uy tín hàng đầu thế giới
                </p>
              </div>
            </div>
            <div className="col-lg-4 col-md-6 mx-auto">
              <div className="feature-item">
                <div className="feature-icon">
                  <i className="bi bi-headset"></i>
                </div>
                <h4>Hỗ trợ 24/7</h4>
                <p className="text-muted">
                  Đội ngũ tư vấn chuyên nghiệp sẵn sàng hỗ trợ bạn mọi lúc, mọi nơi
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Newsletter Section */}
      <div className="container my-5">
        <div className="row justify-content-center">
          <div className="col-lg-8">
            <div className="card border-0 shadow-lg" style={{background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)'}}>
              <div className="card-body text-center text-white p-5">
                <h3 className="fw-bold mb-3">
                  <i className="bi bi-envelope-heart me-2"></i>
                  Đăng ký nhận tin
                </h3>
                <p className="mb-4">Nhận thông tin về sản phẩm mới và ưu đãi đặc biệt</p>
