import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useCart } from '../contexts/CartContext';
import axios from 'axios';

const ProductDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { addToCart } = useCart();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [quantity, setQuantity] = useState(1);

  useEffect(() => {
    fetchProduct();
  }, [id]);

  const fetchProduct = async () => {
    try {
      const response = await axios.get(`/api/products/${id}`);
      setProduct(response.data);
    } catch (error) {
      console.error('Error fetching product:', error);
      if (error.response?.status === 404) {
        navigate('/products');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleAddToCart = () => {
    if (product) {
      addToCart({
        ...product,
        quantity: quantity
      });
      alert('Đã thêm sản phẩm vào giỏ hàng!');
    }
  };

  if (loading) {
    return (
      <div className="container my-5">
        <div className="text-center py-5">
          <div className="spinner-border spinner-border-lg" role="status">
            <span className="visually-hidden">Đang tải...</span>
          </div>
        </div>
      </div>
    );
  }

  if (!product) {
    return (
      <div className="container my-5">
        <div className="text-center py-5">
          <h3>Không tìm thấy sản phẩm</h3>
          <button 
            className="btn btn-primary mt-3"
            onClick={() => navigate('/products')}
          >
            Quay lại danh sách sản phẩm
          </button>
        </div>
      </div>
    );
  }

  const currentPrice = product.sale_price || product.price;
  const originalPrice = product.price;
  const discountPercentage = product.sale_price 
    ? Math.round(((originalPrice - product.sale_price) / originalPrice) * 100)
    : 0;

  return (
    <div className="container my-5">
      {/* Breadcrumb */}
      <nav aria-label="breadcrumb" className="mb-4">
        <ol className="breadcrumb">
          <li className="breadcrumb-item">
            <button 
              className="btn btn-link p-0"
              onClick={() => navigate('/')}
            >
              Trang chủ
            </button>
          </li>
          <li className="breadcrumb-item">
            <button 
              className="btn btn-link p-0"
              onClick={() => navigate('/products')}
            >
              Sản phẩm
            </button>
          </li>
          <li className="breadcrumb-item active">{product.name}</li>
        </ol>
      </nav>

      <div className="row g-5">
        {/* Product Image */}
        <div className="col-lg-6">
          <div className="position-relative">
            <img 
              src={product.image_url || 'https://via.placeholder.com/600x400/f8f9fa/6c757d?text=No+Image'} 
              alt={product.name}
              className="img-fluid rounded shadow-lg w-100"
              style={{ maxHeight: '500px', objectFit: 'cover' }}
            />
            {discountPercentage > 0 && (
              <div className="position-absolute top-0 end-0 m-3">
                <span className="badge bg-danger fs-6 p-2">
                  SALE {discountPercentage}%
                </span>
              </div>
            )}
          </div>
        </div>

        {/* Product Info */}
        <div className="col-lg-6">
          <div className="mb-3">
            <span className="badge bg-primary me-2">{product.category_name}</span>
            {product.brand_name && (
              <span className="badge bg-secondary">{product.brand_name}</span>
            )}
          </div>

          <h1 className="display-6 fw-bold text-dark mb-3">{product.name}</h1>
          
          {product.short_description && (
            <p className="lead text-muted mb-4">{product.short_description}</p>
          )}

          {/* Price */}
          <div className="mb-4">
            <div className="d-flex align-items-baseline gap-3">
              <span className="display-5 fw-bold text-primary">
                {new Intl.NumberFormat('vi-VN', { 
                  style: 'currency', 
                  currency: 'VND' 
                }).format(currentPrice)}
              </span>
              {product.sale_price && (
                <span className="fs-4 text-decoration-line-through text-muted">
                  {new Intl.NumberFormat('vi-VN', { 
                    style: 'currency', 
                    currency: 'VND' 
                  }).format(originalPrice)}
                </span>
              )}
            </div>
            {product.sale_price && (
              <p className="text-success fw-bold mt-2">
                <i className="bi bi-tag me-1"></i>
                Tiết kiệm {new Intl.NumberFormat('vi-VN', { 
                  style: 'currency', 
                  currency: 'VND' 
                }).format(originalPrice - product.sale_price)}
              </p>
            )}
          </div>

          {/* Stock */}
          <div className="mb-4">
            <div className="d-flex align-items-center gap-2">
              <i className="bi bi-box text-primary"></i>
              <span className="fw-medium">Tình trạng:</span>
              {product.stock_quantity > 0 ? (
                <span className="text-success fw-bold">
                  Còn hàng ({product.stock_quantity} sản phẩm)
                </span>
              ) : (
                <span className="text-danger fw-bold">Hết hàng</span>
              )}
            </div>
          </div>

          {/* Quantity & Add to Cart */}
          {product.stock_quantity > 0 && (
            <div className="mb-4">
              <div className="row g-3 align-items-end">
                <div className="col-auto">
                  <label className="form-label fw-medium">Số lượng:</label>
                  <div className="input-group" style={{width: '150px'}}>
                    <button 
                      className="btn btn-outline-secondary"
                      onClick={() => setQuantity(Math.max(1, quantity - 1))}
                    >
                      <i className="bi bi-dash"></i>
                    </button>
                    <input 
                      type="number" 
                      className="form-control text-center"
                      value={quantity}
                      onChange={(e) => setQuantity(Math.max(1, Math.min(product.stock_quantity, parseInt(e.target.value) || 1)))}
                      min="1"
                      max={product.stock_quantity}
                    />
                    <button 
                      className="btn btn-outline-secondary"
                      onClick={() => setQuantity(Math.min(product.stock_quantity, quantity + 1))}
                    >
                      <i className="bi bi-plus"></i>
                    </button>
                  </div>
                </div>
                <div className="col">
                  <button 
                    className="btn btn-primary btn-lg px-4"
                    onClick={handleAddToCart}
                  >
                    <i className="bi bi-cart-plus me-2"></i>
                    Thêm vào giỏ hàng
                  </button>
                </div>
              </div>
            </div>
          )}

          {/* Product Details */}
          <div className="card">
            <div className="card-header">
              <h5 className="mb-0">
                <i className="bi bi-info-circle me-2"></i>
                Thông tin sản phẩm
              </h5>
            </div>
            <div className="card-body">
              <div className="row g-3">
                <div className="col-md-6">
                  <small className="text-muted">Mã sản phẩm:</small>
                  <div className="fw-medium">{product.sku}</div>
                </div>
                {product.weight && (
                  <div className="col-md-6">
                    <small className="text-muted">Trọng lượng:</small>
                    <div className="fw-medium">{product.weight}g</div>
                  </div>
                )}
                <div className="col-md-6">
                  <small className="text-muted">Danh mục:</small>
                  <div className="fw-medium">{product.category_name}</div>
                </div>
                {product.brand_name && (
                  <div className="col-md-6">
                    <small className="text-muted">Thương hiệu:</small>
                    <div className="fw-medium">{product.brand_name}</div>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Product Description */}
      {product.description && (
        <div className="row mt-5">
          <div className="col-12">
            <div className="card">
              <div className="card-header">
                <h5 className="mb-0">
                  <i className="bi bi-file-text me-2"></i>
                  Mô tả chi tiết
                </h5>
              </div>
              <div className="card-body">
                <p className="mb-0" style={{whiteSpace: 'pre-line'}}>
                  {product.description}
                </p>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ProductDetail;
