import React, { createContext, useState, useContext, useEffect } from "react";
import { useAuth } from "./AuthContext";

const CartContext = createContext();

export const useCart = () => {
  return useContext(CartContext);
};

// Tạo key duy nhất cho sản phẩm + size
const getCartItemKey = (productId, selectedSize) => {
  return selectedSize ? `${productId}_${selectedSize.id}` : `${productId}`;
};

export const CartProvider = ({ children }) => {
  const [cartItems, setCartItems] = useState([]);
  const [isInitialized, setIsInitialized] = useState(false);
  const { currentUser } = useAuth();

  // Tạo key cho localStorage dựa trên user ID
  const getCartStorageKey = (userId) => {
    return userId ? `cart_user_${userId}` : "cart_guest";
  };

  // Load cart từ localStorage khi component mount hoặc user thay đổi
  useEffect(() => {
    // Chỉ load cart khi isInitialized = false (lần đầu) hoặc user thay đổi
    console.log("User changed, loading cart for:", currentUser?.id || "guest");
    loadCartFromStorage();
  }, [currentUser?.id]);

  // Save cart vào localStorage khi cartItems thay đổi
  useEffect(() => {
    if (isInitialized) {
      saveCartToStorage();
    }
  }, [cartItems, isInitialized, currentUser?.id]);

  const loadCartFromStorage = () => {
    try {
      const storageKey = getCartStorageKey(currentUser?.id);
      const savedCart = localStorage.getItem(storageKey);

      console.log(
        `Loading cart for user ${currentUser?.id || "guest"}:`,
        savedCart
      );

      if (savedCart) {
        const parsedCart = JSON.parse(savedCart);

        // Đảm bảo tương thích với dữ liệu cũ
        const normalizedCart = parsedCart.map((item) => {
          if (!item.cartKey) {
            const cartKey = getCartItemKey(item.id, item.selectedSize);
            return { ...item, cartKey };
          }
          return item;
        });

        setCartItems(normalizedCart);
        console.log(
          `Loaded ${normalizedCart.length} items for user ${
            currentUser?.id || "guest"
          }`
        );
      } else {
        setCartItems([]);
        console.log(
          `No saved cart found for user ${currentUser?.id || "guest"}`
        );
      }
    } catch (error) {
      console.error("Error loading cart from localStorage:", error);
      setCartItems([]);
    } finally {
      setIsInitialized(true);
    }
  };

  const saveCartToStorage = () => {
    try {
      const storageKey = getCartStorageKey(currentUser?.id);
      localStorage.setItem(storageKey, JSON.stringify(cartItems));
      console.log(
        `Saved cart for user ${currentUser?.id || "guest"}:`,
        cartItems.length,
        "items"
      );
    } catch (error) {
      console.error("Error saving cart to localStorage:", error);
    }
  };

  // Migrate cart từ guest sang user khi login - CHỈ chạy khi login, KHÔNG chạy khi logout
  const migrateGuestCartToUser = () => {
    // Chỉ migrate khi user login (có currentUser và trước đó không có user)
    if (!currentUser?.id) {
      console.log("No user to migrate to, skipping migration");
      return;
    }

    const guestStorageKey = getCartStorageKey(null);
    const userStorageKey = getCartStorageKey(currentUser.id);

    try {
      const guestCart = localStorage.getItem(guestStorageKey);
      const userCart = localStorage.getItem(userStorageKey);

      console.log("Migration check:", {
        hasGuestCart: !!guestCart,
        hasUserCart: !!userCart,
        userId: currentUser.id,
      });

      if (guestCart && !userCart) {
        // Nếu có cart guest và chưa có cart user, migrate cart guest
        localStorage.setItem(userStorageKey, guestCart);
        localStorage.removeItem(guestStorageKey);
        console.log(`Migrated guest cart to user ${currentUser.id}`);

        // Reload cart để hiển thị cart đã migrate
        loadCartFromStorage();
      } else if (guestCart && userCart) {
        // Nếu có cả 2, merge carts
        try {
          const guestItems = JSON.parse(guestCart);
          const userItems = JSON.parse(userCart);

          console.log("Merging carts:", {
            guestItems: guestItems.length,
            userItems: userItems.length,
          });

          // Merge: thêm guest items vào user cart nếu chưa có
          const mergedItems = [...userItems];

          guestItems.forEach((guestItem) => {
            const guestCartKey =
              guestItem.cartKey ||
              getCartItemKey(guestItem.id, guestItem.selectedSize);
            const existingItem = mergedItems.find((item) => {
              return (
                (item.cartKey || getCartItemKey(item.id, item.selectedSize)) ===
                guestCartKey
              );
            });

            if (!existingItem) {
              mergedItems.push(guestItem);
            } else {
              // Nếu đã có, cộng thêm quantity
              existingItem.quantity += guestItem.quantity;
            }
          });

          localStorage.setItem(userStorageKey, JSON.stringify(mergedItems));
          localStorage.removeItem(guestStorageKey);
          console.log(`Merged guest cart with user ${currentUser.id} cart`);

          // Reload cart để hiển thị cart đã merge
          loadCartFromStorage();
        } catch (mergeError) {
          console.error("Error merging carts:", mergeError);
        }
      } else {
        console.log("No migration needed");
      }
    } catch (error) {
      console.error("Error migrating guest cart:", error);
    }
  };

  // Trigger migration CHỈ KHI LOGIN (user từ null/undefined thành có giá trị)
  const [previousUserId, setPreviousUserId] = useState(null);

  useEffect(() => {
    if (isInitialized) {
      // Chỉ migrate khi user login (từ null/undefined thành có user)
      if (!previousUserId && currentUser?.id) {
        console.log("User logged in, triggering migration");
        migrateGuestCartToUser();
      }
      setPreviousUserId(currentUser?.id || null);
    }
  }, [currentUser?.id, isInitialized]);

  const addToCart = (product, quantity = 1, selectedSize = null) => {
    console.log(
      "Adding to cart:",
      product.id,
      "quantity:",
      quantity,
      "size:",
      selectedSize,
      "for user:",
      currentUser?.id || "guest"
    );

    setCartItems((prevItems) => {
      const cartKey = getCartItemKey(product.id, selectedSize);

      const existingItem = prevItems.find((item) => {
        const itemKey =
          item.cartKey || getCartItemKey(item.id, item.selectedSize);
        return itemKey === cartKey;
      });

      if (existingItem) {
        console.log("Found existing item, updating quantity");
        return prevItems.map((item) => {
          const itemKey =
            item.cartKey || getCartItemKey(item.id, item.selectedSize);
          return itemKey === cartKey
            ? { ...item, quantity: item.quantity + quantity }
            : item;
        });
      } else {
        console.log("Adding new item to cart");
        return [
          ...prevItems,
          {
            ...product,
            quantity,
            selectedSize,
            cartKey,
          },
        ];
      }
    });
  };

  const removeFromCart = (productId, selectedSize = null) => {
    const cartKey = getCartItemKey(productId, selectedSize);
    setCartItems((prevItems) =>
      prevItems.filter((item) => {
        const itemKey =
          item.cartKey || getCartItemKey(item.id, item.selectedSize);
        return itemKey !== cartKey;
      })
    );
  };

  const updateQuantity = (productId, quantity, selectedSize = null) => {
    if (quantity <= 0) {
      removeFromCart(productId, selectedSize);
      return;
    }

    const cartKey = getCartItemKey(productId, selectedSize);
    setCartItems((prevItems) =>
      prevItems.map((item) => {
        const itemKey =
          item.cartKey || getCartItemKey(item.id, item.selectedSize);
        return itemKey === cartKey ? { ...item, quantity } : item;
      })
    );
  };

  // Clear cart - chỉ xóa giỏ hàng hiện tại (sau khi đặt hàng thành công)
  const clearCart = () => {
    setCartItems([]);
    console.log(`Cleared cart for user ${currentUser?.id || "guest"}`);
  };

  const getCartTotal = () => {
    return cartItems.reduce(
      (total, item) => total + item.price * item.quantity,
      0
    );
  };

  const getCartItemsCount = () => {
    return cartItems.reduce((total, item) => total + item.quantity, 0);
  };

  const value = {
    cartItems,
    addToCart,
    removeFromCart,
    updateQuantity,
    clearCart,
    getCartTotal,
    getCartItemsCount,
  };

  return <CartContext.Provider value={value}>{children}</CartContext.Provider>;
};
