import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const Products = () => {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchProducts();
    fetchCategories();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await axios.get('/api/products');
      setProducts(response.data);
    } catch (error) {
      console.error('Error fetching products:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchCategories = async () => {
    try {
      const response = await axios.get('/api/categories');
      setCategories(response.data);
    } catch (error) {
      console.error('Error fetching categories:', error);
    }
  };

  const filteredProducts = products.filter(product => {
    const matchesCategory = !selectedCategory || product.category_id == selectedCategory;
    const matchesSearch = product.name.toLowerCase().includes(searchTerm.toLowerCase());
    return matchesCategory && matchesSearch;
  });

  return (
    <div className="container my-5">
      <div className="text-center mb-5">
        <h1 className="display-5 fw-bold text-primary">Sản phẩm</h1>
        <p className="lead text-muted">Khám phá bộ sưu tập thiết bị cầu lông chất lượng cao</p>
      </div>
      
      {/* Filters */}
      <div className="row mb-4">
        <div className="col-lg-8 col-md-6 mb-3">
          <div className="input-group">
            <span className="input-group-text">
              <i className="bi bi-search"></i>
            </span>
            <input
              type="text"
              className="form-control"
              placeholder="Tìm kiếm sản phẩm theo tên..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>
        </div>
        <div className="col-lg-4 col-md-6 mb-3">
          <div className="input-group">
            <span className="input-group-text">
              <i className="bi bi-funnel"></i>
            </span>
            <select
              className="form-select"
              value={selectedCategory}
              onChange={(e) => setSelectedCategory(e.target.value)}
            >
              <option value="">Tất cả danh mục</option>
              {categories.map(category => (
                <option key={category.id} value={category.id}>
                  {category.name}
                </option>
              ))}
            </select>
          </div>
        </div>
      </div>

      {/* Products Grid */}
      {loading ? (
        <div className="text-center py-5">
          <div className="spinner-border spinner-border-lg" role="status">
            <span className="visually-hidden">Đang tải...</span>
          </div>
          <p className="mt-3 text-muted">Đang tải sản phẩm...</p>
        </div>
      ) : (
        <>
          {filteredProducts.length === 0 ? (
            <div className="text-center py-5">
              <div className="mb-4">
                <i className="bi bi-search display-1 text-muted"></i>
              </div>
              <h4 className="text-muted">Không tìm thấy sản phẩm nào</h4>
              <p className="text-muted">Hãy thử thay đổi từ khóa tìm kiếm hoặc bộ lọc</p>
            </div>
          ) : (
            <>
              <div className="d-flex justify-content-between align-items-center mb-4">
                <p className="text-muted mb-0">
                  Hiển thị {filteredProducts.length} sản phẩm
                </p>
              </div>
              
              <div className="row g-4">
                {filteredProducts.map((product, index) => (
                  <div key={product.id} className="col-xl-3 col-lg-4 col-md-6">
                    <div className="product-card h-100 fade-in-up" style={{animationDelay: `${index * 0.1}s`}}>
                      <div className="position-relative overflow-hidden">
                        <img 
                          src={product.image_url || 'https://via.placeholder.com/300x200/f8f9fa/6c757d?text=No+Image'} 
                          className="card-img-top" 
                          alt={product.name}
                          style={{ height: '220px', objectFit: 'cover' }}
                        />
                        {product.sale_price && (
                          <div className="position-absolute top-0 end-0 m-2">
                            <span className="badge bg-danger">
                              SALE {Math.round(((product.price - product.sale_price) / product.price) * 100)}%
                            </span>
                          </div>
                        )}
                        <div className="position-absolute top-0 start-0 m-2">
                          <span className="badge bg-primary">{product.category_name}</span>
                        </div>
                      </div>
                      <div className="card-body d-flex flex-column">
                        <h5 className="card-title text-dark fw-bold">{product.name}</h5>
                        <div className="mt-auto">
                          <div className="d-flex align-items-center justify-content-between mb-3">
                            <div>
                              {product.sale_price ? (
                                <>
                                  <span className="fw-bold text-danger fs-5">
                                    {new Intl.NumberFormat('vi-VN', { 
                                      style: 'currency', 
                                      currency: 'VND' 
                                    }).format(product.sale_price)}
                                  </span>
                                  <br />
                                  <small className="text-decoration-line-through text-muted">
                                    {new Intl.NumberFormat('vi-VN', { 
                                      style: 'currency', 
                                      currency: 'VND' 
                                    }).format(product.price)}
                                  </small>
                                </>
                              ) : (
                                <span className="fw-bold text-primary fs-5">
                                  {new Intl.NumberFormat('vi-VN', { 
                                    style: 'currency', 
                                    currency: 'VND' 
                                  }).format(product.price)}
                                </span>
                              )}
                            </div>
                            <small className="text-muted">
                              <i className="bi bi-box me-1"></i>
                              Còn {product.stock_quantity}
                            </small>
                          </div>
                          <Link 
                            to={`/products/${product.id}`} 
                            className="btn btn-primary w-100"
                          >
                            <i className="bi bi-eye me-2"></i>
                            Xem chi tiết
                          </Link>
                        </div>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </>
          )}
        </>
      )}
    </div>
  );
};

export default Products;
