import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';

const CATEGORY_IDS = {
  'giay-cau-long': 2,
  'vot-cau-long': 1,
  'ao-quan': 3,
  'phu-kien': 4
};

const Home = () => {
  const [categories, setCategories] = useState([]);
  const [productsByCategory, setProductsByCategory] = useState({});
  const [bestSellers, setBestSellers] = useState([]);
  const [favoriteProducts, setFavoriteProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [carouselIndex, setCarouselIndex] = useState({});

  useEffect(() => {
    fetchCategories();
    fetchProducts();
  }, []);

  const fetchCategories = async () => {
    try {
      const res = await axios.get('/api/categories');
      setCategories(res.data);
    } catch (error) {
      setCategories([]);
    }
  };

  const fetchProducts = async () => {
    try {
      const res = await axios.get('/api/products');
      // Phân loại sản phẩm theo danh mục
      const byCat = {};
      Object.values(CATEGORY_IDS).forEach(cid => {
        byCat[cid] = res.data.filter(p => p.category_id === cid);
      });
      setProductsByCategory(byCat);
      // Sản phẩm bán chạy: lấy 8 sản phẩm đầu (demo)
      setBestSellers(res.data.slice(0, 8));
      // Sản phẩm yêu thích: lấy 8 sản phẩm tiếp theo (demo)
      setFavoriteProducts(res.data.slice(8, 16));
    } catch (error) {
      setProductsByCategory({});
      setBestSellers([]);
      setFavoriteProducts([]);
    } finally {
      setLoading(false);
    }
  };

  // Xử lý chuyển slide cho từng danh mục
  const handlePrev = (catId) => {
    setCarouselIndex(idx => ({
      ...idx,
      [catId]: Math.max(0, (idx[catId] || 0) - 1)
    }));
  };
  const handleNext = (catId, max) => {
    setCarouselIndex(idx => ({
      ...idx,
      [catId]: Math.min(max, (idx[catId] || 0) + 1)
    }));
  };

  // Hiển thị sản phẩm dạng card theo mẫu hình
  const renderProductCard = (product) => (
    <div className="home-product-card" key={product.id}>
      <div className="position-relative">
        <img
          src={product.image_url || 'https://via.placeholder.com/300x200?text=No+Image'}
          alt={product.name}
          className="w-100"
          style={{height: '180px', objectFit: 'contain', background: '#fff'}}
        />
        {product.stock_quantity === 0 && (
          <span className="badge badge-het-hang position-absolute top-0 start-0 m-2">Hết hàng</span>
        )}
      </div>
      <div className="mt-3 px-2">
        <div className="fw-bold mb-1" style={{minHeight: '48px'}}>{product.name}</div>
        <div className="fw-bold text-danger mb-2" style={{fontSize: '1.3rem'}}>
          {new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(product.sale_price || product.price)}
        </div>
        <div className="mb-2">
          {product.stock_quantity === 0 ? (
            <Link to={`/products/${product.id}`} className="btn btn-light w-100 home-btn">
              <i className="bi bi-cart3 me-2"></i> Xem chi tiết
            </Link>
          ) : (
            <Link to={`/products/${product.id}`} className="btn btn-dark w-100 home-btn">
              <i className="bi bi-cart3 me-2"></i> Thêm vào giỏ
            </Link>
          )}
        </div>
      </div>
    </div>
  );

  // Hiển thị carousel sản phẩm theo danh mục
  const renderCategoryBlock = (catId, title, link) => {
    const products = productsByCategory[catId] || [];
    const idx = carouselIndex[catId] || 0;
    const visible = products.slice(idx, idx + 5);
    return (
      <div className="home-category-block mb-5">
        <div className="d-flex align-items-center mb-3">
          <div>
            <h2 className="home-category-title">{title}</h2>
            <div className="home-category-underline"></div>
          </div>
          <div className="ms-4 d-flex gap-2">
            <button className="home-carousel-btn" onClick={() => handlePrev(catId)} disabled={idx === 0}>
              <i className="bi bi-chevron-left"></i>
            </button>
            <button className="home-carousel-btn" onClick={() => handleNext(catId, Math.max(0, products.length - 5))} disabled={idx + 5 >= products.length}>
              <i className="bi bi-chevron-right"></i>
            </button>
          </div>
        </div>
        <div className="d-flex gap-3">
          {visible.map(renderProductCard)}
        </div>
        <div className="mt-4">
          <Link to={link} className="btn btn-dark home-btn-all">
            Xem tất cả <i className="bi bi-arrow-right"></i>
          </Link>
        </div>
      </div>
    );
  };

  // Hiển thị khối sản phẩm bán chạy/top yêu thích
  const renderBestSellerBlock = () => (
    <div className="home-category-block mb-5" style={{background:'#fafafa',padding:'32px 0'}}>
      <h2 className="home-category-title">TOP SẢN PHẨM BÁN CHẠY</h2>
      <div className="home-category-underline"></div>
      <div className="d-flex gap-3 mt-4">
        {bestSellers.map(renderProductCard)}
      </div>
    </div>
  );



  // Hiển thị khối thương hiệu nổi bật
  const renderBrandsBlock = () => (
    <div className="container my-5">
      <h2 className="fw-bold text-danger mb-4" style={{fontSize:'2rem'}}>THƯƠNG HIỆU NỔI BẬT</h2>
      <div className="d-flex flex-wrap gap-4 justify-content-center">
        <img src="/images/yonex-logo.png" alt="Yonex" style={{height:'60px'}} />
        <img src="/images/lining-logo.png" alt="Li-Ning" style={{height:'60px'}} />
        <img src="/images/mizuno-logo.png" alt="Mizuno" style={{height:'60px'}} />
        <img src="/images/victor-logo.jfif" alt="Victor" style={{height:'60px'}} />
        <img src="/images/kumpoo-logo.png" alt="Kumpoo" style={{height:'60px'}} />
         <img src="/images/victor-logo.jfif" alt="Victor" style={{height:'60px'}} />
        <img src="/images/kumpoo-logo.png" alt="Kumpoo" style={{height:'60px'}} />
      </div>
    </div>
  );

  return (
    <div>
      {/* Banner */}
      <div className="home-banner mb-5">
        <img src="/images/slides/slide1.jpg" alt="Banner" className="w-100" style={{maxHeight:'320px',objectFit:'cover'}} />
        <div className="home-banner-text">
          <h1 className="fw-bold" style={{fontSize:'2.5rem'}}>Play your style!</h1>
          <div className="fs-4">MY THO</div>
          <div className="mt-2">Since 2018 - www.tusport.vn</div>
        </div>
      </div>

      {/* Sản phẩm theo danh mục */}
      <div className="container">
        {renderCategoryBlock(CATEGORY_IDS['giay-cau-long'], 'GIÀY CẦU LÔNG', '/products?category=2')}
        {renderCategoryBlock(CATEGORY_IDS['vot-cau-long'], 'VỢT CẦU LÔNG', '/products?category=1')}
      </div>

      {/* Top bán chạy */}
      <div className="container">
        {renderBestSellerBlock()}
      </div>

     

      {/* Thương hiệu nổi bật */}
      {renderBrandsBlock()}

      {/* Features Section */}
      <div className="features-section">
        <div className="container">
          <div className="text-center mb-5">
            <h2 className="display-5 fw-bold text-primary mb-3">Tại sao chọn chúng tôi?</h2>
            <p className="lead text-muted">Cam kết mang đến trải nghiệm mua sắm tốt nhất</p>
          </div>
          <div className="row g-4">
            <div className="col-lg-4 col-md-6">
              <div className="feature-item">
                <div className="feature-icon">
                  <i className="bi bi-truck"></i>
                </div>
                <h4>Giao hàng nhanh chóng</h4>
                <p className="text-muted">
                  Giao hàng toàn quốc trong 24-48h. Miễn phí vận chuyển cho đơn hàng từ 500.000đ
                </p>
              </div>
            </div>
            <div className="col-lg-4 col-md-6">
              <div className="feature-item">
                <div className="feature-icon">
                  <i className="bi bi-shield-check"></i>
                </div>
                <h4>Chất lượng đảm bảo</h4>
                <p className="text-muted">
                  100% sản phẩm chính hãng từ các thương hiệu uy tín hàng đầu thế giới
                </p>
              </div>
            </div>
            <div className="col-lg-4 col-md-6 mx-auto">
              <div className="feature-item">
                <div className="feature-icon">
                  <i className="bi bi-headset"></i>
                </div>
                <h4>Hỗ trợ 24/7</h4>
                <p className="text-muted">
                  Đội ngũ tư vấn chuyên nghiệp sẵn sàng hỗ trợ bạn mọi lúc, mọi nơi
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Newsletter Section */}
      <div className="container my-5">
        <div className="row justify-content-center">
          <div className="col-lg-8">
            <div className="card border-0 shadow-lg" style={{background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)'}}>
              <div className="card-body text-center text-white p-5">
                <h3 className="fw-bold mb-3">
                  <i className="bi bi-envelope-heart me-2"></i>
                  Đăng ký nhận tin
                </h3>
                <p className="mb-4">Nhận thông tin về sản phẩm mới và ưu đãi đặc biệt</p>
                <div className="row justify-content-center">
                  <div className="col-md-8">
                    <div className="input-group">
                      <input 
                        type="email" 
                        className="form-control" 
                        placeholder="Nhập email của bạn..."
                      />
                      <button className="btn btn-light" type="button">
                        <i className="bi bi-send"></i>
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
