import React, { createContext, useState, useContext, useEffect } from "react";
import { useAuth } from "./AuthContext";
import axios from "axios";

const CartContext = createContext();

export const useCart = () => {
  return useContext(CartContext);
};

// Tạo key duy nhất cho sản phẩm + size
const getCartItemKey = (productId, selectedSize) => {
  return selectedSize ? `${productId}_${selectedSize.id}` : `${productId}`;
};

export const CartProvider = ({ children }) => {
  const [cartItems, setCartItems] = useState([]);
  const [isInitialized, setIsInitialized] = useState(false);
  const { currentUser } = useAuth();

  // Load giỏ hàng khi user thay đổi
  useEffect(() => {
    if (currentUser) {
      loadUserCart(currentUser.id);
    } else {
      // User logout - clear cart
      setCartItems([]);
      setIsInitialized(true);
    }
  }, [currentUser]);

  // Lưu giỏ hàng khi có thay đổi (chỉ khi đã login)
  useEffect(() => {
    if (isInitialized && currentUser) {
      saveUserCart(currentUser.id);
    }
  }, [cartItems, isInitialized, currentUser]);

  // Load giỏ hàng từ server cho user cụ thể
  const loadUserCart = async (userId) => {
    try {
      console.log(`Loading cart for user ${userId}`);
      
      const token = localStorage.getItem("token");
      const headers = token ? { Authorization: `Bearer ${token}` } : {};
      
      const response = await axios.get(`/api/cart/${userId}`, { headers });
      const items = response.data.items || [];

      // Normalize cart items với cartKey
      const normalizedCart = items.map((item) => {
        if (!item.cartKey) {
          const cartKey = getCartItemKey(item.id, item.selectedSize);
          return { ...item, cartKey };
        }
        return item;
      });

      console.log(`Successfully loaded ${normalizedCart.length} items for user ${userId}`);
      setCartItems(normalizedCart);
    } catch (error) {
      console.error("Error loading user cart:", error);
      if (error.response?.status === 403) {
        console.log("Access denied - user not authorized");
      } else if (error.response?.status === 404) {
        console.log("Cart not found - starting with empty cart");
      }
      setCartItems([]);
    } finally {
      setIsInitialized(true);
    }
  };

  // Lưu giỏ hàng lên server cho user cụ thể
  const saveUserCart = async (userId) => {
    try {
      console.log(`Saving cart for user ${userId}: ${cartItems.length} items`);
      
      const token = localStorage.getItem("token");
      const headers = token ? { Authorization: `Bearer ${token}` } : {};
      
      const response = await axios.post(`/api/cart/${userId}`, {
        items: cartItems,
      }, { headers });
      
      if (response.data.success) {
        console.log(`Cart saved successfully for user ${userId}`);
      }
    } catch (error) {
      console.error("Error saving user cart:", error);
      if (error.response?.status === 403) {
        console.log("Access denied - cannot save cart");
      }
    }
  };

  const addToCart = (product, quantity = 1, selectedSize = null) => {
    if (!currentUser) {
      console.log("User not logged in - cannot add to cart");
      return;
    }

    console.log(
      "Adding to cart:",
      product.id,
      "quantity:",
      quantity,
      "size:",
      selectedSize
    );

    setCartItems((prevItems) => {
      const cartKey = getCartItemKey(product.id, selectedSize);
      console.log("Generated cartKey:", cartKey);

      const existingItem = prevItems.find((item) => {
        const itemKey =
          item.cartKey || getCartItemKey(item.id, item.selectedSize);
        return itemKey === cartKey;
      });

      if (existingItem) {
        console.log("Found existing item, updating quantity");
        return prevItems.map((item) => {
          const itemKey =
            item.cartKey || getCartItemKey(item.id, item.selectedSize);
          return itemKey === cartKey
            ? { ...item, quantity: item.quantity + quantity }
            : item;
        });
      } else {
        console.log("Adding new item to cart");
        return [
          ...prevItems,
          {
            ...product,
            quantity,
            selectedSize,
            cartKey,
          },
        ];
      }
    });
  };

  const removeFromCart = (productId, selectedSize = null) => {
    if (!currentUser) return;

    const cartKey = getCartItemKey(productId, selectedSize);
    setCartItems((prevItems) =>
      prevItems.filter((item) => {
        const itemKey =
          item.cartKey || getCartItemKey(item.id, item.selectedSize);
        return itemKey !== cartKey;
      })
    );
  };

  const updateQuantity = (productId, quantity, selectedSize = null) => {
    if (!currentUser) return;

    if (quantity <= 0) {
      removeFromCart(productId, selectedSize);
      return;
    }

    const cartKey = getCartItemKey(productId, selectedSize);
    setCartItems((prevItems) =>
      prevItems.map((item) => {
        const itemKey =
          item.cartKey || getCartItemKey(item.id, item.selectedSize);
        return itemKey === cartKey ? { ...item, quantity } : item;
      })
    );
  };

  const clearCart = () => {
    setCartItems([]);
  };

  const getCartTotal = () => {
    return cartItems.reduce(
      (total, item) => total + item.price * item.quantity,
      0
    );
  };

  const getCartItemsCount = () => {
    return cartItems.reduce((total, item) => total + item.quantity, 0);
  };

  const value = {
    cartItems,
    addToCart,
    removeFromCart,
    updateQuantity,
    clearCart,
    getCartTotal,
    getCartItemsCount,
  };

  return <CartContext.Provider value={value}>{children}</CartContext.Provider>;
};
