    // X√≥a ƒëo·∫°n n√†y khi ƒë√£ c√≥ backend/api th·ª±c s·ª±
    const mockNews = [
      {
        id: 1,
        title: 'Gi·∫£i c·∫ßu l√¥ng qu·ªëc t·∫ø 2024: Vi·ªát Nam gi√†nh HCV',
        summary: 'ƒê·ªôi tuy·ªÉn c·∫ßu l√¥ng Vi·ªát Nam ƒë√£ xu·∫•t s·∫Øc gi√†nh huy ch∆∞∆°ng v√†ng t·∫°i gi·∫£i qu·ªëc t·∫ø 2024.',
        image: '/images/news1.jpg',
        date: '2024-06-10'
      },
      {
        id: 2,
        title: 'Top 5 m·∫´u v·ª£t c·∫ßu l√¥ng b√°n ch·∫°y nh·∫•t th√°ng 6',
        summary: 'C√πng ƒëi·ªÉm qua 5 m·∫´u v·ª£t c·∫ßu l√¥ng ƒë∆∞·ª£c y√™u th√≠ch v√† b√°n ch·∫°y nh·∫•t th√°ng n√†y.',
        image: '/images/news2.jpg',
        date: '2024-06-08'
      },
      {
        id: 3,
        title: 'H∆∞·ªõng d·∫´n ch·ªçn gi√†y c·∫ßu l√¥ng ph√π h·ª£p',
        summary: 'Ch·ªçn gi√†y c·∫ßu l√¥ng ƒë√∫ng c√°ch gi√∫p b·∫£o v·ªá ƒë√¥i ch√¢n v√† n√¢ng cao th√†nh t√≠ch thi ƒë·∫•u.',
        image: '/images/news3.jpg',
        date: '2024-06-05'
      }
    ];
    setNews(mockNews);
    setLoading(false);
    // fetchNews(); // B·ªè g·ªçi API n·∫øu ch∆∞a c√≥ database
  }, []);

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    if (!form.title || !form.summary || !form.date) return;
    try {
      if (editingId) {
        await axios.put(`/api/news/${editingId}`, form);
      } else {
        await axios.post('/api/news', form);
      }
      setForm({ title: '', summary: '', image: '', date: '' });
      setEditingId(null);
      fetchNews();
    } catch {}
  };

  const handleEdit = item => {
    setForm({
      title: item.title,
      summary: item.summary,
      image: item.image || '',
      date: item.date
    });
    setEditingId(item.id);
  };

  const handleDelete = async id => {
    if (!window.confirm('X√≥a tin t·ª©c n√†y?')) return;
    try {
      await axios.delete(`/api/news/${id}`);
      fetchNews();
    } catch {}
  };

  return (
    <AdminLayout>
      <div className="admin-card mb-4">
        <div className="card-header">
          <h4 className="mb-0">{editingId ? 'C·∫≠p nh·∫≠t tin t·ª©c' : 'Th√™m tin t·ª©c m·ªõi'}</h4>
        </div>
        <div className="card-body">
          <form className="row g-3" onSubmit={handleSubmit}>
            <div className="col-md-6">
              <label className="form-label">Ti√™u ƒë·ªÅ</label>
              <input className="form-control" name="title" value={form.title} onChange={handleChange} required />
            </div>
            <div className="col-md-6">
