                  <th style={{width: 120}}>Thao t√°c</th>
                </tr>
              </thead>
              <tbody>
                {loading ? (
                  <tr>
                    <td colSpan={5} className="text-center text-muted py-4">ƒêang t·∫£i...</td>
                  </tr>
                ) : news.length === 0 ? (
                  <tr>
                    <td colSpan={5} className="text-center text-muted py-4">Ch∆∞a c√≥ tin t·ª©c n√†o.</td>
                  </tr>
                ) : (
                  news.map(item => (
                    <tr key={item.id}>
                      <td>{item.title}</td>
                      <td>{item.date}</td>
                      <td>{item.summary}</td>
                      <td>
                        {item.image && <img src={item.image} alt="" style={{maxWidth: 80, maxHeight: 60, objectFit: 'cover'}} />}
                      </td>
                      <td>
                        <button className="btn btn-sm btn-outline-primary me-2" onClick={() => handleEdit(item)}>S·ª≠a</button>
                        <button className="btn btn-sm btn-outline-danger" onClick={() => handleDelete(item.id)}>X√≥a</button>
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </AdminLayout>
  );
};

export default AdminNews;
