import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const PAGE_SIZE = 16;

const Products = () => {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(true);
  const [page, setPage] = useState(1);
  const [childCategories, setChildCategories] = useState([]);

  useEffect(() => {
    fetchProducts();
    fetchCategories();
  }, []);

  useEffect(() => {
    // Nếu chọn "Phụ kiện", lấy các danh mục con
    if (selectedCategory && categories.length > 0) {
      const selectedCat = categories.find(cat => cat.id == selectedCategory);
      if (selectedCat && selectedCat.name === 'Phụ kiện') {
        axios.get(`/api/categories/${selectedCategory}/children`)
          .then(res => setChildCategories(res.data))
          .catch(() => setChildCategories([]));
      } else {
        setChildCategories([]);
      }
    } else {
      setChildCategories([]);
    }
  }, [selectedCategory, categories]);

  const fetchProducts = async () => {
    try {
      const response = await axios.get('/api/products');
      setProducts(response.data);
    } catch (error) {
      console.error('Error fetching products:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchCategories = async () => {
    try {
      const response = await axios.get('/api/categories');
      setCategories(response.data);
    } catch (error) {
      console.error('Error fetching categories:', error);
    }
  };

  const filteredProducts = (() => {
    // Nếu chọn "Phụ kiện" và có danh mục con
    if (
      selectedCategory &&
      categories.find(cat => cat.id == selectedCategory)?.name === 'Phụ kiện' &&
      childCategories.length > 0
    ) {
      const childCatIds = childCategories.map(cat => cat.id);
      return products.filter(
        product =>
          childCatIds.includes(product.category_id) &&
          product.name.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }
    // Trường hợp khác: lọc như cũ
    return products.filter(product => {
      const matchesCategory = !selectedCategory || product.category_id == selectedCategory;
      const matchesSearch = product.name.toLowerCase().includes(searchTerm.toLowerCase());
      return matchesCategory && matchesSearch;
    });
  })();

  // Nếu chọn "Phụ kiện" và có danh mục con, nhóm sản phẩm theo danh mục con
  let groupedPagedProducts;
  if (
    selectedCategory &&
    categories.find(cat => cat.id == selectedCategory)?.name === 'Phụ kiện' &&
    childCategories.length > 0
  ) {
    // Nhóm sản phẩm theo danh mục con
    groupedPagedProducts = childCategories.reduce((acc, cat) => {
      const items = filteredProducts.filter(p => p.category_id === cat.id);
      if (items.length > 0) acc[cat.name] = items;
      return acc;
    }, {});
  } else {
    // Nhóm theo category_name như cũ
    const sortedProducts = [...filteredProducts].sort((a, b) =>
      a.category_name.localeCompare(b.category_name)
    );
    const pagedProducts = sortedProducts.slice((page - 1) * PAGE_SIZE, page * PAGE_SIZE);
    groupedPagedProducts = pagedProducts.reduce((acc, product) => {
      const category = product.category_name || 'Khác';
      if (!acc[category]) acc[category] = [];
      acc[category].push(product);
      return acc;
    }, {});
  }

  const totalPages = Math.ceil(filteredProducts.length / PAGE_SIZE);

  const handlePageChange = (newPage) => {
    if (newPage >= 1 && newPage <= totalPages) setPage(newPage);
  };

  return (
    <div className="container my-5">
      <div className="text-center mb-5">
        <h1 className="display-5 fw-bold text-primary">Sản phẩm</h1>
        <p className="lead text-muted">Khám phá bộ sưu tập thiết bị cầu lông chất lượng cao</p>
      </div>

      {/* Bộ lọc */}
      <div className="row mb-4">
        <div className="col-lg-8 col-md-6 mb-3">
          <div className="input-group">
            <span className="input-group-text">
              <i className="bi bi-search"></i>
            </span>
            <input
              type="text"
              className="form-control"
              placeholder="Tìm kiếm sản phẩm theo tên..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>
        </div>
        <div className="col-lg-4 col-md-6 mb-3">
          <div className="input-group">
            <span className="input-group-text">
              <i className="bi bi-funnel"></i>
            </span>
            <select
              className="form-select"
              value={selectedCategory}
              onChange={(e) => { setSelectedCategory(e.target.value); setPage(1); }}
            >
              <option value="">Tất cả danh mục</option>
              {categories.map(category => (
                <option key={category.id} value={category.id}>
                  {category.name}
                </option>
              ))}
            </select>
          </div>
        </div>
      </div>

      {/* Hiển thị sản phẩm */}
      {loading ? (
        <div className="text-center py-5">
          <div className="spinner-border spinner-border-lg" role="status">
            <span className="visually-hidden">Đang tải...</span>
          </div>
          <p className="mt-3 text-muted">Đang tải sản phẩm...</p>
        </div>
      ) : (
        <>
          {filteredProducts.length === 0 ? (
            <div className="text-center py-5">
              <div className="mb-4">
                <i className="bi bi-search display-1 text-muted"></i>
              </div>
              <h4 className="text-muted">Không tìm thấy sản phẩm nào</h4>
              <p className="text-muted">Hãy thử thay đổi từ khóa tìm kiếm hoặc bộ lọc</p>
            </div>
          ) : (
            <>
              <div className="d-flex justify-content-between align-items-center mb-4">
                <p className="text-muted mb-0">
                  Hiển thị {filteredProducts.length} sản phẩm
                </p>
              </div>

              {/* Nếu chọn Phụ kiện thì hiển thị các danh mục con */}
              {selectedCategory &&
                categories.find(cat => cat.id == selectedCategory)?.name === 'Phụ kiện' &&
                childCategories.length > 0 ? (
                <div>
                  <h2 className="fw-bold text-primary mb-4">Phụ kiện</h2>
                  <div className="mb-4">
                    <div className="row">
                      {childCategories.map(childCat => (
                        <div key={childCat.id} className="col-12 mb-5">
                          <h4 className="fw-bold text-dark mb-3">{childCat.name}</h4>
                          <div className="row g-3">
                            {products
                              .filter(p => p.category_id === childCat.id && p.name.toLowerCase().includes(searchTerm.toLowerCase()))
                              .map((product, index) => (
                                <div key={product.id} className="col-xl-3 col-lg-4 col-md-6">
                                  <div
                                    className="product-card h-100 fade-in-up"
                                    style={{
                                      animationDelay: `${index * 0.05}s`,
                                      borderRadius: '10px',
                                      boxShadow: '0 4px 16px rgba(0,0,0,0.06)',
                                      overflow: 'hidden',
                                      backgroundColor: '#fff',
                                      transition: 'transform 0.2s ease',
                                    }}
                                    onMouseEnter={(e) => e.currentTarget.style.transform = 'translateY(-4px)'}
                                    onMouseLeave={(e) => e.currentTarget.style.transform = 'translateY(0)'}
                                  >
                                    <div className="position-relative overflow-hidden mb-2">
                                      <img
                                        src={product.image_url || 'https://via.placeholder.com/300x200/f8f9fa/6c757d?text=No+Image'}
                                        className="card-img-top"
                                        alt={product.name}
                                        style={{
                                          height: '320px',
                                          objectFit: 'cover',
                                          width: '100%',
                                        }}
                                      />
                                      {product.stock_quantity === 0 && (
                                        <div className="position-absolute top-0 start-0 m-2">
                                          <span className="badge bg-danger" style={{ fontWeight: 500, fontSize: '0.8rem' }}>Hết hàng</span>
                                        </div>
                                      )}
                                      {product.sale_price && (
                                        <div className="position-absolute top-0 end-0 m-2">
                                          <span className="badge bg-warning text-dark">
                                            SALE {Math.round(((product.price - product.sale_price) / product.price) * 100)}%
                                          </span>
                                        </div>
                                      )}
                                    </div>
                                    <div className="card-body d-flex flex-column">
                                      <h5 className="card-title text-dark fw-semibold" style={{ fontSize: '1rem', minHeight: '3em', paddingLeft: '10px' }}>
                                        {product.name}
                                      </h5>
                                      <div className="mb-1 text-muted small" style={{ paddingLeft: '10px' }}>{product.category_name}</div>
                                      <div className="mb-2" style={{ paddingLeft: '10px' }}>
                                        {product.sale_price ? (
                                          <>
                                            <span className="fw-bold text-danger fs-5">
                                              {new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(product.sale_price)}
                                            </span>
                                            <br />
                                            <small className="text-decoration-line-through text-muted">
                                              {new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(product.price)}
                                            </small>
                                          </>
                                        ) : (
                                          <span className="fw-bold text-danger fs-5">
                                            {new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(product.price)}
                                          </span>
                                        )}
                                      </div>
                                      <div className="mt-auto">
                                        <Link
                                          to={`/products/${product.id}`}
                                          className={`btn ${product.stock_quantity === 0 ? 'btn-outline-secondary' : 'btn-dark'} w-100`}
                                          style={{ fontWeight: 600, fontSize: '0.95rem', padding: '10px' }}
                                        >
                                          {product.stock_quantity === 0 ? 'Xem chi tiết' : 'Xem sản phẩm'}
                                        </Link>
                                      </div>
                                    </div>
                                  </div>
                                </div>
                              ))}
                          </div>
                        </div>
                      ))}
                    </div>
                  </div>
                </div>
              ) : (
                // Nếu không phải Phụ kiện, hiển thị như cũ
                Object.entries(groupedPagedProducts).map(([category, items]) => (
                  <div key={category} className="mb-5">
                    <h4 className="fw-bold text-primary mb-3">{category}</h4>
                    <div className="row g-3">
                      {items.map((product, index) => (
                        <div key={product.id} className="col-xl-3 col-lg-4 col-md-6">
                          <div
                            className="product-card h-100 fade-in-up"
                            style={{
                              animationDelay: `${index * 0.05}s`,
                              borderRadius: '10px',
                              boxShadow: '0 4px 16px rgba(0,0,0,0.06)',
                              overflow: 'hidden',
                              backgroundColor: '#fff',
                              transition: 'transform 0.2s ease',
                            }}
                            onMouseEnter={(e) => e.currentTarget.style.transform = 'translateY(-4px)'}
                            onMouseLeave={(e) => e.currentTarget.style.transform = 'translateY(0)'}
                          >
                            <div className="position-relative overflow-hidden mb-2">
                              <img
                                src={product.image_url || 'https://via.placeholder.com/300x200/f8f9fa/6c757d?text=No+Image'}
                                className="card-img-top"
                                alt={product.name}
                                style={{
                                  height: '320px',
                                  objectFit: 'cover',
                                  width: '100%',
                                }}
                              />
                              {product.stock_quantity === 0 && (
                                <div className="position-absolute top-0 start-0 m-2">
                                  <span className="badge bg-danger" style={{ fontWeight: 500, fontSize: '0.8rem' }}>Hết hàng</span>
                                </div>
                              )}
                              {product.sale_price && (
                                <div className="position-absolute top-0 end-0 m-2">
                                  <span className="badge bg-warning text-dark">
                                    SALE {Math.round(((product.price - product.sale_price) / product.price) * 100)}%
                                  </span>
                                </div>
                              )}
                            </div>
                            <div className="card-body d-flex flex-column">
                              <h5 className="card-title text-dark fw-semibold" style={{ fontSize: '1rem', minHeight: '3em', paddingLeft: '10px' }}>
                                {product.name}
                              </h5>
                              <div className="mb-1 text-muted small" style={{ paddingLeft: '10px' }}>{product.category_name}</div>
                              <div className="mb-2" style={{ paddingLeft: '10px' }}>
                                {product.sale_price ? (
                                  <>
                                    <span className="fw-bold text-danger fs-5">
                                      {new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(product.sale_price)}
                                    </span>
                                    <br />
                                    <small className="text-decoration-line-through text-muted">
                                      {new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(product.price)}
                                    </small>
                                  </>
                                ) : (
                                  <span className="fw-bold text-danger fs-5">
                                    {new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(product.price)}
                                  </span>
                                )}
                              </div>
                              <div className="mt-auto">
                                <Link
                                  to={`/products/${product.id}`}
                                  className={`btn ${product.stock_quantity === 0 ? 'btn-outline-secondary' : 'btn-dark'} w-100`}
                                  style={{ fontWeight: 600, fontSize: '0.95rem', padding: '10px' }}
                                >
                                  {product.stock_quantity === 0 ? 'Xem chi tiết' : 'Xem sản phẩm'}
                                </Link>
                              </div>
                            </div>
                          </div>
                        </div>
                      ))}
                    </div>
                  </div>
                ))
              )}

              {/* Phân trang */}
              {totalPages > 1 && (
                <div className="d-flex justify-content-center mt-4">
                  <nav>
                    <ul className="pagination">
                      <li className={`page-item${page === 1 ? ' disabled' : ''}`}>
                        <button className="page-link" onClick={() => handlePageChange(page - 1)}>&lt;</button>
                      </li>
                      {[...Array(totalPages)].map((_, i) => (
                        <li key={i + 1} className={`page-item${page === i + 1 ? ' active' : ''}`}>
                          <button className="page-link" onClick={() => handlePageChange(i + 1)}>{i + 1}</button>
                        </li>
                      ))}
                      <li className={`page-item${page === totalPages ? ' disabled' : ''}`}>
                        <button className="page-link" onClick={() => handlePageChange(page + 1)}>&gt;</button>
                      </li>
                    </ul>
                  </nav>
                </div>
              )}
            </>
          )}
        </>
      )}
    </div>
  );
};

export default Products;
