import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const CATEGORY_IDS = {
  'vot-cau-long': 1,
  'giay-cau-long': 2,
  'ao-quan': 3,
  'phu-kien': 4
};

const SLIDES = [
  { image: '/images/slides/slide1.jpg', title: 'Vợt Yonex 2024', desc: 'Bộ sưu tập vợt Yonex mới nhất', link: '/products?category=1' },
  { image: '/images/slides/slide2.jpg', title: 'Giày cầu lông sale 30%', desc: 'Khuyến mãi lớn cho giày cầu lông', link: '/products?category=2' },
  { image: '/images/slides/slide3.jpg', title: 'Phụ kiện cao cấp', desc: 'Túi vợt, cước, băng quấn chính hãng', link: '/products?category=4' }
];

const Home = () => {
  const [categories, setCategories] = useState([]);
  const [productsByCategory, setProductsByCategory] = useState({});
  const [slideIdx, setSlideIdx] = useState(0);

  useEffect(() => {
    fetchCategories();
    fetchProducts();
    const timer = setInterval(() => setSlideIdx(idx => (idx + 1) % SLIDES.length), 4000);
    return () => clearInterval(timer);
  }, []);

  const fetchCategories = async () => {
    try {
      const res = await axios.get('/api/categories');
      setCategories(res.data);
    } catch {
      setCategories([]);
    }
  };

  const fetchProducts = async () => {
    try {
      const res = await axios.get('/api/products');
      const byCat = {};
      Object.values(CATEGORY_IDS).forEach(cid => {
        byCat[cid] = res.data.filter(p => p.category_id === cid);
      });
      setProductsByCategory(byCat);
    } catch {
      setProductsByCategory({});
    }
  };

  // Card sản phẩm theo mẫu hình
  const renderProductCard = (product) => (
    <div className="product-card-demo" key={product.id}>
      <div className="product-card-img-wrap">
        <img
          src={product.image_url || 'https://via.placeholder.com/300x200?text=No+Image'}
          alt={product.name}
          className="product-card-img"
        />
        <div className="product-card-brand">FBSHOP</div>
        {product.sale_price && (
          <span className="product-card-sale">SALE {Math.round(((product.price - product.sale_price) / product.price) * 100)}%</span>
        )}
        {product.stock_quantity === 0 && (
          <span className="product-card-out">Hết hàng</span>
        )}
      </div>
      <div className="product-card-body">
        <div className="product-card-rating">
          {/* Demo rating: 4 sao */}
          {[...Array(5)].map((_, i) => (
            <i key={i} className={`bi ${i < 4 ? 'bi-star-fill text-warning' : 'bi-star text-muted'}`}></i>
          ))}
          <span className="ms-1 text-muted">(4)</span>
        </div>
        <div className="product-card-title">{product.name}</div>
        <div className="product-card-category text-muted">{product.category_name}</div>
        <div className="product-card-price">
          <span className="text-danger fw-bold">
            {new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(product.sale_price || product.price)}
          </span>
          {product.sale_price && (
            <span className="text-muted ms-2 text-decoration-line-through">
              {new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(product.price)}
            </span>
          )}
        </div>
        <div className="mt-3">
          {product.stock_quantity === 0 ? (
            <Link to={`/products/${product.id}`} className="btn btn-outline-secondary w-100">
              Xem chi tiết
            </Link>
          ) : (
            <Link to={`/products/${product.id}`} className="btn btn-primary w-100">
              <i className="bi bi-cart-plus me-2"></i>Thêm vào giỏ
            </Link>
          )}
        </div>
      </div>
    </div>
  );

  // Khối sản phẩm theo danh mục
  const renderCategoryBlock = (catId, title, link) => {
    const products = productsByCategory[catId] || [];
    return (
      <div className="mb-5">
        <div className="d-flex align-items-center mb-3">
          <h2 className="fw-bold product-block-title">{title}</h2>
          <div className="ms-auto">
            <Link to={link} className="btn btn-block-all">Xem tất cả</Link>
          </div>
        </div>
        <div className="product-block-list">
          {products.slice(0, 4).map(renderProductCard)}
        </div>
      </div>
    );
  };

  return (
    <div>
      {/* Slide Banner */}
      <div className="home-banner mb-5 position-relative">
        <img src={SLIDES[slideIdx].image} alt="Banner" className="w-100" style={{maxHeight:'320px',objectFit:'cover'}} />
        <div className="home-banner-text">
          <h1 className="fw-bold" style={{fontSize:'2.5rem'}}>{SLIDES[slideIdx].title}</h1>
          <div className="fs-4">{SLIDES[slideIdx].desc}</div>
          <div className="mt-2">
            <Link to={SLIDES[slideIdx].link} className="btn btn-primary btn-lg mt-2">Xem ngay</Link>
          </div>
        </div>
        <div className="home-banner-controls position-absolute bottom-0 end-0 p-3">
          {SLIDES.map((_, idx) => (
            <button
              key={idx}
              className={`banner-dot${slideIdx === idx ? ' active' : ''}`}
              onClick={() => setSlideIdx(idx)}
              style={{margin: '0 4px'}}
            ></button>
          ))}
        </div>
      </div>

      {/* Danh mục sản phẩm */}
      <div className="container mb-5">
        <h2 className="fw-bold text-primary mb-4">Danh mục sản phẩm</h2>
        <div className="row g-4">
          {categories.map(cat => (
            <div key={cat.id} className="col-lg-3 col-md-4 col-sm-6">
              <div className="card h-100 shadow-sm card-category">
                <div className="card-body d-flex flex-column align-items-center justify-content-center">
                  <img
                    src={cat.image || 'https://via.placeholder.com/120x120?text=Category'}
                    alt={cat.name}
                    style={{ width: '120px', height: '120px', objectFit: 'cover', borderRadius: '16px', background: '#f8f9fa' }}
                  />
                  <h5 className="card-title text-dark fw-bold mb-2 mt-3">{cat.name}</h5>
                  <p className="card-text text-muted text-center mb-3" style={{minHeight: '48px'}}>
                    {cat.description}
                  </p>
                  <Link
                    to={`/products?category=${cat.id}`}
                    className="btn btn-primary w-100"
                  >
                    Xem sản phẩm
                  </Link>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Sản phẩm theo từng danh mục */}
      <div className="container">
        {renderCategoryBlock(CATEGORY_IDS['vot-cau-long'], 'Vợt cầu lông', '/products?category=1')}
        {renderCategoryBlock(CATEGORY_IDS['giay-cau-long'], 'Giày cầu lông', '/products?category=2')}
        {renderCategoryBlock(CATEGORY_IDS['ao-quan'], 'Áo quần', '/products?category=3')}
        {renderCategoryBlock(CATEGORY_IDS['phu-kien'], 'Phụ kiện', '/products?category=4')}
      </div>

      {/* Top bán chạy */}
      <div className="container">
        {renderBestSellerBlock()}
      </div>

      {/* Thương hiệu nổi bật */}
      {renderBrandsBlock()}

      {/* Features Section */}
      <div className="features-section">
        <div className="container">
          <div className="text-center mb-5">
            <h2 className="display-5 fw-bold text-primary mb-3">Tại sao chọn chúng tôi?</h2>
            <p className="lead text-muted">Cam kết mang đến trải nghiệm mua sắm tốt nhất</p>
          </div>
          <div className="row g-4">
            <div className="col-lg-4 col-md-6">
              <div className="feature-item">
                <div className="feature-icon">
                  <i className="bi bi-truck"></i>
                </div>
                <h4>Giao hàng nhanh chóng</h4>
                <p className="text-muted">
                  Giao hàng toàn quốc trong 24-48h. Miễn phí vận chuyển cho đơn hàng từ 500.000đ
                </p>
              </div>
            </div>
            <div className="col-lg-4 col-md-6">
              <div className="feature-item">
                <div className="feature-icon">
                  <i className="bi bi-shield-check"></i>
                </div>
                <h4>Chất lượng đảm bảo</h4>
                <p className="text-muted">
                  100% sản phẩm chính hãng từ các thương hiệu uy tín hàng đầu thế giới
                </p>
              </div>
            </div>
            <div className="col-lg-4 col-md-6 mx-auto">
              <div className="feature-item">
                <div className="feature-icon">
                  <i className="bi bi-headset"></i>
                </div>
                <h4>Hỗ trợ 24/7</h4>
                <p className="text-muted">
                  Đội ngũ tư vấn chuyên nghiệp sẵn sàng hỗ trợ bạn mọi lúc, mọi nơi
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Newsletter Section */}
      <div className="container my-5">
        <div className="row justify-content-center">
          <div className="col-lg-8">
            <div className="card border-0 shadow-lg" style={{background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)'}}>
              <div className="card-body text-center text-white p-5">
                <h3 className="fw-bold mb-3">
                  <i className="bi bi-envelope-heart me-2"></i>
                  Đăng ký nhận tin
                </h3>
                <p className="mb-4">Nhận thông tin về sản phẩm mới và ưu đãi đặc biệt</p>
                <div className="row justify-content-center">
                  <div className="col-md-8">
                    <div className="input-group">
                      <input 
                        type="email" 
                        className="form-control" 
                        placeholder="Nhập email của bạn..."
                      />
                      <button className="btn btn-light" type="button">
                        <i className="bi bi-send"></i>
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
