import React, { useState, useEffect } from 'react';
import AdminLayout from '../../components/AdminLayout';
import Modal from '../../components/Modal';
import { useModal } from '../../hooks/useModal';
import axios from 'axios';

const AdminProducts = () => {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [brands, setBrands] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [editingProduct, setEditingProduct] = useState(null);
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    price: '',
    stock_quantity: '',
    category_id: '',
    brand_id: '',
    image_url: '',
    sku: '',
    slug: '',
    status: 'active'
  });
  const [loading, setLoading] = useState(true);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [searchTerm, setSearchTerm] = useState('');
  const itemsPerPage = 10;

  const { modal, hideModal, showSuccess, showError, showConfirm } = useModal();

  useEffect(() => {
    fetchProducts();
    fetchCategories();
    fetchBrands();
  }, [currentPage, searchTerm]);

  const fetchProducts = async () => {
    try {
      const response = await axios.get('/api/products');
      const allProducts = response.data;

      // Filter by search term
      const filteredProducts = searchTerm 
        ? allProducts.filter(product => 
            product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
            product.sku.toLowerCase().includes(searchTerm.toLowerCase())
          )
        : allProducts;

      // Calculate pagination
      const totalItems = filteredProducts.length;
      const totalPagesCount = Math.ceil(totalItems / itemsPerPage);
      setTotalPages(totalPagesCount);

      // Get current page items
      const startIndex = (currentPage - 1) * itemsPerPage;
      const endIndex = startIndex + itemsPerPage;
      const currentProducts = filteredProducts.slice(startIndex, endIndex);

      setProducts(currentProducts);
    } catch (error) {
      console.error('Error fetching products:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchCategories = async () => {
    try {
      const response = await axios.get('/api/categories');
      setCategories(response.data);
    } catch (error) {
      console.error('Error fetching categories:', error);
    }
  };

  const fetchBrands = async () => {
    try {
      const response = await axios.get('/api/brands');
      setBrands(response.data);
    } catch (error) {
      console.error('Error fetching brands:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (
      !formData.name ||
      !formData.price ||
      !formData.stock_quantity ||
      !formData.category_id ||
      !formData.sku
    ) {
      showError('Vui lÃ²ng nháº­p Ä‘áº§y Ä‘á»§ cÃ¡c trÆ°á»ng báº¯t buá»™c!');
      return;
    }
    try {
      // Chuáº©n hÃ³a dá»¯ liá»‡u gá»­i lÃªn backend
      const dataToSend = {
        ...formData,
        price: parseFloat(formData.price),
        stock_quantity: parseInt(formData.stock_quantity),
        category_id: parseInt(formData.category_id),
        brand_id: formData.brand_id ? parseInt(formData.brand_id) : null,
        status: formData.status || 'active',
        slug: formData.slug || formData.name.toLowerCase().replace(/\s+/g, '-'),
        sku: formData.sku || `SKU-${Date.now()}`
      };

      if (editingProduct) {
        await axios.put(`/api/products/${editingProduct.id}`, dataToSend);
        showSuccess('Cáº­p nháº­t sáº£n pháº©m thÃ nh cÃ´ng!');
      } else {
        await axios.post('/api/products', dataToSend);
        showSuccess('ThÃªm sáº£n pháº©m thÃ nh cÃ´ng!');
      }

      setShowModal(false);
      setEditingProduct(null);
      resetForm();
      fetchProducts();
    } catch (error) {
      console.error('Error saving product:', error);
      showError(error.response?.data?.message || 'CÃ³ lá»—i xáº£y ra!');
    }
  };

  const handleEdit = (product) => {
    setEditingProduct(product);
    setFormData({
      name: product.name,
      description: product.description || '',
      price: product.price,
      stock_quantity: product.stock_quantity,
      category_id: product.category_id,
      brand_id: product.brand_id || '',
      image_url: product.image_url || '',
      sku: product.sku || '',
      slug: product.slug || '',
      status: product.status || 'active'
    });
    setShowModal(true);
  };

  const handleDelete = async (id) => {
    if (!id || isNaN(Number(id))) {
      showError('ID sáº£n pháº©m khÃ´ng há»£p lá»‡!');
      return;
    }

    showConfirm(
      'Báº¡n cÃ³ cháº¯c muá»‘n xÃ³a sáº£n pháº©m nÃ y?',
      async () => {
        try {
          await axios.delete(`/api/products/${id}`);
          showSuccess('XÃ³a sáº£n pháº©m thÃ nh cÃ´ng!');
          fetchProducts();
        } catch (error) {
          console.error('Error deleting product:', error);
          showError(error.response?.data?.message || 'CÃ³ lá»—i xáº£y ra!');
        }
      }
    );
  };

  const resetForm = () => {
    setFormData({
      name: '',
      description: '',
      price: '',
      stock_quantity: '',
      category_id: '',
      brand_id: '',
      image_url: '',
      sku: '',
      slug: '',
      status: 'active'
    });
  };

  const handleModalClose = () => {
    setShowModal(false);
    setEditingProduct(null);
    resetForm();
  };

  return (
    <>
      <AdminLayout>
        <div className="d-flex justify-content-between align-items-center mb-4">
          <h2>ðŸ“¦ Quáº£n lÃ½ sáº£n pháº©m</h2>
          <button 
            className="btn btn-admin-primary"
            onClick={() => setShowModal(true)}
          >
            âž• ThÃªm sáº£n pháº©m
          </button>
        </div>

        {/* Search and Filter */}
        <div className="row mb-3">
          <div className="col-md-6">
            <div className="input-group">
              <input
                type="text"
                className="form-control"
                placeholder="TÃ¬m kiáº¿m theo tÃªn hoáº·c SKU..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
              <button 
                className="btn btn-outline-secondary" 
                type="button"
                onClick={() => setSearchTerm('')}
              >
                XÃ³a
              </button>
            </div>
          </div>
        </div>

        {loading ? (
          <div className="text-center py-5">
            <div className="spinner-border text-primary" role="status">
              <span className="visually-hidden">Loading...</span>
            </div>
          </div>
        ) : (
          <div className="admin-table">
            <table className="table">
              <thead>
                <tr>
                  <th>ID</th>
                  <th>HÃ¬nh áº£nh</th>
                  <th>TÃªn sáº£n pháº©m</th>
                  <th>Danh má»¥c</th>
                  <th>GiÃ¡</th>
                  <th>Tá»“n kho</th>
                  <th>Thao tÃ¡c</th>
                </tr>
              </thead>
              <tbody>
                {products.map(product => (
                  <tr key={product.id}>
                    <td>{product.id}</td>
                    <td>
                      <img 
                        src={product.image_url || 'https://via.placeholder.com/50x50'} 
                        alt={product.name}
                        style={{ width: '50px', height: '50px', objectFit: 'cover' }}
                        className="rounded"
                      />
                    </td>
                    <td>{product.name}</td>
                    <td>{product.category_name}</td>
                    <td>
                      {new Intl.NumberFormat('vi-VN', { 
                        style: 'currency', 
                        currency: 'VND' 
                      }).format(product.price)}
                    </td>
                    <td>{product.stock_quantity}</td>
                    <td>
                      <button 
                        className="btn btn-sm btn-outline-primary me-2"
                        onClick={() => handleEdit(product)}
                      >
                        Sá»­a
                      </button>
                      <button 
                        className="btn btn-sm btn-outline-danger"
                        onClick={() => handleDelete(product.id)}
                      >
                        XÃ³a
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}

        {/* Pagination */}
        {totalPages > 1 && (
          <nav aria-label="Product pagination" className="mt-4">
            <ul className="pagination justify-content-center">
              <li className={`page-item ${currentPage === 1 ? 'disabled' : ''}`}>
                <button 
                  className="page-link" 
                  onClick={() => setCurrentPage(currentPage - 1)}
                  disabled={currentPage === 1}
                >
                  TrÆ°á»›c
                </button>
              </li>

              {Array.from({ length: totalPages }, (_, i) => i + 1).map(page => (
                <li key={page} className={`page-item ${currentPage === page ? 'active' : ''}`}>
                  <button 
                    className="page-link" 
                    onClick={() => setCurrentPage(page)}
                  >
                    {page}
                  </button>
                </li>
              ))}

              <li className={`page-item ${currentPage === totalPages ? 'disabled' : ''}`}>
                <button 
                  className="page-link" 
                  onClick={() => setCurrentPage(currentPage + 1)}
                  disabled={currentPage === totalPages}
                >
                  Sau
                </button>
              </li>
            </ul>
          </nav>
        )}

        {/* Modal with admin styling */}
        {showModal && (
          <div className="modal show d-block admin-modal" tabIndex="-1">
            <div className="modal-dialog">
              <div className="modal-content">
                <div className="modal-header">
                  <h5 className="modal-title">
                    {editingProduct ? 'Sá»­a sáº£n pháº©m' : 'ThÃªm sáº£n pháº©m'}
                  </h5>
                  <button 
                    type="button" 
                    className="btn-close" 
                    onClick={handleModalClose}
                  ></button>
                </div>
                <form onSubmit={handleSubmit}>
                  <div className="modal-body">
                    <div className="mb-3">
                      <label className="form-label">TÃªn sáº£n pháº©m:</label>
                      <input
                        type="text"
                        className="form-control"
                        value={formData.name}
                        onChange={(e) => setFormData({...formData, name: e.target.value})}
                        required
                      />
                    </div>

                    <div className="mb-3">
                      <label className="form-label">MÃ´ táº£:</label>
                      <textarea
                        className="form-control"
                        rows="3"
                        value={formData.description}
                        onChange={(e) => setFormData({...formData, description: e.target.value})}
                      />
                    </div>

                    <div className="row">
                      <div className="col-md-6 mb-3">
                        <label className="form-label">GiÃ¡:</label>
                        <input
                          type="number"
                          className="form-control"
                          value={formData.price}
                          onChange={(e) => setFormData({...formData, price: e.target.value})}
                          required
                        />
                      </div>

                      <div className="col-md-6 mb-3">
                        <label className="form-label">Tá»“n kho:</label>
                        <input
                          type="number"
                          className="form-control"
                          value={formData.stock_quantity}
                          onChange={(e) => setFormData({...formData, stock_quantity: e.target.value})}
                          required
                        />
                      </div>
                    </div>

                    <div className="mb-3">
                      <label className="form-label">Danh má»¥c:</label>
                      <select
                        className="form-select"
                        value={formData.category_id}
                        onChange={(e) => setFormData({...formData, category_id: e.target.value})}
                        required
                      >
                        <option value="">Chá»n danh má»¥c</option>
                        {categories.map(category => (
                          <option key={category.id} value={category.id}>
                            {category.name}
                          </option>
                        ))}
                      </select>
                    </div>

                    <div className="mb-3">
                      <label className="form-label">ThÆ°Æ¡ng hiá»‡u:</label>
                      <select
                        className="form-select"
                        value={formData.brand_id}
                        onChange={(e) => setFormData({...formData, brand_id: e.target.value})}
                      >
                        <option value="">Chá»n thÆ°Æ¡ng hiá»‡u</option>
                        {brands.map(brand => (
                          <option key={brand.id} value={brand.id}>
                            {brand.name}
                          </option>
                        ))}
                      </select>
                    </div>

                    <div className="mb-3">
                      <label className="form-label">SKU:</label>
                      <input
                        type="text"
                        className="form-control"
                        value={formData.sku}
                        onChange={(e) => setFormData({...formData, sku: e.target.value})}
                      />
                    </div>

                    <div className="mb-3">
                      <label className="form-label">Slug:</label>
                      <input
                        type="text"
                        className="form-control"
                        value={formData.slug}
                        onChange={(e) => setFormData({...formData, slug: e.target.value})}
                      />
                    </div>

                    <div className="mb-3">
                      <label className="form-label">Tráº¡ng thÃ¡i:</label>
                      <select
                        className="form-select"
                        value={formData.status}
                        onChange={(e) => setFormData({...formData, status: e.target.value})}
                      >
                        <option value="active">KÃ­ch hoáº¡t</option>
                        <option value="inactive">KhÃ´ng kÃ­ch hoáº¡t</option>
                      </select>
                    </div>

                    <div className="mb-3">
                      <label className="form-label">URL hÃ¬nh áº£nh:</label>
                      <input
                        type="url"
                        className="form-control"
                        value={formData.image_url}
                        onChange={(e) => setFormData({...formData, image_url: e.target.value})}
                      />
                    </div>
                  </div>
                  <div className="modal-footer">
                    <button 
                      type="button" 
                      className="btn btn-secondary" 
                      onClick={handleModalClose}
                    >
                      Há»§y
                    </button>
                    <button type="submit" className="btn btn-primary">
                      {editingProduct ? 'Cáº­p nháº­t' : 'ThÃªm'}
                    </button>
                  </div>
                </form>
              </div>
            </div>
          </div>
        )}
      </AdminLayout>

      <Modal
        show={modal.show}
        onClose={hideModal}
        title={modal.title}
        message={modal.message}
        type={modal.type}
        onConfirm={modal.onConfirm}
        confirmText={modal.confirmText}
        cancelText={modal.cancelText}
        showCancel={modal.showCancel}
      />
    </>
  );
};

export default AdminProducts;
