import React, { useEffect, useState } from 'react';
import axios from 'axios';

const News = () => {
  const [news, setNews] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get('/api/news')
      .then(res => setNews(res.data))
      .catch(() => setNews([]))
      .finally(() => setLoading(false));
  }, []);

  return (
    <div className="container my-5">
      <h2 className="fw-bold mb-4 text-success">Tin tức cầu lông</h2>
      <div className="row g-4">
        {loading ? (
          <div className="col-12 text-center py-5">
            <div className="spinner-border" role="status"></div>
            <div className="mt-3 text-muted">Đang tải tin tức...</div>
          </div>
        ) : news.length === 0 ? (
          <div className="col-12 text-center text-muted py-5">
            Chưa có tin tức nào.
          </div>
        ) : (
          news.map(item => (
            <div className="col-md-4" key={item.id}>
              <div className="card h-100 shadow-sm">
                <img
                  src={item.image}
                  alt={item.title}
                  className="card-img-top"
                  style={{ height: 200, objectFit: 'cover' }}
                />
                <div className="card-body d-flex flex-column">
                  <h5 className="card-title fw-bold">{item.title}</h5>
                  <div className="mb-2 text-muted" style={{ fontSize: '0.95rem' }}>
                    <i className="bi bi-calendar-event"></i> {item.date}
                  </div>
                  <p className="card-text">{item.summary}</p>
                  <div className="mt-auto">
                    <button className="btn btn-outline-success w-100" disabled>
                      Xem chi tiết
                    </button>
                  </div>
                </div>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default News;
