import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useCart } from '../contexts/CartContext';
import Modal from '../components/Modal';
import { useModal } from '../hooks/useModal';
import axios from 'axios';

const ProductDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { addToCart } = useCart();
  const { modal, hideModal, showSuccess, showError } = useModal();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [quantity, setQuantity] = useState(1);
  const [selectedSize, setSelectedSize] = useState('');

  useEffect(() => {
    fetchProduct();
  }, [id]);

  const fetchProduct = async () => {
    try {
      const response = await axios.get(`/api/products/${id}`);
      setProduct(response.data);
    } catch (error) {
      console.error('Error fetching product:', error);
      if (error.response?.status === 404) {
        navigate('/products');
      }
    } finally {
      setLoading(false);
    }
  };

  // Define size options based on category
  const getSizeOptions = (categoryName) => {
    if (!categoryName) return [];
    
    const category = categoryName.toLowerCase();
    
    // Giày cầu lông
    if (category.includes('giày')) {
      return [
        { value: '36', label: '36' },
        { value: '37', label: '37' },
        { value: '38', label: '38' },
        { value: '39', label: '39' },
        { value: '39.5', label: '39.5' },
        { value: '40', label: '40' },
        { value: '40.5', label: '40.5' },
        { value: '41', label: '41' },
        { value: '42', label: '42' },
        { value: '43', label: '43' },
        { value: '44', label: '44' },
        { value: '45', label: '45' }
      ];
    }
    
    // Áo, quần, váy cầu lông
    if (category.includes('áo') || category.includes('quần') || category.includes('váy')) {
      return [
        { value: 'XS', label: 'XS' },
        { value: 'S', label: 'S' },
        { value: 'M', label: 'M' },
        { value: 'L', label: 'L' },
        { value: 'XL', label: 'XL' },
        { value: '2XL', label: '2XL' }
      ];
    }
    
    return [];
  };

  const sizeOptions = getSizeOptions(product?.category_name);
  const requiresSize = sizeOptions.length > 0;

  const handleAddToCart = () => {
    if (product) {
      // Validate size selection if required
      if (requiresSize && !selectedSize) {
        showError('Vui lòng chọn size!');
        return;
      }

      addToCart({
        ...product,
        quantity: quantity,
        selectedSize: selectedSize || null
      });
      
      const sizeText = selectedSize ? ` (Size: ${selectedSize})` : '';
      showSuccess(`Đã thêm sản phẩm${sizeText} vào giỏ hàng!`);
    }
  };

  if (loading) {
    return (
      <div className="container my-5">
        <div className="text-center py-5">
          <div className="spinner-border spinner-border-lg" role="status">
            <span className="visually-hidden">Đang tải...</span>
          </div>
        </div>
      </div>
    );
  }

  if (!product) {
    return (
      <div className="container my-5">
        <div className="text-center py-5">
          <h3>Không tìm thấy sản phẩm</h3>
          <button 
            className="btn btn-dark mt-3"
            onClick={() => navigate('/products')}
          >
            Quay lại danh sách sản phẩm
          </button>
        </div>
      </div>
    );
  }

  const currentPrice = product.sale_price || product.price;
  const originalPrice = product.price;
  const discountPercentage = product.sale_price 
    ? Math.round(((originalPrice - product.sale_price) / originalPrice) * 100)
    : 0;

  return (
    <>
      <div className="container my-5">
        {/* Breadcrumb */}
        <nav aria-label="breadcrumb" className="mb-4">
          <ol className="breadcrumb">
            <li className="breadcrumb-item">
              <button 
                className="btn btn-link p-0"
                onClick={() => navigate('/')}
                style={{ color: '#00a61eff', textDecoration: 'underline', fontWeight: 500 }}

              >
                Trang chủ
              </button>
            </li>
            <li className="breadcrumb-item">
              <button 
                className="btn btn-link p-0"
                onClick={() => navigate('/products')}
                style={{ color: '#00a61eff', textDecoration: 'underline', fontWeight: 500 }}

              >
                Sản phẩm
              </button>
            </li>
            <li className="breadcrumb-item active">{product.name}</li>
          </ol>
        </nav>

        <div className="row g-5">
          {/* Product Image */}
          <div className="col-lg-5">
            <div className="position-relative">
              <img 
                src={product.image_url || 'https://via.placeholder.com/600x400/f8f9fa/6c757d?text=No+Image'} 
                alt={product.name}
                className="img-fluid rounded shadow-lg w-100"
                style={{ maxHeight: '500px', objectFit: 'cover' }}
              />
              {product.stock_quantity === 0 && (
                <div className="position-absolute top-0 start-0 m-3">
                  <span className="badge bg-danger fs-6 p-2" style={{fontWeight:600}}>Hết hàng</span>
                </div>
              )}
              {discountPercentage > 0 && (
                <div className="position-absolute top-0 end-0 m-3">
                  <span className="badge bg-warning text-dark fs-6 p-2">
                    SALE {discountPercentage}%
                  </span>
                </div>
              )}
            </div>
          </div>

          {/* Product Info */}
          <div className="col-lg-7">
            <h1 className="fw-bold text-dark mb-2" style={{fontSize:'2rem'}}>{product.name}</h1>
            <div className="mb-2">
              <span className="badge bg-primary me-2">{product.category_name}</span>
              {product.brand_name && (
                <span className="badge bg-success">{product.brand_name}</span>
              )}
            </div>
            <div className="mb-3">
              <span className="fw-bold text-danger" style={{fontSize:'2rem'}}>
                {new Intl.NumberFormat('vi-VN', { 
                  style: 'currency', 
                  currency: 'VND' 
                }).format(currentPrice)}
              </span>
              {product.sale_price && (
                <span className="fs-5 text-decoration-line-through text-muted ms-3">
                  {new Intl.NumberFormat('vi-VN', { 
                    style: 'currency', 
                    currency: 'VND' 
                  }).format(originalPrice)}
                </span>
              )}
            </div>
            <div className="mb-3">
              {product.stock_quantity > 0 ? (
                <span className="text-success fw-bold">Còn hàng ({product.stock_quantity})</span>
              ) : (
                <span className="text-danger fw-bold">Hết hàng</span>
              )}
            </div>
            <div className="mb-3">
              {requiresSize && (
                <div>
                  <label className="form-label fw-medium">
                    Chọn size: <span className="text-danger">*</span>
                  </label>
                  

                  {product.category_name?.toLowerCase().includes('giày') ? (
                    // Hiển thị size giày dạng radio buttons inline
                    <div className="d-flex flex-wrap gap-2">
                      {sizeOptions.map(option => (
                        <div key={option.value}>
                          <input
                            type="radio"
                            className="btn-check"
                            name="size"
                            id={`size-${option.value}`}
                            value={option.value}
                            checked={selectedSize === option.value}
                            onChange={(e) => setSelectedSize(e.target.value)}
                          />
                          <label
                            className="btn btn-outline-primary btn-sm"
                            htmlFor={`size-${option.value}`}
                            style={{ minWidth: '50px' }}
                          >
                            {option.label}
                          </label>
                        </div>
                      ))}
                    </div>
                  ) : (
                    // Hiển thị size quần áo dạng buttons lớn hơn
                    <div className="d-flex flex-wrap gap-2">
                      {sizeOptions.map(option => (
                        <div key={option.value}>
                          <input
                            type="radio"
                            className="btn-check"
                            name="size"
                            id={`size-${option.value}`}
                            value={option.value}
                            checked={selectedSize === option.value}
                            onChange={(e) => setSelectedSize(e.target.value)}
                          />
                          <label
                            className="btn btn-outline-success"
                            htmlFor={`size-${option.value}`}
                            style={{ minWidth: '60px', fontWeight: '600' }}
                          >
                            {option.label}
                          </label>
                        </div>
                      ))}
                    </div>
                  )}
                  

                  {!selectedSize && (
                    <div className="text-muted small mt-2">
                      <i className="bi bi-info-circle me-1"></i>
                      Vui lòng chọn size trước khi thêm vào giỏ hàng
                    </div>
                  )}
                </div>
              )}
            </div>
            {/* Add to Cart */}
            {product.stock_quantity > 0 && (
              <div className="mb-4">
                <div className="row g-3 align-items-end">
                  <div className="col-auto">
                    <label className="form-label fw-medium">Số lượng:</label>
                    <div className="input-group" style={{width: '150px'}}>
                      <button 
                        className="btn btn-outline-success"
                        onClick={() => setQuantity(Math.max(1, quantity - 1))}
                      >
                        <i className="bi bi-dash"></i>
                      </button>
                      <input 
                        type="number" 
                        className="form-control text-center "
                        value={quantity}
                        onChange={(e) => setQuantity(Math.max(1, Math.min(product.stock_quantity, parseInt(e.target.value) || 1)))}
                        min="1"
                        max={product.stock_quantity}
                      />
                      <button 
                        className="btn btn-outline-success"
                        onClick={() => setQuantity(Math.min(product.stock_quantity, quantity + 1))}
                      >
                        <i className="bi bi-plus"></i>
                      </button>
                    </div>
                  </div>
                  <div className="col">
                    <button 
                      className="btn btn-outline-success fw-bold btn-lg px-3 fs-6" 
                      style={{height:'38px'}}
                      onClick={handleAddToCart}
                    >
                      <i className="bi bi-cart-plus me-2"></i>
                      Thêm vào giỏ hàng
                    </button>
                  </div>
                </div>
              </div>
            )}
            
            {product.short_description && (
              <div className="mb-3">
                <strong>Đặc điểm:</strong>
                <div className="text-muted">{product.short_description}</div>
              </div>
            )}
            
            {/* Info Table */}
            <div className="mb-4">
              <table className="table table-bordered">
                <tbody>
                  <tr>
                    <th style={{width:'160px'}}>Mã sản phẩm</th>
                    <td>{product.sku}</td>
                  </tr>
                  {product.weight && (
                    <tr>
                      <th>Trọng lượng</th>
                      <td>{product.weight}g</td>
                    </tr>
                  )}
                  <tr>
                    <th>Danh mục</th>
                    <td>{product.category_name}</td>
                  </tr>
                  {product.brand_name && (
                    <tr>
                      <th>Thương hiệu</th>
                      <td>{product.brand_name}</td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
            
            {/* Features */}
            <div className="row mb-2">
              <div className="col-md-4 mb-2">
                <div className="border rounded p-2 d-flex align-items-center gap-2">
                  <span className="badge bg-success rounded-circle" style={{width:'32px',height:'32px', paddingTop: '10px'}}>1</span>
                  <span>Cam kết chính hãng 100%</span>
                </div>
              </div>
              <div className="col-md-4 mb-2">
                <div className="border rounded p-2 d-flex align-items-center gap-2">
                  <span className="badge bg-success rounded-circle" style={{width:'32px',height:'32px', paddingTop: '10px'}}>2</span>
                  <span>Bảo hành theo chính sách công ty</span>
                </div>
              </div>
              <div className="col-md-4 mb-2">
                <div className="border rounded p-2 d-flex align-items-center gap-2">
                  <span className="badge bg-success rounded-circle" style={{width:'32px',height:'32px', paddingTop: '10px'}}>3</span>
                  <span>Giao hàng nhanh toàn quốc</span>
                </div>
              </div>
            </div>
          </div>
        </div>
        
        {/* Product Description */}
        {product.description && (
          <div className="row mt-5">
            <div className="col-12">
              <div className="card">
                <div className="card-header">
                  <h5 className="mb-0 text-success">
                    <i className="bi bi-file-text me-2"></i>
                    Mô tả sản phẩm
                  </h5>
                </div>
                <div className="card-body">
                  <div className="product-description" style={{whiteSpace: 'pre-line'}}>
                    {product.description}
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
      
      <Modal
        show={modal.show}
        onClose={hideModal}
        title={modal.title}
        message={modal.message}
        type={modal.type}
        onConfirm={modal.onConfirm}
        confirmText={modal.confirmText}
        cancelText={modal.cancelText}
        showCancel={modal.showCancel}
      />
    </>
  );
};

export default ProductDetail;
