import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useCart } from "../contexts/CartContext";
import { useAuth } from "../contexts/AuthContext";
import Modal from "../components/Modal";
import { useModal } from "../hooks/useModal";
import axios from "axios";
import ScrollToTopButton from "../components/ScrollToTopButton";

const Cart = () => {
  const { cartItems, updateQuantity, removeFromCart, getCartTotal, clearCart } =
    useCart();
  const { currentUser } = useAuth();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [shippingAddress, setShippingAddress] = useState("");
  const [paymentMethod, setPaymentMethod] = useState("cod");
  const [couponCode, setCouponCode] = useState("");
  const [discountAmount, setDiscountAmount] = useState(0);
  const [shippingFee, setShippingFee] = useState(0);
  const [phone, setPhone] = useState("");
  const [couponError, setCouponError] = useState("");
  const [freeShippingThreshold, setFreeShippingThreshold] = useState(0);
  const [availableCoupons, setAvailableCoupons] = useState([]);
  const { modal, hideModal, showSuccess, showError } = useModal();

  React.useEffect(() => {
    axios.get("/api/settings").then((res) => {
      const threshold = res.data.find(
        (s) => s.key_name === "free_shipping_threshold"
      );
      setFreeShippingThreshold(threshold ? parseInt(threshold.value) : 0);
    });
  }, []);

  React.useEffect(() => {
    const total = getCartTotal() - discountAmount;
    setShippingFee(total >= freeShippingThreshold ? 0 : 30000);
  }, [getCartTotal(), discountAmount, freeShippingThreshold]);

  React.useEffect(() => {
    if (currentUser) {
      axios
        .get(`/api/coupons/available?user_id=${currentUser.id}`)
        .then((res) => setAvailableCoupons(res.data))
        .catch(() => setAvailableCoupons([]));
    }
  }, [currentUser]);

  const handleQuantityChange = (productId, size, newQuantity) => {
    if (newQuantity <= 0) {
      removeFromCart(productId, size);
    } else {
      updateQuantity(productId, size, newQuantity);
    }
  };

  const handleApplyCoupon = async () => {
    setCouponError("");
    if (!couponCode.trim()) return;
    try {
      const res = await axios.post("/api/coupons/apply", {
        code: couponCode,
        total_amount: getCartTotal(),
        user_id: currentUser?.id,
      });
      if (res.data.success) {
        setDiscountAmount(res.data.discount);
        setCouponError("");
      } else {
        setDiscountAmount(0);
        setCouponError(res.data.message || "Mã giảm giá không hợp lệ");
      }
    } catch (err) {
      setDiscountAmount(0);
      setCouponError("Mã giảm giá không hợp lệ");
    }
  };

  const handleCheckout = async () => {
    if (!currentUser) {
      navigate("/login");
      return;
    }
    if (currentUser.status !== "active") {
      showError("Tài khoản của bạn đã bị khóa hoặc chưa kích hoạt!");
      return;
    }
    if (!shippingAddress.trim()) {
      showError("Vui lòng nhập địa chỉ giao hàng!");
      return;
    }
    if (!phone.trim()) {
      showError("Vui lòng nhập số điện thoại!");
      return;
    }
    setLoading(true);
    try {
      const selectedCouponObj = availableCoupons.find(
        (c) => c.code === couponCode
      );
      const orderData = {
        items: cartItems.map((item) => ({
          product_id: item.id,
          size: item.size, // 👈 gửi size về backend
          quantity: item.quantity,
          price: item.price,
        })),
        total_amount: getCartTotal(),
        shipping_address: shippingAddress,
        shipping_fee: shippingFee,
        customer_phone: phone,
        payment_method: paymentMethod,
        coupon_code: couponCode,
        discount_amount: discountAmount,
        coupon_id: selectedCouponObj?.id || null,
        user_id: currentUser?.id,
      };
      const res = await axios.post("/api/orders", orderData);

      if (res.data && res.data.success === false) {
        showError(res.data.message || "Có lỗi xảy ra khi đặt hàng!");
        setLoading(false);
        return;
      }

      showSuccess(
        `Đặt hàng thành công!\n\nMã đơn hàng: #${
          res.data.orderId || "N/A"
        }\nTổng tiền: ${new Intl.NumberFormat("vi-VN", {
          style: "currency",
          currency: "VND",
        }).format(getCartTotal() - discountAmount + shippingFee)}`,
        "Đặt hàng thành công",
        () => {
          clearCart();
          if (paymentMethod === "bank") {
            navigate("/payment-bank-info");
          } else {
            navigate("/orders");
          }
        }
      );
    } catch (error) {
      const msg =
        error.response?.data?.message ||
        error.message ||
        "Có lỗi xảy ra khi đặt hàng!";
      showError(msg);
    } finally {
      setLoading(false);
    }
  };

  if (cartItems.length === 0) {
    return (
      <div className="container my-5 text-center">
        <i className="bi bi-cart-x display-1 text-muted mb-4"></i>
        <h2>Giỏ hàng trống</h2>
        <p className="text-muted mb-4">Bạn chưa có sản phẩm nào trong giỏ.</p>
        <Link to="/products" className="btn btn-success btn-lg">
          <i className="bi bi-bag-plus me-2"></i>Tiếp tục mua sắm
        </Link>
      </div>
    );
  }

  return (
    <>
      <div className="container my-5">
        <h1 className="mb-4">Giỏ hàng</h1>
        <div className="row">
          <div className="col-md-8">
            {cartItems.map((item) => (
              <div key={`${item.id}-${item.size}`} className="card mb-3">
                <div className="row g-0">
                  <div className="col-md-3">
                    <img
                      src={item.image_url || "https://via.placeholder.com/150x100"}
                      className="img-fluid rounded-start h-100"
                      alt={item.name}
                      style={{ objectFit: "cover" }}
                    />
                  </div>
                  <div className="col-md-9">
                    <div className="card-body">
                      <div className="row align-items-center">
                        <div className="col-md-6">
                          <h5 className="card-title">{item.name}</h5>
                          <p className="card-text text-muted">{item.category_name}</p>
                          {item.size && (
                            <p className="card-text">
                              <strong>Size:</strong> {item.size}
                            </p>
                          )}
                          <p className="card-text fw-bold text-danger">
                            {new Intl.NumberFormat("vi-VN", {
                              style: "currency",
                              currency: "VND",
                            }).format(item.price)}
                          </p>
                        </div>
                        <div className="col-md-3">
                          <div className="input-group">
                            <button
                              className="btn btn-outline-secondary"
                              onClick={() =>
                                handleQuantityChange(item.id, item.size, item.quantity - 1)
                              }
                            >
                              -
                            </button>
                            <input
                              type="number"
                              className="form-control text-center"
                              value={item.quantity}
                              onChange={(e) =>
                                handleQuantityChange(
                                  item.id,
                                  item.size,
                                  parseInt(e.target.value) || 0
                                )
                              }
                              min="0"
                            />
                            <button
                              className="btn btn-outline-secondary"
                              onClick={() =>
                                handleQuantityChange(item.id, item.size, item.quantity + 1)
                              }
                            >
                              +
                            </button>
                          </div>
                        </div>
                        <div className="col-md-3">
                          <button
                            className="btn btn-danger"
                            onClick={() => removeFromCart(item.id, item.size)}
                          >
                            Xóa
                          </button>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>

          {/* --- Sidebar tổng tiền --- */}
          <div className="col-md-4">
            {/* giữ nguyên phần tính tổng / checkout */}
          </div>
        </div>
      </div>

      <ScrollToTopButton bottom={88} right={32} zIndex={999} />

      <Modal
        show={modal.show}
        onClose={hideModal}
        title={modal.title}
        message={modal.message}
        type={modal.type}
        onConfirm={modal.onConfirm}
        confirmText={modal.confirmText}
        cancelText={modal.cancelText}
        showCancel={modal.showCancel}
      />
    </>
  );
};

export default Cart;
