import React, { useState, useEffect } from 'react';
import AdminLayout from '../../components/AdminLayout';
import axios from 'axios';

const AdminOrders = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showStatusModal, setShowStatusModal] = useState(false);
  const [selectedOrder, setSelectedOrder] = useState(null);

  useEffect(() => {
    fetchOrders();
  }, []);

  const fetchOrders = async () => {
    try {
      const response = await axios.get('/api/orders/admin');
      setOrders(response.data);
    } catch (error) {
      console.error('Error fetching orders:', error);
    } finally {
      setLoading(false);
    }
  };

  const updateOrderStatus = async (orderId, newStatus) => {
    try {
      await axios.put(`/api/orders/${orderId}/status`, { status: newStatus });
      alert('C·∫≠p nh·∫≠t tr·∫°ng th√°i th√†nh c√¥ng!');
      fetchOrders();
    } catch (error) {
      console.error('Error updating order status:', error);
      alert(error.response?.data?.message || 'C√≥ l·ªói x·∫£y ra!');
    }
  };

  const openStatusModal = (order) => {
    setSelectedOrder(order);
    setShowStatusModal(true);
  };

  const handleStatusChange = async (newStatus) => {
    if (!selectedOrder) return;
    await updateOrderStatus(selectedOrder.id, newStatus);
    setShowStatusModal(false);
    setSelectedOrder(null);
  };

  const getStatusBadgeClass = (status) => {
    switch (status) {
      case 'pending': return 'bg-warning';
      case 'confirmed': return 'bg-info';
      case 'shipped': return 'bg-primary';
      case 'delivered': return 'bg-success';
      case 'cancelled': return 'bg-danger';
      default: return 'bg-secondary';
    }
  };

  const getStatusText = (status) => {
    switch (status) {
      case 'pending': return 'Ch·ªù x√°c nh·∫≠n';
      case 'confirmed': return 'ƒê√£ x√°c nh·∫≠n';
      case 'shipped': return 'ƒêang giao';
      case 'delivered': return 'ƒê√£ giao';
      case 'cancelled': return 'ƒê√£ h·ªßy';
      default: return status;
    }
  };

  return (
    <AdminLayout>
      <h2 className="mb-4">üõí Qu·∫£n l√Ω ƒë∆°n h√†ng</h2>

      {loading ? (
        <div className="text-center py-5">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
        </div>
      ) : (
        <div className="admin-table">
          <table className="table">
            <thead>
              <tr>
                <th>ID</th>
                <th>Kh√°ch h√†ng</th>
                <th>Email</th>
                <th>S·∫£n ph·∫©m</th>
                <th>T·ªïng ti·ªÅn</th>
                <th>Tr·∫°ng th√°i</th>
                <th>Ng√†y ƒë·∫∑t</th>
                <th>M√£ gi·∫£m gi√°</th>
                <th>Ship  </th>
                <th>SƒêT</th>
                <th>Thao t√°c</th>
              </tr>
            </thead>
            <tbody>
              {orders.map(order => (
                <tr key={order.id}>
                  <td>{order.id}</td>
                  <td>{order.user_name}</td>
                  <td style={{width:'150px'}}>{order.user_email}</td>
                  <td style={{width:'150px'}}>{order.items}</td>
                  <td>
                    {new Intl.NumberFormat('vi-VN', { 
                      style: 'currency', 
                      currency: 'VND' 
                    }).format(order.total_amount)}
                  </td>
                  <td>
                    <span className={`badge ${getStatusBadgeClass(order.status)}`}>
                      {getStatusText(order.status)}
                    </span>
                  </td>
                  <td>
                    {new Date(order.created_at).toLocaleDateString('vi-VN')}
                  </td>
                  <td>{order.coupon_code || '-'}</td>
                  <td>{order.shipping_fee !== undefined ? new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(order.shipping_fee) : '-'}</td>
                  <td>{order.customer_phone || '-'}</td>
                  <td>
                    <button
                      className="btn btn-sm btn-outline-primary"
                      onClick={() => openStatusModal(order)}
                    >
                      C·∫≠p nh·∫≠t
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {showStatusModal && selectedOrder && (
        <div>
          <div
            className="modal show d-block"
            tabIndex="-1"
            style={{
              zIndex: 1060,
              position: 'fixed',
              top: 0, left: 0, right: 0, bottom: 0,
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center'
            }}
          >
            <div className="modal-dialog">
              <div className="modal-content">
                <div className="modal-header">
                  <h5 className="modal-title">C·∫≠p nh·∫≠t tr·∫°ng th√°i ƒë∆°n #{selectedOrder.id}</h5>
                  <button type="button" className="btn-close" onClick={() => setShowStatusModal(false)}></button>
                </div>
                <div className="modal-body">
                  <div className="d-grid gap-2">
                    <button
                      className="btn btn-outline-info"
                      disabled={selectedOrder.status !== 'pending'}
                      onClick={() => handleStatusChange('confirmed')}
                    >
                      X√°c nh·∫≠n
                    </button>
                    <button
                      className="btn btn-outline-primary"
                      disabled={selectedOrder.status !== 'confirmed'}
                      onClick={() => handleStatusChange('shipped')}
                    >
                      ƒêang giao
                    </button>
                    <button
                      className="btn btn-outline-success"
                      disabled={selectedOrder.status !== 'shipped'}
                      onClick={() => handleStatusChange('delivered')}
                    >
                      ƒê√£ giao
                    </button>
                    <button
                      className="btn btn-outline-danger"
                      disabled={selectedOrder.status === 'delivered' || selectedOrder.status === 'cancelled'}
                      onClick={() => handleStatusChange('cancelled')}
                    >
                      H·ªßy ƒë∆°n
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </div>
          <div
            className="modal-backdrop show"
            style={{
              zIndex: 1050,
              position: 'fixed',
              top: 0, left: 0, right: 0, bottom: 0
            }}
          ></div>
        </div>
      )}
    </AdminLayout>
  );
};

export default AdminOrders;
