import React, { useState, useEffect } from 'react';
import AdminLayout from '../../components/AdminLayout';
import axios from 'axios';

const AdminUsers = () => {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const response = await axios.get('/api/users/admin');
      setUsers(response.data);
    } catch (error) {
      console.error('Error fetching users:', error);
    } finally {
      setLoading(false);
    }
  };

  const updateUserRole = async (userId, newRole) => {
    try {
      await axios.put(`/api/users/${userId}/role`, { role: newRole });
      alert('C·∫≠p nh·∫≠t vai tr√≤ th√†nh c√¥ng!');
      fetchUsers();
    } catch (error) {
      console.error('Error updating user role:', error);
      alert('C√≥ l·ªói x·∫£y ra!');
    }
  };

  const deleteUser = async (userId) => {
    if (window.confirm('B·∫°n c√≥ ch·∫Øc mu·ªën x√≥a ng∆∞·ªùi d√πng n√†y?')) {
      try {
        await axios.delete(`/api/users/${userId}`);
        alert('X√≥a ng∆∞·ªùi d√πng th√†nh c√¥ng!');
        fetchUsers();
      } catch (error) {
        console.error('Error deleting user:', error);
        alert(error.response?.data?.message || 'C√≥ l·ªói x·∫£y ra!');
      }
    }
  };

  return (
    <AdminLayout>
      <h2 className="mb-4">üë• Qu·∫£n l√Ω ng∆∞·ªùi d√πng</h2>

      {loading ? (
        <div className="text-center py-5">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
        </div>
      ) : (
        <div className="admin-table">
          <table className="table">
            <thead>
              <tr>
                <th>ID</th>
                <th>H·ªç t√™n</th>
                <th>Email</th>
                <th>S·ªë ƒëi·ªán tho·∫°i</th>
                <th>Vai tr√≤</th>
                <th>Ng√†y ƒëƒÉng k√Ω</th>
                <th>Thao t√°c</th>
              </tr>
            </thead>
            <tbody>
              {users.map(user => (
                <tr key={user.id}>
                  <td>{user.id}</td>
                  <td>{user.name}</td>
                  <td>{user.email}</td>
                  <td>{user.phone || 'Ch∆∞a c·∫≠p nh·∫≠t'}</td>
                  <td>
                    <span className={`badge ${user.role === 'admin' ? 'bg-danger' : 'bg-primary'}`}>
                      {user.role === 'admin' ? 'Qu·∫£n tr·ªã vi√™n' : 'Ng∆∞·ªùi d√πng'}
                    </span>
                  </td>
                  <td>
                    {new Date(user.created_at).toLocaleDateString('vi-VN')}
                  </td>
                  <td>
                    <div className="dropdown">
                      <button 
                        className="btn btn-sm btn-outline-primary dropdown-toggle" 
                        type="button" 
                        data-bs-toggle="dropdown"
                      >
                        Thao t√°c
                      </button>
                      <ul className="dropdown-menu">
                        <li>
                          <button 
                            className="dropdown-item"
                            onClick={() => updateUserRole(user.id, user.role === 'admin' ? 'user' : 'admin')}
                          >
                            {user.role === 'admin' ? 'Chuy·ªÉn th√†nh User' : 'Chuy·ªÉn th√†nh Admin'}
                          </button>
                        </li>
                        <li><hr className="dropdown-divider" /></li>
                        <li>
                          <button 
                            className="dropdown-item text-danger"
                            onClick={() => deleteUser(user.id)}
                          >
                            X√≥a ng∆∞·ªùi d√πng
                          </button>
                        </li>
                      </ul>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </AdminLayout>
  );
};

export default AdminUsers;
