import React, { useEffect, useState } from "react";
impo
import { Table, Button, Modal, Form, Badge } from "react-bootstrap";
import axios from "axios";
import { showSuccess, showError } from "../../utils/notify";
import { getAuthHeaders } from "../../utils/auth";

const AdminSizes = () => {
  const [sizes, setSizes] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [editingSize, setEditingSize] = useState(null);
  const [form, setForm] = useState({ name: "", description: "", status: "active" });

  // Lấy danh sách size
  const fetchSizes = async () => {
    try {
      const res = await axios.get("/api/sizes");
      setSizes(res.data);
    } catch (err) {
      showError("Lỗi tải danh sách size");
    }
  };

  useEffect(() => {
    fetchSizes();
  }, []);

  // Mở modal thêm/sửa
  const handleShowModal = (size = null) => {
    setEditingSize(size);
    setForm(size || { name: "", description: "", status: "active" });
    setShowModal(true);
  };

  const handleCloseModal = () => setShowModal(false);

  // Submit form
  const handleSubmit = async () => {
    try {
      if (editingSize) {
        await axios.put(`/api/sizes/${editingSize.id}`, form, { headers: getAuthHeaders() });
        showSuccess("Cập nhật size thành công!");
      } else {
        await axios.post("/api/sizes", form, { headers: getAuthHeaders() });
        showSuccess("Thêm size thành công!");
      }
      fetchSizes();
      handleCloseModal();
    } catch (err) {
      showError("Lỗi khi lưu size");
    }
  };

  // Xóa size
  const handleDelete = async (id) => {
    if (!window.confirm("Bạn có chắc muốn xóa size này?")) return;
    try {
      await axios.delete(`/api/sizes/${id}`, { headers: getAuthHeaders() });
      showSuccess("Xóa size thành công!");
      fetchSizes();
    } catch (err) {
      showError("Lỗi khi xóa size");
    }
  };

  return (
    <div>
      <h3>Quản lý Size</h3>
      <Button variant="success" className="mb-3" onClick={() => handleShowModal()}>
        ➕ Thêm Size
      </Button>

      <Table striped bordered hover>
        <thead>
          <tr>
            <th>#</th>
            <th>Tên</th>
            <th>Mô tả</th>
            <th>Trạng thái</th>
            <th>Hành động</th>
          </tr>
        </thead>
        <tbody>
          {sizes.map((size, idx) => (
            <tr key={size.id}>
              <td>{idx + 1}</td>
              <td>{size.name}</td>
              <td>{size.description}</td>
              <td>
                <Badge bg={size.status === "active" ? "success" : "secondary"}>
                  {size.status === "active" ? "Kích hoạt" : "Không kích hoạt"}
                </Badge>
              </td>
              <td>
                <Button variant="primary" size="sm" onClick={() => handleShowModal(size)}>
                  ✏️ Sửa
                </Button>{" "}
                <Button variant="danger" size="sm" onClick={() => handleDelete(size.id)}>
                  🗑️ Xóa
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>

      {/* Modal thêm/sửa */}
      <Modal show={showModal} onHide={handleCloseModal}>
        <Modal.Header closeButton>
          <Modal.Title>{editingSize ? "Sửa Size" : "Thêm Size"}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group>
              <Form.Label>Tên</Form.Label>
              <Form.Control
                type="text"
                value={form.name}
                onChange={(e) => setForm({ ...form, name: e.target.value })}
              />
            </Form.Group>
            <Form.Group>
              <Form.Label>Mô tả</Form.Label>
              <Form.Control
                as="textarea"
                value={form.description}
                onChange={(e) => setForm({ ...form, description: e.target.value })}
              />
            </Form.Group>
            <Form.Group>
              <Form.Label>Trạng thái</Form.Label>
              <Form.Select
                value={form.status}
                onChange={(e) => setForm({ ...form, status: e.target.value })}
              >
                <option value="active">Kích hoạt</option>
                <option value="inactive">Không kích hoạt</option>
              </Form.Select>
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseModal}>
            Đóng
          </Button>
          <Button variant="primary" onClick={handleSubmit}>
            Lưu
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default AdminSizes;
