import React, { useState, useEffect } from 'react';
import AdminLayout from '../../components/AdminLayout';
import ImageUpload from '../../components/ImageUpload';
import Modal from '../../components/Modal';
import { useModal } from '../../hooks/useModal';
import axios from 'axios';

const AdminSlides = () => {
  const [slides, setSlides] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [editingSlide, setEditingSlide] = useState(null);
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    image: '',
    link: '',
    // button_text: 'Xem ngay',
    sort_order: 0,
    status: 'active'
  });
  const [loading, setLoading] = useState(true);
  const { modal, hideModal, showSuccess, showError, showConfirm } = useModal();

  useEffect(() => {
    fetchSlides();
  }, []);

  const fetchSlides = async () => {
    try {
      const response = await axios.get('/api/slides/admin');
      setSlides(response.data);
    } catch (error) {
      console.error('Error fetching slides:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!formData.title || !formData.image) {
      showError('Vui l√≤ng nh·∫≠p ti√™u ƒë·ªÅ v√† h√¨nh ·∫£nh!');
      return;
    }

    try {
      const dataToSubmit = {
        ...formData,
        sort_order: parseInt(formData.sort_order) || 0,
        status: formData.status || 'active'
      };

      if (editingSlide) {
        await axios.put(`/api/slides/${editingSlide.id}`, dataToSubmit);
        showSuccess('C·∫≠p nh·∫≠t slide th√†nh c√¥ng!');
      } else {
        await axios.post('/api/slides', dataToSubmit);
        showSuccess('Th√™m slide th√†nh c√¥ng!');
      }
      
      setShowModal(false);
      setEditingSlide(null);
      resetForm();
      fetchSlides();
    } catch (error) {
      console.error('Error saving slide:', error);
      showError(error.response?.data?.message || 'C√≥ l·ªói x·∫£y ra!');
    }
  };

  const handleEdit = (slide) => {
    setEditingSlide(slide);
    setFormData({
      title: slide.title,
      description: slide.description || '',
      image: slide.image,
      link: slide.link || '',
      // button_text: slide.button_text || 'Xem ngay',
      sort_order: slide.sort_order || 0,
      status: slide.status || 'active'
    });
    setShowModal(true);
  };

  const handleDelete = async (id) => {
    showConfirm(
      'B·∫°n c√≥ ch·∫Øc mu·ªën x√≥a slide n√†y?',
      async () => {
        try {
          await axios.delete(`/api/slides/${id}`);
          showSuccess('X√≥a slide th√†nh c√¥ng!');
          fetchSlides();
        } catch (error) {
          console.error('Error deleting slide:', error);
          showError(error.response?.data?.message || 'C√≥ l·ªói x·∫£y ra!');
        }
      }
    );
  };

  const handleImageUpload = (imageUrl) => {
    setFormData({ ...formData, image: imageUrl });
  };

  const resetForm = () => {
    setFormData({
      title: '',
      description: '',
      image: '',
      link: '',
      // button_text: 'Xem ngay',
      sort_order: 0,
      status: 'active'
    });
  };

  const handleModalClose = () => {
    setShowModal(false);
    setEditingSlide(null);
    resetForm();
  };

  const updateSortOrder = async (slideId, newOrder) => {
    try {
      await axios.put(`/api/slides/${slideId}/order`, { sort_order: newOrder });
      fetchSlides();
    } catch (error) {
      console.error('Error updating sort order:', error);
    }
  };

  return (
    <>
      <AdminLayout>
        <div className="d-flex justify-content-between align-items-center mb-4">
          <h2>üñºÔ∏è Qu·∫£n l√Ω Slide Banner</h2>
          <button 
            className="btn btn-admin-primary"
            onClick={() => setShowModal(true)}
          >
            ‚ûï Th√™m slide
          </button>
        </div>

        {loading ? (
          <div className="text-center py-5">
            <div className="spinner-border text-primary" role="status">
              <span className="visually-hidden">Loading...</span>
            </div>
          </div>
        ) : (
          <div className="row">
            {slides.map((slide, index) => (
              <div key={slide.id} className="col-lg-4 col-md-6 mb-4">
                <div className="card h-100 shadow-sm">
                  <div className="position-relative">
                    <img 
                      src={slide.image} 
                      alt={slide.title}
                      className="card-img-top"
                      style={{ height: '200px', objectFit: 'cover' }}
                    />
                    <div className="position-absolute top-0 start-0 m-2">
                      <span className={`badge ${slide.status === 'active' ? 'bg-success' : 'bg-secondary'}`}>
                        {slide.status === 'active' ? 'Ho·∫°t ƒë·ªông' : 'T·∫°m d·ª´ng'}
                      </span>
                    </div>
                    <div className="position-absolute top-0 end-0 m-2">
                      <span className="badge bg-primary">#{slide.sort_order}</span>
                    </div>
                  </div>
                  
                  <div className="card-body">
                    <h5 className="card-title fw-bold">{slide.title}</h5>
                    <p className="card-text text-muted small">
                      {slide.description || 'Kh√¥ng c√≥ m√¥ t·∫£'}
                    </p>
                    
                    <div className="mb-2">
                      <small className="text-muted">
                        <strong>Link:</strong> {slide.link || 'Kh√¥ng c√≥'}
                      </small>
                    </div>
                    
                    {/* <div className="mb-3">
                      <small className="text-muted">
                        <strong>Button:</strong> {slide.button_text}
                      </small>
                    </div> */}
                    
                    <div className="d-flex gap-2">
                      <button 
                        className="btn btn-sm btn-outline-primary"
                        onClick={() => handleEdit(slide)}
                      >
                        S·ª≠a
                      </button>
                      <button 
                        className="btn btn-sm btn-outline-danger"
                        onClick={() => handleDelete(slide.id)}
                      >
                        X√≥a
                      </button>
                    </div>
                    
                    <div className="mt-2">
                      <small className="text-muted">
                        Th·ª© t·ª±: 
                        <select 
                          className="form-select form-select-sm d-inline-block w-auto ms-1"
                          value={slide.sort_order}
                          onChange={(e) => updateSortOrder(slide.id, parseInt(e.target.value))}
                        >
                          {slides.map((_, i) => (
                            <option key={i} value={i}>{i + 1}</option>
                          ))}
                        </select>
                      </small>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}

        {/* Modal */}
        {showModal && (
          <div className="modal show d-block admin-modal" tabIndex="-1">
            <div className="modal-dialog modal-lg">
              <div className="modal-content">
                <div className="modal-header">
                  <h5 className="modal-title">
                    {editingSlide ? 'S·ª≠a slide' : 'Th√™m slide'}
                  </h5>
                  <button 
                    type="button" 
                    className="btn-close" 
                    onClick={handleModalClose}
                  ></button>
                </div>
                <form onSubmit={handleSubmit}>
                  <div className="modal-body">
                    <div className="mb-3">
                      <label className="form-label">Ti√™u ƒë·ªÅ:</label>
                      <input
                        type="text"
                        className="form-control"
                        value={formData.title}
                        onChange={(e) => setFormData({...formData, title: e.target.value})}
                        required
                      />
                    </div>
                    
                    <div className="mb-3">
                      <label className="form-label">M√¥ t·∫£:</label>
                      <textarea
                        className="form-control"
                        rows="3"
                        value={formData.description}
                        onChange={(e) => setFormData({...formData, description: e.target.value})}
                      />
                    </div>
                    
                    <ImageUpload 
                      onImageUpload={handleImageUpload}
                      currentImage={formData.image}
                      label="H√¨nh ·∫£nh slide"
                    />
                    
                    <div className="row">
                      <div className="col-md-6 mb-3">
                        <label className="form-label">Link:</label>
                        <input
                          type="url"
                          className="form-control"
                          value={formData.link}
                          onChange={(e) => setFormData({...formData, link: e.target.value})}
                          placeholder="https://example.com"
                        />
                      </div>
                      
                      {/* <div className="col-md-6 mb-3">
                        <label className="form-label">Text button:</label>
                        <input
                          type="text"
                          className="form-control"
                          value={formData.button_text}
                          onChange={(e) => setFormData({...formData, button_text: e.target.value})}
                          placeholder="Xem ngay"
                        />
                      </div> */}
                    </div>
                    
                    <div className="row">
                      <div className="col-md-6 mb-3">
                        <label className="form-label">Th·ª© t·ª±:</label>
                        <input
                          type="number"
                          className="form-control"
                          value={formData.sort_order}
                          onChange={(e) => setFormData({...formData, sort_order: e.target.value})}
                          min="0"
                        />
                      </div>
                      
                      <div className="col-md-6 mb-3">
                        <label className="form-label">Tr·∫°ng th√°i:</label>
                        <select
                          className="form-select"
                          value={formData.status}
                          onChange={(e) => setFormData({...formData, status: e.target.value})}
                        >
                          <option value="active">Ho·∫°t ƒë·ªông</option>
                          <option value="inactive">T·∫°m d·ª´ng</option>
                        </select>
                      </div>
                    </div>
                  </div>
                  <div className="modal-footer">
                    <button 
                      type="button" 
                      className="btn btn-secondary" 
                      onClick={handleModalClose}
                    >
                      H·ªßy
                    </button>
                    <button type="submit" className="btn btn-primary">
                      {editingSlide ? 'C·∫≠p nh·∫≠t' : 'Th√™m'}
                    </button>
                  </div>
                </form>
              </div>
            </div>
          </div>
        )}
        {showModal && <div className="modal-backdrop show"></div>}
      </AdminLayout>
      
      <Modal
        show={modal.show}
        onClose={hideModal}
        title={modal.title}
        message={modal.message}
        type={modal.type}
        onConfirm={modal.onConfirm}
        confirmText={modal.confirmText}
        cancelText={modal.cancelText}
        showCancel={modal.showCancel}
      />
    </>
  );
};

export default AdminSlides;
