import React, { useState, useEffect } from 'react';
import AdminLayout from '../../components/AdminLayout';
import ImageUpload from '../../components/ImageUpload';
import Modal from '../../components/Modal';
import { useModal } from '../../hooks/useModal';
import axios from 'axios';

const AdminCategories = () => {
  const [categories, setCategories] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [editingCategory, setEditingCategory] = useState(null);
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    slug: '',
    image: '',
    status: 'active'
  });
  const [loading, setLoading] = useState(true);
  const { modal, hideModal, showSuccess, showError, showConfirm } = useModal();

  useEffect(() => {
    // Äáº£m báº£o axios cÃ³ Authorization header khi vÃ o trang admin
    const token = localStorage.getItem('token');
    if (token) {
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
    }
    fetchCategories();
  }, []);

  const fetchCategories = async () => {
    try {
      const response = await axios.get('/api/categories');
      setCategories(response.data);
    } catch (error) {
      console.error('Error fetching categories:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const dataToSend = {
        ...formData,
        slug: formData.slug || formData.name.toLowerCase().replace(/\s+/g, '-'),
        status: formData.status || 'active'
      };

      if (editingCategory) {
        await axios.put(`/api/categories/${editingCategory.id}`, dataToSend);
        showSuccess('Cáº­p nháº­t danh má»¥c thÃ nh cÃ´ng!');
      } else {
        await axios.post('/api/categories', dataToSend);
        showSuccess('ThÃªm danh má»¥c thÃ nh cÃ´ng!');
      }

      setShowModal(false);
      setEditingCategory(null);
      resetForm();
      fetchCategories();
    } catch (error) {
      console.error('Error saving category:', error);
      showError(error.response?.data?.message || 'CÃ³ lá»—i xáº£y ra!');
    }
  };

  const handleEdit = (category) => {
    setEditingCategory(category);
    setFormData({
      name: category.name,
      description: category.description || '',
      slug: category.slug || '',
      image: category.image || '',
      status: category.status || 'active'
    });
    setShowModal(true);
  };

  const handleDelete = async (id) => {
    showConfirm(
      'Báº¡n cÃ³ cháº¯c muá»‘n xÃ³a danh má»¥c nÃ y?',
      async () => {
        try {
          await axios.delete(`/api/categories/${id}`);
          showSuccess('XÃ³a danh má»¥c thÃ nh cÃ´ng!');
          fetchCategories();
        } catch (error) {
          console.error('Error deleting category:', error);
          showError('CÃ³ lá»—i xáº£y ra!');
        }
      }
    );
  };

  const resetForm = () => {
    setFormData({
      name: '',
      description: '',
      slug: '',
      image: '',
      status: 'active'
    });
  };

  const handleModalClose = () => {
    setShowModal(false);
    setEditingCategory(null);
    resetForm();
  };

  const handleImageUpload = (imageUrl) => {
    setFormData({ ...formData, image: imageUrl });
  };

  return (
    <>
      <AdminLayout>
        <div className="d-flex justify-content-between align-items-center mb-4">
          <h2>ðŸ“‚ Quáº£n lÃ½ danh má»¥c</h2>
          <button 
            className="btn btn-admin-primary"
            onClick={() => setShowModal(true)}
          >
            âž• ThÃªm danh má»¥c
          </button>
        </div>

        {loading ? (
          <div className="text-center py-5">
            <div className="spinner-border text-primary" role="status">
              <span className="visually-hidden">Loading...</span>
            </div>
          </div>
        ) : (
          <div className="admin-table">
            <table className="table">
              <thead>
                <tr>
                  <th>ID</th>
                  <th>TÃªn DM</th>
