import React, { useEffect, useState } from 'react';
import AdminLayout from '../../components/AdminLayout';
import Modal from '../../components/Modal';
import { useModal } from '../../hooks/useModal';
import axios from 'axios';

const formatDate = (dateStr) => {
  if (!dateStr) return '';
  const d = new Date(dateStr);
  return d.toLocaleDateString('vi-VN');
};

const initialForm = { title: '', summary: '', image: '', date: '' };

const AdminNews = () => {
  const [news, setNews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [editingNews, setEditingNews] = useState(null);
  const [form, setForm] = useState(initialForm);
  const [message, setMessage] = useState('');
  const { modal, hideModal, showSuccess, showError, showConfirm } = useModal();

  useEffect(() => {
    fetchNews();
  }, []);

  const fetchNews = async () => {
    setLoading(true);
    try {
      const res = await axios.get('/api/news');
      console.log('API /api/news trả về:', res.data); 
      setNews(res.data);
    } catch (err) {
      setNews([]);
      console.error('Lỗi lấy tin tức:', err); 
    } finally {
      setLoading(false);
    }
  };

  const handleAddClick = () => {
    setForm(initialForm);
    setEditingNews(null);
    setShowModal(true);
    setMessage('');
  };

  const handleEdit = (item) => {
    setForm({
      title: item.title,
      summary: item.summary,
      image: item.image || '',
      date: item.date
    });
    setEditingNews(item);
    setShowModal(true);
    setMessage('');
  };

  const handleDelete = (id) => {
    showConfirm(
      'Bạn có chắc muốn xóa tin tức này?',
      async () => {
        try {
          await axios.delete(`/api/news/${id}`);
          showSuccess('Đã xóa tin tức.');
          fetchNews();
        } catch {
          showError('Có lỗi khi xóa.');
        }
      }
    );
  };

  const handleModalClose = () => {
    setShowModal(false);
    setEditingNews(null);
    setForm(initialForm);
    setMessage('');
  };

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    if (!form.title || !form.summary || !form.date) {
      setMessage('Vui lòng nhập đầy đủ thông tin.');
      return;
    }
    try {
      if (editingNews) {
        await axios.put(`/api/news/${editingNews.id}`, form);
        showSuccess('Cập nhật tin tức thành công!');
      } else {
        await axios.post('/api/news', form);
        showSuccess('Thêm tin tức thành công!');
      }
      setShowModal(false);
      setEditingNews(null);
      setForm(initialForm);
      fetchNews();
    } catch {
      setMessage('Có lỗi xảy ra, vui lòng thử lại.');
    }
  };

  return (
    <>
      <AdminLayout>
        <div className="d-flex justify-content-between align-items-center mb-4">
          <h2>
            <i className="bi bi-newspaper me-2"></i>
            Quản lý tin tức
          </h2>
          <button
            className="btn btn-admin-primary"
            onClick={handleAddClick}
          >
            ➕ Thêm tin tức
          </button>
        </div>

        {loading ? (
          <div className="text-center py-5">
            <div className="spinner-border text-primary" role="status">
              <span className="visually-hidden">Loading...</span>
            </div>
          </div>
        ) : (
          <div className="admin-table">
            <table className="table">
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Tiêu đề</th>
                  <th>Tóm tắt</th>
                  <th>Ảnh</th>
                  <th>Ngày đăng</th>
                  <th>Thao tác</th>
                </tr>
              </thead>
              <tbody>
                {news.length === 0 ? (
                  <tr>
                    <td colSpan={6} className="text-center text-muted py-4">
                      Chưa có tin tức nào.
                    </td>
                  </tr>
                ) : (
                  news.map(item => (
                    <tr key={item.id}>
                      <td>{item.id}</td>
                      <td className="fw-bold">{item.title}</td>
                      <td style={{width: '200px'}}>{item.summary}</td>
                      <td>
                        {item.image && (
                          <img
                            src={item.image}
                            alt=""
                            style={{
                              maxWidth: 60,
                              maxHeight: 40,
                              objectFit: 'cover',
                              borderRadius: 4
                            }}
                          />
                        )}
                      </td>
                      <td>{formatDate(item.date)}</td>
                      <td>
                        <button
                          className="btn btn-sm btn-outline-primary me-2"
                          onClick={() => handleEdit(item)}
                        >
                          Sửa
                        </button>
                        <button
                          className="btn btn-sm btn-outline-danger"
                          onClick={() => handleDelete(item.id)}
                        >
                          Xóa
                        </button>
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>
        )}

        {/* Modal thêm/sửa tin tức */}
        {showModal && (
          <div className="modal show d-block admin-modal" tabIndex="-1">
            <div className="modal-dialog">
              <div className="modal-content">
                <div className="modal-header">
                  <h5 className="modal-title">
                    {editingNews ? 'Sửa tin tức' : 'Thêm tin tức'}
                  </h5>
                  <button
                    type="button"
                    className="btn-close"
                    onClick={handleModalClose}
                  ></button>
                </div>
                <form onSubmit={handleSubmit}>
                  <div className="modal-body">
                    <div className="mb-3">
                      <label className="form-label">Tiêu đề:</label>
                      <input
                        type="text"
                        className="form-control"
                        name="title"
                        value={form.title}
                        onChange={handleChange}
                        required
                      />
                    </div>
                    <div className="mb-3">
                      <label className="form-label">Ngày đăng:</label>
                      <input
                        type="date"
                        className="form-control"
                        name="date"
                        value={form.date}
                        onChange={handleChange}
                        required
                      />
                    </div>
                    <div className="mb-3 " >
                      <label className="form-label">Tóm tắt:</label>
                      <textarea
                        className="form-control"
                        name="summary"
                        value={form.summary}
                        onChange={handleChange}
                        rows={2}
                        required
                      />
                    </div>
                    <div className="mb-3">
                      <label className="form-label">Ảnh (URL):</label>
                      <input
                        className="form-control"
                        name="image"
                        value={form.image}
                        onChange={handleChange}
                      />
                    </div>
                    {message && (
                      <div className="alert alert-info py-2 mb-0">{message}</div>
                    )}
                  </div>
                  <div className="modal-footer">
                    <button
                      type="button"
                      className="btn btn-secondary"
                      onClick={handleModalClose}
                    >
                      Hủy
                    </button>
                    <button type="submit" className="btn btn-primary">
                      {editingNews ? 'Cập nhật' : 'Thêm'}
                    </button>
                  </div>
                </form>
              </div>
            </div>
          </div>
        )}

        <Modal
          show={modal.show}
          onClose={hideModal}
          title={modal.title}
          message={modal.message}
          type={modal.type}
          onConfirm={modal.onConfirm}
          confirmText={modal.confirmText}
          cancelText={modal.cancelText}
          showCancel={modal.showCancel}
        />
      </AdminLayout>
    </>
  );
};

export default AdminNews;
