import React, { useState, useEffect } from 'react';
import AdminLayout from '../../components/AdminLayout';
import axios from 'axios';

const AdminUsers = () => {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [editingUser, setEditingUser] = useState(null);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    role: 'user',
    password: ''
  });
  const [showActionModal, setShowActionModal] = useState(false);
  const [selectedUser, setSelectedUser] = useState(null);
  const [currentUser, setCurrentUser] = useState(null);

  useEffect(() => {
    fetchUsers();
    // Gi·∫£ l·∫≠p th√¥ng tin ng∆∞·ªùi d√πng hi·ªán t·∫°i (current user)
    setCurrentUser({
      id: 1,
      name: 'Admin',
      email: 'admin@example.com',
      role: 'admin',
      status: 'active'
    });
  }, []);

  const fetchUsers = async () => {
    try {
      const response = await axios.get('/api/users/admin');
      setUsers(response.data);
    } catch (error) {
      console.error('Error fetching users:', error);
    } finally {
      setLoading(false);
    }
  };

  const updateUserRole = async (userId, newRole) => {
    try {
      await axios.put(`/api/users/${userId}/role`, { role: newRole });
      alert('C·∫≠p nh·∫≠t vai tr√≤ th√†nh c√¥ng!');
      fetchUsers();
    } catch (error) {
      console.error('Error updating user role:', error);
      alert(error.response?.data?.message || 'C√≥ l·ªói x·∫£y ra!');
    }
  };

  const updateUserStatus = async (userId, newStatus) => {
    if (userId === currentUser?.id) {
      alert('B·∫°n kh√¥ng th·ªÉ thay ƒë·ªïi tr·∫°ng th√°i t√†i kho·∫£n c·ªßa ch√≠nh m√¨nh!');
      return;
    }

    const statusText = {
      'active': 'k√≠ch ho·∫°t',
      'inactive': 't·∫°m d·ª´ng', 
      'blocked': 'kh√≥a'
    };

    if (window.confirm(`B·∫°n c√≥ ch·∫Øc mu·ªën ${statusText[newStatus]} t√†i kho·∫£n n√†y?`)) {
      try {
        await axios.put(`/api/users/${userId}/status`, { status: newStatus });
        alert('C·∫≠p nh·∫≠t tr·∫°ng th√°i t√†i kho·∫£n th√†nh c√¥ng!');
        fetchUsers();
      } catch (error) {
        console.error('Error updating user status:', error);
        alert(error.response?.data?.message || 'C√≥ l·ªói x·∫£y ra!');
      }
    }
  };

  const deleteUser = async (userId) => {
    if (userId === currentUser?.id) {
      alert('B·∫°n kh√¥ng th·ªÉ x√≥a t√†i kho·∫£n c·ªßa ch√≠nh m√¨nh!');
      return;
    }

    if (window.confirm('B·∫°n c√≥ ch·∫Øc mu·ªën x√≥a ng∆∞·ªùi d√πng n√†y? H√†nh ƒë·ªông n√†y kh√¥ng th·ªÉ ho√†n t√°c!')) {
      try {
        await axios.delete(`/api/users/${userId}`);
        alert('X√≥a ng∆∞·ªùi d√πng th√†nh c√¥ng!');
        fetchUsers();
      } catch (error) {
        console.error('Error deleting user:', error);
        alert(error.response?.data?.message || 'C√≥ l·ªói x·∫£y ra!');
      }
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.name || !formData.email || !formData.role) {
      alert('Vui l√≤ng nh·∫≠p ƒë·∫ßy ƒë·ªß c√°c tr∆∞·ªùng b·∫Øt bu·ªôc!');
      return;
    }

    if (!editingUser && !formData.password) {
      alert('Vui l√≤ng nh·∫≠p m·∫≠t kh·∫©u cho ng∆∞·ªùi d√πng m·ªõi!');
      return;
    }

    try {
      const dataToSubmit = {
        ...formData,
        password: editingUser ? undefined : formData.password
      };

      if (editingUser) {
        await axios.put(`/api/users/${editingUser.id}`, dataToSubmit);
        alert('C·∫≠p nh·∫≠t ng∆∞·ªùi d√πng th√†nh c√¥ng!');
      } else {
        await axios.post('/api/users', dataToSubmit);
        alert('Th√™m ng∆∞·ªùi d√πng th√†nh c√¥ng!');
      }

      setShowModal(false);
      setEditingUser(null);
      resetForm();
      fetchUsers();
    } catch (error) {
      console.error('Error saving user:', error);
      alert(error.response?.data?.message || 'C√≥ l·ªói x·∫£y ra!');
    }
  };

  const handleEdit = (user) => {
    setEditingUser(user);
    setFormData({
      name: user.name,
      email: user.email,
      phone: user.phone || '',
      role: user.role,
      password: ''
    });
    setShowModal(true);
  };

  const resetForm = () => {
    setFormData({
      name: '',
      email: '',
      phone: '',
      role: 'user',
      password: ''
    });
  };

  const handleModalClose = () => {
    setShowModal(false);
    setEditingUser(null);
    resetForm();
  };

  const openActionModal = (user) => {
    setSelectedUser(user);
    setShowActionModal(true);
  };

  const closeActionModal = () => {
    setShowActionModal(false);
    setSelectedUser(null);
  };

  const getStatusBadge = (status) => {
    switch(status) {
      case 'active':
        return <span className="badge bg-success">Ho·∫°t ƒë·ªông</span>;
      case 'inactive':
        return <span className="badge bg-secondary">Ch∆∞a k√≠ch ho·∫°t</span>;
      case 'blocked':
        return <span className="badge bg-danger">B·ªã kh√≥a</span>;
      default:
        return <span className="badge bg-secondary">Kh√¥ng x√°c ƒë·ªãnh</span>;
    }
  };

  return (
    <AdminLayout>
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>üë• Qu·∫£n l√Ω ng∆∞·ªùi d√πng</h2>
        <button 
          className="btn btn-admin-primary"
          onClick={() => setShowModal(true)}
        >
          ‚ûï Th√™m ng∆∞·ªùi d√πng
        </button>
      </div>

      {loading ? (
        <div className="text-center py-5">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
        </div>
      ) : (
        <div className="admin-table">
          <table className="table">
            <thead>
              <tr>
                <th>ID</th>
                <th>H·ªç t√™n</th>
                <th>Email</th>
                <th>S·ªë ƒëi·ªán tho·∫°i</th>
                <th>Vai tr√≤</th>
                <th>Tr·∫°ng th√°i</th>
                <th>Ng√†y ƒëƒÉng k√Ω</th>
                <th>Thao t√°c</th>
              </tr>
            </thead>
            <tbody>
              {users.map(user => (
                <tr key={user.id}>
                  <td>{user.id}</td>
                  <td>
                    <div className="d-flex align-items-center">
                      <img 
                        src={user.avatar || 'https://via.placeholder.com/32x32?text=U'} 
                        alt={user.name}
                        className="rounded-circle me-2"
                        style={{ width: '32px', height: '32px', objectFit: 'cover' }}
                        onError={(e) => {
                          e.target.src = 'https://via.placeholder.com/32x32?text=U';
                        }}
                      />
                      {user.name}
                    </div>
                  </td>
                  <td>{user.email}</td>
                  <td>{user.phone || 'Ch∆∞a c·∫≠p nh·∫≠t'}</td>
                  <td>
                    <span className={`badge ${user.role === 'admin' ? 'bg-danger' : 'bg-primary'}`}>
                      {user.role === 'admin' ? 'Qu·∫£n tr·ªã vi√™n' : 'Ng∆∞·ªùi d√πng'}
                    </span>
                  </td>
                  <td>{getStatusBadge(user.status)}</td>
                  <td>
                    {new Date(user.created_at).toLocaleDateString('vi-VN')}
                  </td>
                  <td>
                    <button
                      className="btn btn-sm btn-outline-primary"
                      onClick={() => openActionModal(user)}
                    >
                      Thao t√°c
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {/* Modal */}
      {showModal && (
        <div className="modal show d-block admin-modal" tabIndex="-1">
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">
                  {editingUser ? 'S·ª≠a ng∆∞·ªùi d√πng' : 'Th√™m ng∆∞·ªùi d√πng'}
                </h5>
                <button 
                  type="button" 
                  className="btn-close" 
                  onClick={handleModalClose}
                ></button>
              </div>
              <form onSubmit={handleSubmit}>
                <div className="modal-body">
                  <div className="mb-3">
                    <label className="form-label">H·ªç t√™n:</label>
                    <input
                      type="text"
                      className="form-control"
                      value={formData.name}
                      onChange={(e) => setFormData({...formData, name: e.target.value})}
                      required
                    />
                  </div>

                  <div className="mb-3">
                    <label className="form-label">Email:</label>
                    <input
                      type="email"
                      className="form-control"
                      value={formData.email}
                      onChange={(e) => setFormData({...formData, email: e.target.value})}
                      required
                    />
                  </div>

                  <div className="mb-3">
                    <label className="form-label">S·ªë ƒëi·ªán tho·∫°i:</label>
                    <input
                      type="text"
                      className="form-control"
                      value={formData.phone}
                      onChange={(e) => setFormData({...formData, phone: e.target.value})}
                    />
                  </div>

                  <div className="mb-3">
                    <label className="form-label">Vai tr√≤:</label>
                    <select
                      className="form-select"
                      value={formData.role}
                      onChange={(e) => setFormData({...formData, role: e.target.value})}
                      required
                    >
                      <option value="user">Ng∆∞·ªùi d√πng</option>
                      <option value="admin">Qu·∫£n tr·ªã vi√™n</option>
                    </select>
                  </div>

                  {!editingUser && (
                    <div className="mb-3">
                      <label className="form-label">M·∫≠t kh·∫©u:</label>
                      <input
                        type="password"
                        className="form-control"
                        value={formData.password}
                        onChange={(e) => setFormData({...formData, password: e.target.value})}
                        required={!editingUser}
                      />
                    </div>
                  )}
                </div>
                <div className="modal-footer">
                  <button 
                    type="button" 
                    className="btn btn-secondary" 
                    onClick={handleModalClose}
                  >
                    H·ªßy
                  </button>
                  <button type="submit" className="btn btn-primary">
                    {editingUser ? 'C·∫≠p nh·∫≠t' : 'Th√™m'}
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}
      {showModal && <div className="modal-backdrop show"></div>}

      {/* Action Modal */}
      {showActionModal && selectedUser && (
        <div>
          <div className="modal show d-block" tabIndex="-1"
            style={{
              zIndex: 1060,
              position: 'fixed',
              top: 0, left: 0, right: 0, bottom: 0,
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center'
            }}
          >
            <div className="modal-dialog">
              <div className="modal-content">
                <div className="modal-header">
                  <h5 className="modal-title">
                    Thao t√°c v·ªõi ng∆∞·ªùi d√πng: <strong>{selectedUser.name}</strong>
                  </h5>
                  <button type="button" className="btn-close" onClick={closeActionModal}></button>
                </div>
                <div className="modal-body">
                  <div className="mb-3 p-3 bg-light rounded">
                    <div className="row">
                      <div className="col-6">
                        <strong>Vai tr√≤ hi·ªán t·∫°i:</strong> {getStatusBadge(selectedUser.role === 'admin' ? 'admin' : 'user')}
                      </div>
                      <div className="col-6">
                        <strong>Tr·∫°ng th√°i hi·ªán t·∫°i:</strong> {getStatusBadge(selectedUser.status)}
                      </div>
                    </div>
                  </div>

                  <div className="d-grid gap-2">
                    <button
                      className="btn btn-outline-primary"
                      onClick={() => { closeActionModal(); handleEdit(selectedUser); }}
                    >
                      <i className="bi bi-pencil me-2"></i>
                      S·ª≠a th√¥ng tin
                    </button>

                    <button
                      className="btn btn-outline-warning"
                      onClick={() => {
                        closeActionModal();
                        updateUserRole(selectedUser.id, selectedUser.role === 'admin' ? 'user' : 'admin');
                      }}
                      disabled={selectedUser.id === currentUser?.id}
                    >
                      <i className="bi bi-arrow-repeat me-2"></i>
                      {selectedUser.role === 'admin' ? 'Chuy·ªÉn th√†nh User' : 'Chuy·ªÉn th√†nh Admin'}
                    </button>

                    {/* Status Actions */}
                    {selectedUser.status === 'active' ? (
                      <>
                        <button
                          className="btn btn-outline-secondary"
