import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const PAGE_SIZE = 16;

const Products = () => {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(true);
  const [page, setPage] = useState(1);

  useEffect(() => {
    fetchProducts();
    fetchCategories();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await axios.get('/api/products');
      setProducts(response.data);
    } catch (error) {
      console.error('Error fetching products:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchCategories = async () => {
    try {
      const response = await axios.get('/api/categories');
      setCategories(response.data);
    } catch (error) {
      console.error('Error fetching categories:', error);
    }
  };

  const filteredProducts = products.filter(product => {
    const matchesCategory = !selectedCategory || product.category_id == selectedCategory;
    const matchesSearch = product.name.toLowerCase().includes(searchTerm.toLowerCase());
    return matchesCategory && matchesSearch;
  });

  const sortedProducts = [...filteredProducts].sort((a, b) =>
    a.category_name.localeCompare(b.category_name)
  );

  const totalPages = Math.ceil(sortedProducts.length / PAGE_SIZE);
  const pagedProducts = sortedProducts.slice((page - 1) * PAGE_SIZE, page * PAGE_SIZE);

  const groupedPagedProducts = pagedProducts.reduce((acc, product) => {
    const category = product.category_name || 'KhÃ¡c';
    if (!acc[category]) acc[category] = [];
    acc[category].push(product);
    return acc;
  }, {});

  const handlePageChange = (newPage) => {
    if (newPage >= 1 && newPage <= totalPages) setPage(newPage);
  };

  return (
    <div className="container my-5">
      <div className="text-center mb-5">
        <h1 className="display-5 fw-bold text-primary">Sáº£n pháº©m</h1>
        <p className="lead text-muted">KhÃ¡m phÃ¡ bá» sÆ°u táº­p thiáº¿t bá» cáº§u lÃ´ng cháº¥t lÆ°á»£ng cao</p>
      </div>

      {/* Bá» lá»c */}
      <div className="row mb-4">
        <div className="col-lg-8 col-md-6 mb-3">
          <div className="input-group">
            <span className="input-group-text">
              <i className="bi bi-search"></i>
            </span>
            <input
              type="text"
              className="form-control"
              placeholder="TÃ¬m kiáº¿m sáº£n pháº©m theo tÃªn..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>
        </div>
        <div className="col-lg-4 col-md-6 mb-3">
          <div className="input-group">
            <span className="input-group-text">
              <i className="bi bi-funnel"></i>
            </span>
            <select
              className="form-select"
              value={selectedCategory}
              onChange={(e) => { setSelectedCategory(e.target.value); setPage(1); }}
            >
              <option value="">Táº¥t cáº£ danh má»¥c</option>
              {categories.map(category => (
                <option key={category.id} value={category.id}>
                  {category.name}
                </option>
              ))}
            </select>
          </div>
        </div>
      </div>

      {/* Hiá»n thá» sáº£n pháº©m */}
      {loading ? (
        <div className="text-center py-5">
          <div className="spinner-border spinner-border-lg" role="status">
            <span className="visually-hidden">Äang táº£i...</span>
          </div>
          <p className="mt-3 text-muted">Äang táº£i sáº£n pháº©m...</p>
        </div>
      ) : (
        <>
          {filteredProducts.length === 0 ? (
            <div className="text-center py-5">
              <div className="mb-4">
                <i className="bi bi-search display-1 text-muted"></i>
              </div>
              <h4 className="text-muted">KhÃ´ng tÃ¬m tháº¥y sáº£n pháº©m nÃ o</h4>
              <p className="text-muted">HÃ£y thá»­ thay Äá»i tá»« khÃ³a tÃ¬m kiáº¿m hoáº·c bá» lá»c</p>
            </div>
          ) : (
            <>
              <div className="d-flex justify-content-between align-items-center mb-4">
                <p className="text-muted mb-0">
                  Hiá»n thá» {filteredProducts.length} sáº£n pháº©m
                </p>
              </div>

              {Object.entries(groupedPagedProducts).map(([category, items]) => (
                <div key={category} className="mb-5">
                  <h4 className="fw-bold text-primary mb-3">{category}</h4>
                  <div className="row g-3">
                    {items.map((product, index) => (
                      <div key={product.id} className="col-xl-3 col-lg-4 col-md-6">
                        <div
                          className="product-card h-100 fade-in-up"
                          style={{
                            animationDelay: `${index * 0.05}s`,
                            borderRadius: '10px',
                            boxShadow: '0 4px 16px rgba(0,0,0,0.06)',
                            overflow: 'hidden',
                            backgroundColor: '#fff',
                            transition: 'transform 0.2s ease',
                          }}
                          onMouseEnter={(e) => e.currentTarget.style.transform = 'translateY(-4px)'}
                          onMouseLeave={(e) => e.currentTarget.style.transform = 'translateY(0)'}
                        >
                          <div className="position-relative overflow-hidden mb-2">
                            <img
                              src={product.image_url || 'https://via.placeholder.com/300x200/f8f9fa/6c757d?text=No+Image'}
                              className="card-img-top"
                              alt={product.name}
                              style={{
                                height: '320px',
                                objectFit: 'cover',
                                width: '100%',
                              }}
                            />
                            {product.stock_quantity === 0 && (
                              <div className="position-absolute top-0 start-0 m-2">
                                <span className="badge bg-danger" style={{ fontWeight: 500, fontSize: '0.8rem' }}>Háº¿t hÃ ng</span>
                              </div>
                            )}
                            {product.sale_price && (
                              <div className="position-absolute top-0 end-0 m-2">
                                <span className="badge bg-warning text-dark">
                                  SALE {Math.round(((product.price - product.sale_price) / product.price) * 100)}%
                                </span>
                              </div>
                            )}
                          </div>
                          <div className="card-body d-flex flex-column">
                            <h5 className="card-title text-dark fw-semibold" style={{ fontSize: '1rem', minHeight: '3em', paddingLeft: '10px' }}>
                              {product.name}
                            </h5>
                            <div className="mb-1 text-muted small" style={{ paddingLeft: '10px' }}>{product.category_name}</div>
                            <div className="mb-2" style={{ paddingLeft: '10px' }}>
                              {product.sale_price ? (
                                <>
                                  <span className="fw-bold text-danger fs-5">
                                    {new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(product.sale_price)}
                                  </span>
                                  <br />
                                  <small className="text-decoration-line-through text-muted">
                                    {new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(product.price)}
                                  </small>
                                </>
                              ) : (
                                <span className="fw-bold text-danger fs-5">
                                  {new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(product.price)}
                                </span>
                              )}
                            </div>
                            <div className="mt-auto">
                              <Link
                                to={`/products/${product.id}`}
                                className={`btn ${product.stock_quantity === 0 ? 'btn-outline-secondary' : 'btn-dark'} w-100`}
                                style={{ fontWeight: 600, fontSize: '0.95rem', padding: '10px' }}
                              >
                                {product.stock_quantity === 0 ? 'Xem chi tiáº¿t' : 'Xem sáº£n pháº©m'}
                              </Link>
                            </div>
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              ))}

              {/* PhÃ¢n trang */}
              {totalPages > 1 && (
                <div className="d-flex justify-content-center mt-4">
                  <nav>
                    <ul className="pagination">
                      <li className={`page-item${page === 1 ? ' disabled' : ''}`}>
                        <button className="page-link" onClick={() => handlePageChange(page - 1)}>&lt;</button>
                      </li>
                      {[...Array(totalPages)].map((_, i) => (
                        <li key={i + 1} className={`page-item${page === i + 1 ? ' active' : ''}`}>
                          <button className="page-link" onClick={() => handlePageChange(i + 1)}>{i + 1}</button>
                        </li>
                      ))}
                      <li className={`page-item${page === totalPages ? ' disabled' : ''}`}>
                        <button className="page-link" onClick={() => handlePageChange(page + 1)}>&gt;</button>
                      </li>
                    </ul>
                  </nav>
                </div>
              )}
            </>
          )}
        </>
      )}
    </div>
  );
};

export default Products;
