import React from 'react';

const NotificationModal = ({ show, onClose, type = 'success', title, message, onConfirm }) => {
  if (!show) return null;

  const getIcon = () => {
    switch(type) {
      case 'success':
        return <i className="bi bi-check-circle-fill text-success" style={{fontSize: '3rem'}}></i>;
      case 'error':
        return <i className="bi bi-x-circle-fill text-danger" style={{fontSize: '3rem'}}></i>;
      case 'warning':
        return <i className="bi bi-exclamation-triangle-fill text-warning" style={{fontSize: '3rem'}}></i>;
      case 'info':
        return <i className="bi bi-info-circle-fill text-info" style={{fontSize: '3rem'}}></i>;
      case 'confirm':
        return <i className="bi bi-question-circle-fill text-primary" style={{fontSize: '3rem'}}></i>;
      default:
        return <i className="bi bi-info-circle-fill text-info" style={{fontSize: '3rem'}}></i>;
    }
  };

  const getTitle = () => {
    if (title) return title;
    
    switch(type) {
      case 'success':
        return 'Thành công!';
      case 'error':
        return 'Lỗi!';
      case 'warning':
        return 'Cảnh báo!';
      case 'info':
        return 'Thông báo';
      case 'confirm':
        return 'Xác nhận';
      default:
        return 'Thông báo';
    }
  };

  return (
    <div className="modal show d-block" tabIndex="-1" style={{zIndex: 9999}}>
      <div className="modal-dialog modal-dialog-centered">
        <div className="modal-content border-0 shadow-lg">
          <div className="modal-body text-center p-4">
            <div className="mb-3">
              {getIcon()}
            </div>
            <h5 className="modal-title mb-3 fw-bold">
              {getTitle()}
            </h5>
            <p className="text-muted mb-4" style={{fontSize: '1.1rem'}}>
              {message}
            </p>
            <div className="d-flex gap-2 justify-content-center">
              {type === 'confirm' ? (
                <>
                  <button 
                    type="button" 
                    className="btn btn-outline-secondary px-4"
                    onClick={onClose}
                  >
                    Hủy
                  </button>
                  <button 
                    type="button" 
                    className="btn btn-primary px-4"
                    onClick={onConfirm}
                  >
                    Xác nhận
                  </button>
                </>
              ) : (
                <button 
                  type="button" 
                  className="btn btn-primary px-4"
                  onClick={onClose}
                >
                  OK
                </button>
              )}
            </div>
          </div>
        </div>
      </div>
      <div className="modal-backdrop show"></div>
    </div>
  );
};

export default NotificationModal;
