import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import AdminLayout from '../../components/AdminLayout';
import axios from 'axios';

const AdminNews = () => {
  const [news, setNews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [form, setForm] = useState({ title: '', summary: '', image: '', date: '' });
  const [editingId, setEditingId] = useState(null);

  useEffect(() => {
    fetchNews();
  }, []);

  const fetchNews = async () => {
    setLoading(true);
    try {
      const res = await axios.get('/api/news');
      setNews(res.data);
    } catch {
      setNews([]);
    } finally {
      setLoading(false);
    }
  };

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    if (!form.title || !form.summary || !form.date) return;
    try {
      if (editingId) {
        await axios.put(`/api/news/${editingId}`, form);
      } else {
        await axios.post('/api/news', form);
      }
      setForm({ title: '', summary: '', image: '', date: '' });
      setEditingId(null);
      fetchNews();
    } catch {}
  };

  const handleEdit = item => {
    setForm({
      title: item.title,
      summary: item.summary,
      image: item.image || '',
      date: item.date
    });
    setEditingId(item.id);
  };

  const handleDelete = async id => {
    if (!window.confirm('X√≥a tin t·ª©c n√†y?')) return;
    try {
      await axios.delete(`/api/news/${id}`);
      fetchNews();
    } catch {}
  };

  return (
    <>
      <AdminLayout>
        <h2 className="mb-4">üõí Qu·∫£n l√Ω ƒë∆°n h√†ng</h2>

        {loading ? (
          <div className="text-center py-5">
            <div className="spinner-border text-primary" role="status">
              <span className="visually-hidden">Loading...</span>
            </div>
          </div>
        ) : (
          <div className="admin-table">
            <table className="table">
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Kh√°ch h√†ng</th>
                  <th>Email</th>
                  <th>S·∫£n ph·∫©m</th>
                  <th>T·ªïng ti·ªÅn</th>
                  <th>Tr·∫°ng th√°i</th>
                  <th>Ng√†y ƒë·∫∑t</th>
                  <th>M√£ sale</th>
                  <th>Shipping</th>
                  <th>SƒêT</th>
                  <th>Thao t√°c</th>
                </tr>
              </thead>
              <tbody>
                {orders.map(order => (
                  <tr key={order.id}>
                    <td>{order.id}</td>
                    <td>{order.user_name}</td>
                    <td>{order.user_email}</td>
                    <td style={{width:'170px'}}>{order.items}</td>
                    <td>
                      {new Intl.NumberFormat('vi-VN', { 
                        style: 'currency', 
                        currency: 'VND' 
                      }).format(order.total_amount)}
                    </td>
                    <td>
                      <span className={`badge ${getStatusBadgeClass(order.status)}`}>
                        {getStatusText(order.status)}
                      </span>
                    </td>
                    <td>
                      {new Date(order.created_at).toLocaleDateString('vi-VN')}
                    </td>
                    <td>{order.coupon_code || '-'}</td>
                    <td>{order.shipping_fee !== undefined ? new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(order.shipping_fee) : '-'}</td>
                    <td>{order.customer_phone || '-'}</td>
                    <td>
                      <button
                        className="btn btn-sm btn-outline-primary"
                        onClick={() => openStatusModal(order)}
                      >
                        C·∫≠p nh·∫≠t
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}

        {showStatusModal && selectedOrder && (
          <div>
            <div
              className="modal show d-block"
              tabIndex="-1"
              style={{
                zIndex: 1060,
                position: 'fixed',
                top: 0, left: 0, right: 0, bottom: 0,
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center'
              }}
            >
              <div className="modal-dialog">
                <div className="modal-content">
                  <div className="modal-header">
                    <h5 className="modal-title">C·∫≠p nh·∫≠t tr·∫°ng th√°i ƒë∆°n #{selectedOrder.id}</h5>
                    <button type="button" className="btn-close" onClick={() => setShowStatusModal(false)}></button>
                  </div>
                  <div className="modal-body">
                    <div className="d-grid gap-2">
                      <button
                        className="btn btn-outline-info"
                        disabled={selectedOrder.status !== 'pending'}
                        onClick={() => handleStatusChange('confirmed')}
                      >
                        X√°c nh·∫≠n
                      </button>
                      <button
                        className="btn btn-outline-primary"
                        disabled={selectedOrder.status !== 'confirmed'}
                        onClick={() => handleStatusChange('shipped')}
                      >
                        ƒêang giao
                      </button>
                      <button
                        className="btn btn-outline-success"
                        disabled={selectedOrder.status !== 'shipped'}
                        onClick={() => handleStatusChange('delivered')}
                      >
                        ƒê√£ giao
                      </button>
                      <button
                        className="btn btn-outline-danger"
                        disabled={selectedOrder.status === 'delivered' || selectedOrder.status === 'cancelled'}
                        onClick={() => handleStatusChange('cancelled')}
                      >
                        H·ªßy ƒë∆°n
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            </div>
            <div
              className="modal-backdrop show"
              style={{
                zIndex: 1050,
                position: 'fixed',
                top: 0, left: 0, right: 0, bottom: 0
              }}
            ></div>
          </div>
        )}
      </AdminLayout>
      
      <Modal
        show={modal.show}
        onClose={hideModal}
        title={modal.title}
        message={modal.message}
        type={modal.type}
        onConfirm={modal.onConfirm}
        confirmText={modal.confirmText}
        cancelText={modal.cancelText}
        showCancel={modal.showCancel}
      />
    </>
  );
  return (
    <div className="container my-5">
      <h2 className="fw-bold mb-4 text-success">Qu·∫£n l√Ω tin t·ª©c</h2>
      <form className="card p-3 mb-4" onSubmit={handleSubmit}>
        <div className="row g-3">
          <div className="col-md-6">
            <label className="form-label">Ti√™u ƒë·ªÅ</label>
            <input className="form-control" name="title" value={form.title} onChange={handleChange} required />
          </div>
          <div className="col-md-6">
            <label className="form-label">Ng√†y ƒëƒÉng</label>
            <input className="form-control" name="date" type="date" value={form.date} onChange={handleChange} required />
          </div>
          <div className="col-md-12">
            <label className="form-label">T√≥m t·∫Øt</label>
            <textarea className="form-control" name="summary" value={form.summary} onChange={handleChange} rows={2} required />
          </div>
          <div className="col-md-12">
            <label className="form-label">·∫¢nh (URL)</label>
            <input className="form-control" name="image" value={form.image} onChange={handleChange} />
          </div>
        </div>
        <div className="mt-3">
          <button className="btn btn-success" type="submit">
            {editingId ? 'C·∫≠p nh·∫≠t' : 'Th√™m m·ªõi'}
          </button>
          {editingId && (
            <button className="btn btn-secondary ms-2" type="button" onClick={() => { setForm({ title: '', summary: '', image: '', date: '' }); setEditingId(null); }}>
              H·ªßy
            </button>
          )}
        </div>
      </form>
      <div className="table-responsive">
        <table className="table table-bordered align-middle">
          <thead>
            <tr>
              <th>Ti√™u ƒë·ªÅ</th>
              <th>Ng√†y ƒëƒÉng</th>
              <th>T√≥m t·∫Øt</th>
              <th>·∫¢nh</th>
              <th style={{width: 120}}>Thao t√°c</th>
            </tr>
          </thead>
          <tbody>
            {loading ? (
              <tr>
                <td colSpan={5} className="text-center text-muted py-4">ƒêang t·∫£i...</td>
              </tr>
            ) : news.length === 0 ? (
              <tr>
                <td colSpan={5} className="text-center text-muted py-4">Ch∆∞a c√≥ tin t·ª©c n√†o.</td>
              </tr>
            ) : (
              news.map(item => (
                <tr key={item.id}>
                  <td>{item.title}</td>
                  <td>{item.date}</td>
                  <td>{item.summary}</td>
                  <td>
                    {item.image && <img src={item.image} alt="" style={{maxWidth: 80, maxHeight: 60, objectFit: 'cover'}} />}
                  </td>
                  <td>
                    <button className="btn btn-sm btn-outline-primary me-2" onClick={() => handleEdit(item)}>S·ª≠a</button>
                    <button className="btn btn-sm btn-outline-danger" onClick={() => handleDelete(item.id)}>X√≥a</button>
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default AdminNews;
