import React, { useState, useEffect } from 'react';
import AdminLayout from '../../components/AdminLayout';
import Modal from '../../components/Modal';
import { useModal } from '../../hooks/useModal';
import axios from 'axios';

const AdminReviews = () => {
  const [reviews, setReviews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedReview, setSelectedReview] = useState(null);
  const [showReplyModal, setShowReplyModal] = useState(false);
  const [reply, setReply] = useState('');
  const { modal, hideModal, showSuccess, showError, showConfirm } = useModal();

  useEffect(() => {
    fetchReviews();
  }, []);

  const fetchReviews = async () => {
    try {
      const res = await axios.get('/api/reviews/admin');
      setReviews(res.data);
    } catch (error) {
      showError('Lỗi khi tải đánh giá!');
    } finally {
      setLoading(false);
    }
  };

  const updateStatus = async (id, status) => {
    try {
      await axios.put(`/api/reviews/${id}/status`, { status });
      showSuccess('Cập nhật trạng thái thành công!');
      fetchReviews();
    } catch (error) {
      showError('Có lỗi xảy ra!');
    }
  };

  const handleReply = async () => {
    if (!reply.trim()) {
      showError('Vui lòng nhập nội dung phản hồi!');
      return;
    }
    try {
      await axios.put(`/api/reviews/${selectedReview.id}/reply`, { admin_reply: reply });
      showSuccess('Phản hồi thành công!');
      setShowReplyModal(false);
      setReply('');
      setSelectedReview(null);
      fetchReviews();
    } catch (error) {
      showError('Có lỗi xảy ra!');
    }
  };

  const getStatusBadge = (status) => {
    switch (status) {
      case 'pending': return <span className="badge bg-warning">Chờ duyệt</span>;
      case 'approved': return <span className="badge bg-success">Đã duyệt</span>;
      case 'rejected': return <span className="badge bg-danger">Từ chối</span>;
      default: return <span className="badge bg-secondary">{status}</span>;
    }
  };

  return (
    <>
      <AdminLayout>
        <h2 className="mb-4">⭐ Quản lý đánh giá sản phẩm</h2>
        {loading ? (
          <div className="text-center py-5">
            <div className="spinner-border text-primary" role="status">
              <span className="visually-hidden">Loading...</span>
            </div>
          </div>
        ) : (
          <div className="admin-table">
            <table className="table">
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Sản phẩm</th>
                  <th>Người dùng</th>
                  <th>Đánh giá</th>
                  <th>Nội dung</th>
                  <th>Phản hồi admin</th>
                  <th>Trạng thái</th>
                  <th>Ngày</th>
                  <th>Thao tác</th>
                </tr>
              </thead>
              <tbody>
                {reviews.map(r => (
                  <tr key={r.id}>
                    <td>{r.id}</td>
                    <td>{r.product_name}</td>
                    <td>{r.user_name}</td>
                    <td>
                      <span className="text-warning">{'★'.repeat(r.rating)}</span>
                    </td>
                    <td style={{maxWidth:200}}>{r.content}</td>
                    <td style={{maxWidth:200}}>
                      {r.admin_reply ? (
                        <span className="text-success">{r.admin_reply}</span>
                      ) : (
                        <span className="text-muted">-</span>
                      )}
                    </td>
                    <td>{getStatusBadge(r.status)}</td>
                    <td>{new Date(r.created_at).toLocaleDateString('vi-VN')}</td>
                    <td>
                      <div className="d-flex flex-column gap-1">
                        {r.status === 'pending' && (
                          <>
                            <button className="btn btn-sm btn-success" onClick={() => updateStatus(r.id, 'approved')}>Duyệt</button>
                            <button className="btn btn-sm btn-danger" onClick={() => updateStatus(r.id, 'rejected')}>Từ chối</button>
                          </>
                        )}
                        <button className="btn btn-sm btn-outline-primary" onClick={() => { setSelectedReview(r); setShowReplyModal(true); setReply(r.admin_reply || ''); }}>
                          Phản hồi
                        </button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}

        {/* Modal phản hồi */}
        {showReplyModal && selectedReview && (
          <div className="modal show d-block" tabIndex="-1">
            <div className="modal-dialog">
              <div className="modal-content">
                <div className="modal-header">
                  <h5 className="modal-title">Phản hồi đánh giá #{selectedReview.id}</h5>
                  <button type="button" className="btn-close" onClick={() => setShowReplyModal(false)}></button>
                </div>
                <div className="modal-body">
                  <div className="mb-2">
                    <strong>Sản phẩm:</strong> {selectedReview.product_name}
                  </div>
                  <div className="mb-2">
                    <strong>Người dùng:</strong> {selectedReview.user_name}
                  </div>
                  <div className="mb-2">
                    <strong>Nội dung đánh giá:</strong>
                    <div className="bg-light p-2 rounded">{selectedReview.content}</div>
                  </div>
                  <div className="mb-2">
                    <label className="form-label">Phản hồi admin:</label>
                    <textarea
                      className="form-control"
                      rows="3"
                      value={reply}
                      onChange={e => setReply(e.target.value)}
                      placeholder="Nhập phản hồi cho đánh giá này..."
                    />
                  </div>
                </div>
                <div className="modal-footer">
                  <button className="btn btn-secondary" onClick={() => setShowReplyModal(false)}>Đóng</button>
                  <button className="btn btn-primary" onClick={handleReply}>Gửi phản hồi</button>
                </div>
              </div>
            </div>
          </div>
        )}
        {showReplyModal && <div className="modal-backdrop show"></div>}
      </AdminLayout>
      <Modal
        show={modal.show}
        onClose={hideModal}
        title={modal.title}
        message={modal.message}
        type={modal.type}
        onConfirm={modal.onConfirm}
        confirmText={modal.confirmText}
        cancelText={modal.cancelText}
        showCancel={modal.showCancel}
      />
    </>
  );
};



export default AdminReviews;
