import React, { useEffect, useState } from "react";
import { fetchOrders, updateOrderStatus } from "../../services/orderService";
import Modal from "../../components/Modal";
import { useModal } from "../../hooks/useModal";

const AdminOrders = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [showStatusModal, setShowStatusModal] = useState(false);
  const [updating, setUpdating] = useState(false);
  const { modal, hideModal, showSuccess, showError } = useModal();

  useEffect(() => {
    loadOrders();
  }, []);

  const loadOrders = async () => {
    try {
      setLoading(true);
      const data = await fetchOrders();
      setOrders(data);
    } catch (error) {
      showError("Không thể tải danh sách đơn hàng!");
    } finally {
      setLoading(false);
    }
  };

  const handleStatusChange = async (newStatus) => {
    if (!selectedOrder) return;

    try {
      setUpdating(true);
      const response = await updateOrderStatus(selectedOrder.id, newStatus);

      // Cập nhật trực tiếp order trong state
      setOrders((prev) =>
        prev.map((order) =>
          order.id === selectedOrder.id
            ? { ...order, status: newStatus }
            : order
        )
      );

      setShowStatusModal(false);
      setSelectedOrder(null);
      showSuccess("Cập nhật trạng thái đơn hàng thành công!");
    } catch (error) {
      showError("Có lỗi xảy ra khi cập nhật trạng thái!");
    } finally {
      setUpdating(false);
    }
  };

  const getStatusBadge = (status) => {
    const statusConfig = {
      pending: { class: "bg-warning text-dark", text: "Chờ xử lý" },
      confirmed: { class: "bg-info", text: "Đã xác nhận" },
      shipped: { class: "bg-primary", text: "Đã giao hàng" },
      delivered: { class: "bg-success", text: "Đã giao" },
      cancelled: { class: "bg-danger", text: "Đã hủy" },
    };
    const config = statusConfig[status] || {
      class: "bg-secondary",
      text: status,
    };
    return <span className={`badge ${config.class}`}>{config.text}</span>;
  };

  const getStatusText = (status) => {
    const statusText = {
      pending: "Chờ xử lý",
      confirmed: "Đã xác nhận",
      shipped: "Đã giao hàng",
      delivered: "Đã giao",
      cancelled: "Đã hủy",
    };
    return statusText[status] || status;
  };

  if (loading) {
    return (
      <div className="container-fluid py-4">
        <div className="text-center py-5">
          <div className="spinner-border" role="status">
            <span className="visually-hidden">Đang tải...</span>
          </div>
        </div>
      </div>
    );
  }

  return (
    <>
      <div className="container-fluid py-4">
        <div className="d-flex justify-content-between align-items-center mb-4">
          <h2 className="fw-bold">
            <i className="bi bi-box-seam me-2 text-primary"></i>
            Quản lý đơn hàng
          </h2>
          <div className="badge bg-primary fs-6">
            {orders.length} đơn hàng
          </div>
        </div>

        {orders.length === 0 ? (
          <div className="text-center py-5">
            <i className="bi bi-inbox display-1 text-muted"></i>
            <h4 className="text-muted mt-3">Chưa có đơn hàng nào</h4>
          </div>
        ) : (
          <div className="card border-0 shadow-sm">
            <div className="card-body p-0">
              <div className="table-responsive">
                <table className="table table-hover mb-0">
                  <thead className="table-light">
                    <tr>
                      <th className="px-4 py-3">Mã đơn</th>
                      <th className="py-3">Khách hàng</th>
                      <th className="py-3">Sản phẩm</th>
                      <th className="py-3">Tổng tiền</th>
                      <th className="py-3">Trạng thái</th>
                      <th className="py-3">Ngày đặt</th>
                      <th className="py-3 text-center">Thao tác</th>
                    </tr>
                  </thead>
                  <tbody>
                    {orders.map((order) => (
                      <tr key={order.id}>
                        <td className="px-4 py-3">
                          <span className="fw-bold text-primary">#{order.id}</span>
                        </td>
                        <td className="py-3">
                          <div>
                            <div className="fw-medium">{order.user_name}</div>
                            <small className="text-muted">{order.user_email}</small>
                          </div>
                        </td>
                        <td className="py-3">
                          <div
                            className="text-truncate"
                            style={{ maxWidth: "200px" }}
                          >
                            {order.items || "Không có thông tin"}
                          </div>
                        </td>
                        <td className="py-3">
                          <span className="fw-bold text-success">
                            {new Intl.NumberFormat("vi-VN", {
                              style: "currency",
                              currency: "VND",
                            }).format(order.total_amount)}
                          </span>
                        </td>
                        <td className="py-3">{getStatusBadge(order.status)}</td>
                        <td className="py-3">
                          <small>
                            {new Date(order.created_at).toLocaleDateString("vi-VN")}
                          </small>
                        </td>
                        <td className="py-3 text-center">
                          <button
                            className="btn btn-outline-primary btn-sm"
                            onClick={() => {
                              setSelectedOrder(order);
                              setShowStatusModal(true);
                            }}
                            disabled={updating}
                          >
                            <i className="bi bi-pencil-square me-1"></i>
                            Đổi trạng thái
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        )}
      </div>

      {/* Modal đổi trạng thái */}
      {showStatusModal && selectedOrder && (
        <div
          className="modal show d-block"
          tabIndex="-1"
          style={{ backgroundColor: "rgba(0,0,0,0.5)" }}
        >
          <div className="modal-dialog modal-dialog-centered">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">
                  <i className="bi bi-gear me-2"></i>
                  Cập nhật trạng thái đơn hàng #{selectedOrder.id}
                </h5>
                <button
                  type="button"
                  className="btn-close"
                  onClick={() => setShowStatusModal(false)}
                  disabled={updating}
                ></button>
              </div>
              <div className="modal-body">
                <div className="mb-3">
                  <label className="form-label">Trạng thái hiện tại:</label>
                  <div>{getStatusBadge(selectedOrder.status)}</div>
                </div>
                <div className="mb-3">
                  <label className="form-label">Chọn trạng thái mới:</label>
                  <div className="d-grid gap-2">
                    {["pending", "confirmed", "shipped", "delivered", "cancelled"].map(
                      (status) => (
                        <button
                          key={status}
                          className={`btn ${
                            selectedOrder.status === status
                              ? "btn-secondary"
                              : "btn-outline-primary"
                          } text-start`}
                          onClick={() => handleStatusChange(status)}
                          disabled={updating || selectedOrder.status === status}
                        >
                          {updating ? (
                            <span className="spinner-border spinner-border-sm me-2"></span>
                          ) : (
                            <i className="bi bi-check-circle me-2"></i>
                          )}
                          {getStatusText(status)}
                          {selectedOrder.status === status && (
                            <span className="badge bg-light text-dark ms-2">
                              Hiện tại
                            </span>
                          )}
                        </button>
                      )
                    )}
                  </div>
                </div>
              </div>
              <div className="modal-footer">
                <button
                  type="button"
                  className="btn btn-secondary"
                  onClick={() => setShowStatusModal(false)}
                  disabled={updating}
                >
                  Đóng
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      <Modal
        show={modal.show}
        onClose={hideModal}
        title={modal.title}
        message={modal.message}
        type={modal.type}
        onConfirm={modal.onConfirm}
        confirmText={modal.confirmText}
        cancelText={modal.cancelText}
        showCancel={modal.showCancel}
      />
    </>
  );
};

export default AdminOrders;
