import React, { useEffect, useState } from "react";
import AdminLayout from "../../components/AdminLayout";
import { Table, Button, Modal, Form, Badge } from "react-bootstrap";
import axios from "axios";
import { showSuccess } from "../../utils/notify";
import { getAuthHeaders } from "../../utils/auth";

const AdminSizes = () => {
  const [sizes, setSizes] = useState([]);
  const [products, setProducts] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [showProductModal, setShowProductModal] = useState(false);
  const [showErrorModal, setShowErrorModal] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");
  const [editingSize, setEditingSize] = useState(null);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [form, setForm] = useState({
    name: "",
    description: "",
    category_type: "shoes",
    sort_order: 0,
    status: 1,
  });

  // L·∫•y danh s√°ch size
  const fetchSizes = async () => {
    try {
      const res = await axios.get("/api/sizes");
      setSizes(res.data);
    } catch (err) {
      setErrorMessage("L·ªói t·∫£i danh s√°ch size");
      setShowErrorModal(true);
    }
  };

  // L·∫•y danh s√°ch s·∫£n ph·∫©m
  const fetchProducts = async () => {
    try {
      const res = await axios.get("/api/products");
      setProducts(res.data);
    } catch (err) {
      setErrorMessage("L·ªói t·∫£i danh s√°ch s·∫£n ph·∫©m");
      setShowErrorModal(true);
    }
  };

  useEffect(() => {
    fetchSizes();
    fetchProducts();
  }, []);

  // M·ªü modal th√™m/s·ª≠a
  const handleShowModal = (size = null) => {
    setEditingSize(size);
    setForm(
      size || {
        name: "",
        description: "",
        category_type: "shoes",
        sort_order: 0,
        status: 1,
      }
    );
    setShowModal(true);
  };

  const handleCloseModal = () => setShowModal(false);

  // M·ªü modal ch·ªçn s·∫£n ph·∫©m
  const handleShowProductModal = () => setShowProductModal(true);

  const handleCloseProductModal = () => setShowProductModal(false);

  // Ch·ªçn s·∫£n ph·∫©m
  const handleSelectProduct = (product) => {
    setSelectedProduct(product);
    handleCloseProductModal();
  };

  // Submit form
  const handleSubmit = async () => {
    try {
      if (editingSize) {
        await axios.put(`/api/sizes/${editingSize.id}`, form, {
          headers: getAuthHeaders(),
        });
        showSuccess("C·∫≠p nh·∫≠t size th√†nh c√¥ng!");
      } else {
        await axios.post(
          "/api/sizes",
          { ...form, product_id: selectedProduct?.id },
          {
            headers: getAuthHeaders(),
          }
        );
        showSuccess("Th√™m size th√†nh c√¥ng!");
      }
      fetchSizes();
      handleCloseModal();
    } catch (err) {
      setErrorMessage("L·ªói khi l∆∞u size");
      setShowErrorModal(true);
    }
  };

  // X√≥a size
  const handleDelete = async (id) => {
    if (!window.confirm("B·∫°n c√≥ ch·∫Øc mu·ªën x√≥a size n√†y?")) return;
    try {
      await axios.delete(`/api/sizes/${id}`, { headers: getAuthHeaders() });
      showSuccess("X√≥a size th√†nh c√¥ng!");
      fetchSizes();
    } catch (err) {
      setErrorMessage("L·ªói khi x√≥a size");
      setShowErrorModal(true);
    }
  };

  return (
    <AdminLayout>
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>Qu·∫£n l√Ω Size</h2>
        <button
            className="btn btn-admin-primary"
            onClick={() => setShowModal(true)}
          >
            ‚ûï Th√™m s·∫£n ph·∫©m
          </button>
      </div>
{loading ? (
          <div className="text-center py-5">
            <div className="spinner-border text-primary" role="status">
              <span className="visually-hidden">Loading...</span>
            </div>
          </div>
        ) : (
          <div className="admin-table">
            <table className="table">
              <thead>
                <tr>
                  <th>ID</th>
                  <th>H√¨nh ·∫£nh</th>
                  <th>T√™n s·∫£n ph·∫©m</th>
                  <th>Danh m·ª•c</th>
                  <th>Gi√°</th>
                  <th>Gi√° sale</th> {/* Th√™m c·ªôt Gi√° sale */}
                  <th>T·ªìn kho</th>
                  <th>Thao t√°c</th>
                </tr>
              </thead>
              <tbody>
                {products.map((product) => (
                  <tr key={product.id}>
                    <td>{product.id}</td>
                    <td>
                      <img
                        src={
                          product.image_url ||
                          "https://via.placeholder.com/50x50"
                        }
                        alt={product.name}
                        style={{
                          width: "50px",
                          height: "50px",
                          objectFit: "cover",
                        }}
                        className="rounded"
                      />
                    </td>
                    <td>{product.name}</td>
                    <td>{product.category_name}</td>
                    <td>
                      {new Intl.NumberFormat("vi-VN", {
                        style: "currency",
                        currency: "VND",
                      }).format(product.price)}
                    </td>
                    <td>
                      {product.sale_price ? (
                        <span className="text-danger fw-bold">
                          {new Intl.NumberFormat("vi-VN", {
                            style: "currency",
                            currency: "VND",
                          }).format(product.sale_price)}
                        </span>
                      ) : (
                        <span className="text-muted">-</span>
                      )}
                    </td>
                    <td>{product.stock_quantity}</td>
                    <td>
                      <button
                        className="btn btn-sm btn-outline-primary me-2"
                        onClick={() => handleEdit(product)}
                      >
                        S·ª≠a
                      </button>
                      <button
                        className="btn btn-sm btn-outline-danger"
                        onClick={() => handleDelete(product.id)}
                      >
                        X√≥a
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>#</th>
            <th>T√™n</th>
            <th>M√¥ t·∫£</th>
            <th>Lo·∫°i</th>
            <th>Th·ª© t·ª±</th>
            <th>Tr·∫°ng th√°i</th>
            <th>H√†nh ƒë·ªông</th>
          </tr>
        </thead>
        <tbody>
          {sizes.map((size, idx) => (
            <tr key={size.id}>
              <td>{idx + 1}</td>
              <td>{size.name}</td>
              <td>{size.description}</td>
              <td>{size.category_type}</td>
              <td>{size.sort_order}</td>
              <td>
                <Badge bg={size.status === 1 ? "success" : "secondary"}>
                  {size.status === 1 ? "K√≠ch ho·∫°t" : "Kh√¥ng k√≠ch ho·∫°t"}
                </Badge>
              </td>
              <td>
                <Button
                  variant="primary"
                  size="sm"
                  onClick={() => handleShowModal(size)}
                >
                  ‚úèÔ∏è S·ª≠a
                </Button>{" "}
                <Button
                  variant="danger"
                  size="sm"
                  onClick={() => handleDelete(size.id)}
                >
                  üóëÔ∏è X√≥a
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>

      {/* Modal th√™m/s·ª≠a */}
      <Modal show={showModal} onHide={handleCloseModal}>
        <Modal.Header closeButton>
          <Modal.Title>{editingSize ? "S·ª≠a Size" : "Th√™m Size"}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group className="mb-2">
              <Form.Label>T√™n</Form.Label>
              <Form.Control
                type="text"
                value={form.name}
                onChange={(e) => setForm({ ...form, name: e.target.value })}
              />
            </Form.Group>
            <Form.Group className="mb-2">
              <Form.Label>M√¥ t·∫£</Form.Label>
              <Form.Control
                as="textarea"
                value={form.description}
                onChange={(e) =>
                  setForm({ ...form, description: e.target.value })
                }
              />
            </Form.Group>
            <Form.Group className="mb-2">
              <Form.Label>Lo·∫°i s·∫£n ph·∫©m</Form.Label>
              <Form.Select
                value={form.category_type}
                onChange={(e) =>
                  setForm({ ...form, category_type: e.target.value })
                }
              >
                <option value="shoes">Gi√†y</option>
                <option value="clothing">Qu·∫ßn √°o</option>
              </Form.Select>
            </Form.Group>
            <Form.Group className="mb-2">
              <Form.Label>Th·ª© t·ª± s·∫Øp x·∫øp</Form.Label>
              <Form.Control
                type="number"
                value={form.sort_order}
                onChange={(e) =>
                  setForm({ ...form, sort_order: parseInt(e.target.value) })
                }
              />
            </Form.Group>
            <Form.Group>
              <Form.Label>Tr·∫°ng th√°i</Form.Label>
              <Form.Select
                value={form.status}
                onChange={(e) =>
                  setForm({ ...form, status: parseInt(e.target.value) })
                }
              >
                <option value={1}>K√≠ch ho·∫°t</option>
                <option value={0}>Kh√¥ng k√≠ch ho·∫°t</option>
              </Form.Select>
            </Form.Group>
            <Form.Group className="mb-2">
              <Form.Label>S·∫£n ph·∫©m</Form.Label>
              <Button variant="info" onClick={handleShowProductModal}>
                {selectedProduct ? selectedProduct.name : "Ch·ªçn s·∫£n ph·∫©m"}
              </Button>
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseModal}>
            ƒê√≥ng
          </Button>
          <Button variant="primary" onClick={handleSubmit}>
            L∆∞u
          </Button>
        </Modal.Footer>
      </Modal>

      {/* Modal ch·ªçn s·∫£n ph·∫©m */}
      <Modal show={showProductModal} onHide={handleCloseProductModal}>
        <Modal.Header closeButton>
          <Modal.Title>Ch·ªçn s·∫£n ph·∫©m</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Table striped bordered hover>
            <thead>
              <tr>
                <th>#</th>
                <th>T√™n s·∫£n ph·∫©m</th>
                <th>H√†nh ƒë·ªông</th>
              </tr>
            </thead>
            <tbody>
              {products.map((product, idx) => (
                <tr key={product.id}>
                  <td>{product.id}</td>
                  <td>{product.name}</td>
                  <td>
                    <Button
                      variant="primary"
                      size="sm"
                      onClick={() => handleSelectProduct(product)}
                    >
                      Ch·ªçn
                    </Button>
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseProductModal}>
            ƒê√≥ng
          </Button>
        </Modal.Footer>
      </Modal>

      {/* Modal th√¥ng b√°o l·ªói */}
      <Modal show={showErrorModal} onHide={() => setShowErrorModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>L·ªói</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <p>{errorMessage}</p>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowErrorModal(false)}>
            ƒê√≥ng
          </Button>
        </Modal.Footer>
      </Modal>
    </AdminLayout>
  );
};

export default AdminSizes;
