import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { useCart } from "../contexts/CartContext";
import Modal from "../components/Modal";
import { useModal } from "../hooks/useModal";
import ReviewForm from "../components/ReviewForm";
import ReviewList from "../components/ReviewList";
import axios from "axios";
import ScrollToTopButton from "../components/ScrollToTopButton";

const ProductDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { addToCart } = useCart();
  const { modal, hideModal, showSuccess } = useModal();

  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [quantity, setQuantity] = useState(1);
  const [activeTab, setActiveTab] = useState("description");
  const [reviewRefresh, setReviewRefresh] = useState(0);
  const [selectedSize, setSelectedSize] = useState(null);

  useEffect(() => {
    fetchProduct();
  }, [id]);

  const fetchProduct = async () => {
    try {
      const response = await axios.get(`/api/products/${id}`);
      setProduct(response.data);
    } catch (error) {
      console.error("Error fetching product:", error);
      if (error.response?.status === 404) {
        navigate("/products");
      }
    } finally {
      setLoading(false);
    }
  };

  const handleAddToCart = () => {
    if (!selectedSize && product.sizes?.length > 0) {
      alert("Vui lòng chọn size trước khi thêm vào giỏ hàng!");
      return;
    }
    addToCart({
      ...product,
      size: selectedSize?.name || null,
      size_id: selectedSize?.id || null,
      quantity: quantity,
    });
    showSuccess(
      `Đã thêm sản phẩm${
        selectedSize ? " (Size " + selectedSize.name + ")" : ""
      } vào giỏ hàng!`
    );
  };

  const handleReviewSubmitted = () => {
    setReviewRefresh((prev) => prev + 1);
  };

  if (loading) {
    return (
      <div className="container my-5">
        <div className="text-center py-5">
          <div className="spinner-border spinner-border-lg" role="status">
            <span className="visually-hidden">Đang tải...</span>
          </div>
        </div>
      </div>
    );
  }

  if (!product) {
    return (
      <div className="container my-5">
        <div className="text-center py-5">
          <h3>Không tìm thấy sản phẩm</h3>
          <button
            className="btn btn-dark mt-3"
            onClick={() => navigate("/products")}
          >
            Quay lại danh sách sản phẩm
          </button>
        </div>
      </div>
    );
  }

  const currentPrice = product.sale_price || product.price;
  const originalPrice = product.price;
  const discountPercentage = product.sale_price
    ? Math.round(
        ((originalPrice - product.sale_price) / originalPrice) * 100
      )
    : 0;

  return (
    <>
      <div className="container my-5">
        {/* Breadcrumb */}
        <nav aria-label="breadcrumb" className="mb-4">
          <ol className="breadcrumb">
            <li className="breadcrumb-item">
              <button
                className="btn btn-link p-0"
                onClick={() => navigate("/")}
                style={{
                  color: "#00a61eff",
                  textDecoration: "none",
                  fontWeight: 500,
                }}
              >
                Trang chủ
              </button>
            </li>
            <li className="breadcrumb-item">
              <button
                className="btn btn-link p-0"
                onClick={() => navigate("/products")}
                style={{
                  color: "#00a61eff",
                  textDecoration: "none",
                  fontWeight: 500,
                }}
              >
                Sản phẩm
              </button>
            </li>
            <li className="breadcrumb-item active">
              <button
                className="btn btn-link p-0"
                onClick={() => navigate("/products")}
                style={{
                  color: "#00a61eff",
                  textDecoration: "none",
                  fontWeight: 500,
                }}
              >
                {product.name}
              </button>
            </li>
          </ol>
        </nav>

        <div className="row g-5">
          {/* Product Image */}
          <div className="col-lg-5">
            <div className="position-relative">
              <img
                src={
                  product.image_url ||
                  "https://via.placeholder.com/600x400/f8f9fa/6c757d?text=No+Image"
                }
                alt={product.name}
                className="img-fluid rounded shadow-lg w-100"
                style={{ maxHeight: "500px", objectFit: "cover" }}
              />
              {product.stock_quantity === 0 && (
                <div className="position-absolute top-0 start-0 m-3">
                  <span
                    className="badge bg-danger fs-6 p-2"
                    style={{ fontWeight: 600 }}
                  >
                    Hết hàng
                  </span>
                </div>
              )}
              {discountPercentage > 0 && (
                <div className="position-absolute top-0 end-0 m-3">
                  <span className="badge bg-warning text-dark fs-6 p-2">
                    SALE {discountPercentage}%
                  </span>
                </div>
              )}
            </div>
          </div>

          {/* Product Info */}
          <div className="col-lg-7">
            <h1
              className="fw-bold text-dark mb-2"
              style={{ fontSize: "2rem" }}
            >
              {product.name}
            </h1>
            <div className="mb-2">
              <span className="badge bg-primary me-2">
                {product.category_name}
              </span>
              {product.brand_name && (
                <span className="badge bg-success">{product.brand_name}</span>
              )}
            </div>
            <div className="mb-3">
              <span
                className="fw-bold text-danger"
                style={{ fontSize: "2rem" }}
              >
                {new Intl.NumberFormat("vi-VN", {
                  style: "currency",
                  currency: "VND",
                }).format(currentPrice)}
              </span>
              {product.sale_price && (
                <span className="fs-5 text-decoration-line-through text-muted ms-3">
                  {new Intl.NumberFormat("vi-VN", {
                    style: "currency",
                    currency: "VND",
                  }).format(originalPrice)}
                </span>
              )}
            </div>
            <div className="mb-3">
              {product.stock_quantity > 0 ? (
                <span className="text-success fw-bold">
                  Còn hàng ({product.stock_quantity})
                </span>
              ) : (
                <span className="text-danger fw-bold">Hết hàng</span>
              )}
            </div>

            {/* chọn size */}
            {product.sizes && product.sizes.length > 0 && (
              <div className="mb-3">
                <label className="form-label fw-medium">Chọn size:</label>
                <div className="d-flex flex-wrap gap-2">
                  {product.sizes.map((size) => (
                    <button
                      key={size.id}
                      className={`btn ${
                        selectedSize?.id === size.id
                          ? "btn-success"
                          : "btn-outline-success"
                      }`}
                      onClick={() => setSelectedSize(size)}
                      disabled={size.stock_quantity === 0}
                    >
                      {size.name}
                    </button>
                  ))}
                </div>
              </div>
            )}

            {product.short_description && (
              <div className="mb-3">
                <strong>Đặc điểm:</strong>
                <div className="text-muted">
                  {product.short_description}
                </div>
              </div>
            )}

            {/* Add to Cart */}
            {product.stock_quantity > 0 && (
              <div className="mb-4">
                <div className="row g-3 align-items-end">
                  <div className="col-auto">
                    <label className="form-label fw-medium">Số lượng:</label>
                    <div className="input-group" style={{ width: "150px" }}>
                      <button
                        className="btn btn-outline-success"
                        onClick={() =>
                          setQuantity(Math.max(1, quantity - 1))
                        }
                      >
                        <i className="bi bi-dash"></i>
                      </button>
                      <input
                        type="number"
                        className="form-control text-center "
                        value={quantity}
                        onChange={(e) =>
                          setQuantity(
                            Math.max(
                              1,
                              Math.min(
                                product.stock_quantity,
                                parseInt(e.target.value) || 1
                              )
                            )
                          )
                        }
                        min="1"
                        max={product.stock_quantity}
                      />
                      <button
                        className="btn btn-outline-success"
                        onClick={() =>
                          setQuantity(
                            Math.min(product.stock_quantity, quantity + 1)
                          )
                        }
                      >
                        <i className="bi bi-plus"></i>
                      </button>
                    </div>
                  </div>
                  <div className="col">
                    <button
                      className="btn btn-outline-success fw-bold btn-lg px-3 fs-6"
                      style={{ height: "38px" }}
                      onClick={handleAddToCart}
                    >
                      <i className="bi bi-cart-plus me-2"></i>
                      Thêm vào giỏ hàng
                    </button>
                  </div>
                </div>
              </div>
            )}
          </div>
        </div>

        {/* Tabs mô tả và review */}
        <div className="row mt-5">
          <div className="col-12">
            <ul className="nav nav-tabs" id="productTabs">
              <li className="nav-item">
                <button
                  className={`nav-link ${
                    activeTab === "description" ? "active" : ""
                  }`}
                  onClick={() => setActiveTab("description")}
                >
                  <i className="bi bi-file-text me-2"></i>
                  Mô tả sản phẩm
                </button>
              </li>
              <li className="nav-item">
                <button
                  className={`nav-link ${
                    activeTab === "reviews" ? "active" : ""
                  }`}
                  onClick={() => setActiveTab("reviews")}
                >
                  <i className="bi bi-star me-2"></i>
                  Đánh giá & nhận xét
                </button>
              </li>
            </ul>

            <div className="tab-content mt-4">
              {activeTab === "description" && (
                <div className="tab-pane fade show active">
                  {product.description ? (
                    <div className="card">
                      <div className="card-body">
                        <div
                          className="product-description"
                          style={{ whiteSpace: "pre-line" }}
                        >
                          {product.description}
                        </div>
                      </div>
                    </div>
                  ) : (
                    <div className="text-center py-4">
                      <i className="bi bi-file-text display-1 text-muted"></i>
                      <h5 className="text-muted mt-3">
                        Chưa có mô tả sản phẩm
                      </h5>
                    </div>
                  )}
                </div>
              )}

              {activeTab === "reviews" && (
                <div className="tab-pane fade show active">
                  <div className="row">
                    <div className="col-lg-8">
                      <ReviewList
                        productId={id}
                        refreshTrigger={reviewRefresh}
                      />
                    </div>
                    <div className="col-lg-4">
                      <ReviewForm
                        product={product}
                        onReviewSubmitted={handleReviewSubmitted}
                      />
                    </div>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>

      {/* nút scroll */}
      <ScrollToTopButton bottom={88} right={32} zIndex={999} />

      <Modal
        show={modal.show}
        onClose={hideModal}
        title={modal.title}
        message={modal.message}
        type={modal.type}
        onConfirm={modal.onConfirm}
        confirmText={modal.confirmText}
        cancelText={modal.cancelText}
        showCancel={modal.showCancel}
      />
    </>
  );
};

export default ProductDetail;
