import React, { useEffect, useState } from "react";
import AdminLayout from "../../components/AdminLayout";
import Modal from "../../components/Modal";
import axios from "axios";
import { useModal } from "../../hooks/useModal";
import { getAuthHeaders } from "../../utils/auth";

const AdminSizes = () => {
  const [sizes, setSizes] = useState([]);
  const [products, setProducts] = useState([]);

  const [showModal, setShowModal] = useState(false);
  const [editingSize, setEditingSize] = useState(null);
  const [formData, setFormData] = useState({
    name: "",
    description: "",
    category_type: "shoes",
    sort_order: 0,
    status: 1,
  });

  // Modal √°p d·ª•ng size
  const [showApplyModal, setShowApplyModal] = useState(false);
  const [applyData, setApplyData] = useState({
    product_id: "",
    size_id: "",
    stock_quantity: 0,
  });

  const { modal, hideModal, showSuccess, showError, showConfirm } = useModal();

  useEffect(() => {
    fetchSizes();
    fetchProducts();
  }, []);

  const fetchSizes = async () => {
    try {
      const res = await axios.get("/api/sizes");
      console.log("Sizes data:", res.data);
      setSizes(res.data);
    } catch (err) {
      console.error("L·ªói fetch sizes:", err);
      showError("L·ªói t·∫£i danh s√°ch size!");
    }
  };

  const fetchProducts = async () => {
    try {
      const res = await axios.get("/api/products");
      console.log("Products data:", res.data);
      setProducts(res.data);
    } catch (err) {
      console.error("L·ªói fetch products:", err);
      showError("L·ªói t·∫£i danh s√°ch s·∫£n ph·∫©m!");
    }
  };

  const resetForm = () => {
    setFormData({
      name: "",
      description: "",
      category_type: "shoes",
      sort_order: 0,
      status: 1,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.name.trim()) {
      showError("T√™n size kh√¥ng ƒë∆∞·ª£c ƒë·ªÉ tr·ªëng!");
      return;
    }

    try {
      console.log("Submitting size data:", formData);

      if (editingSize) {
        await axios.put(`/api/sizes/${editingSize.id}`, formData, {
          headers: getAuthHeaders(),
        });
        showSuccess("C·∫≠p nh·∫≠t size th√†nh c√¥ng!");
      } else {
        await axios.post("/api/sizes", formData, {
          headers: getAuthHeaders(),
        });
        showSuccess("Th√™m size th√†nh c√¥ng!");
      }
      setShowModal(false);
      setEditingSize(null);
      resetForm();
      fetchSizes();
    } catch (err) {
      console.error("L·ªói submit size:", err);
      const errorMsg = err.response?.data?.error || "L·ªói khi l∆∞u size!";
      showError(errorMsg);
    }
  };

  const handleEdit = (size) => {
    setEditingSize(size);
    setFormData(size);
    setShowModal(true);
  };

  const handleDelete = (id) => {
    showConfirm("B·∫°n c√≥ ch·∫Øc mu·ªën x√≥a size n√†y?", async () => {
      try {
        await axios.delete(`/api/sizes/${id}`, {
          headers: getAuthHeaders(),
        });
        showSuccess("X√≥a size th√†nh c√¥ng!");
        fetchSizes();
      } catch (err) {
        showError("L·ªói khi x√≥a size!");
      }
    });
  };

  const handleModalClose = () => {
    setShowModal(false);
    setEditingSize(null);
    resetForm();
  };

  const handleApply = async (e) => {
    e.preventDefault();

    if (!applyData.product_id || !applyData.size_id) {
      showError("Vui l√≤ng ch·ªçn s·∫£n ph·∫©m v√† size!");
      return;
    }

    try {
      await axios.post("/api/product-sizes", applyData, {
        headers: getAuthHeaders(),
      });
      showSuccess("√Åp d·ª•ng size cho s·∫£n ph·∫©m th√†nh c√¥ng!");
      setShowApplyModal(false);
      setApplyData({ product_id: "", size_id: "", stock_quantity: 0 });
    } catch (err) {
      showError("L·ªói khi √°p d·ª•ng size cho s·∫£n ph·∫©m!");
    }
  };

  return (
    <AdminLayout>
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>üìè Qu·∫£n l√Ω Size</h2>
        <div>
          <button
            className="btn btn-admin-primary me-2"
            onClick={() => setShowModal(true)}
          >
            ‚ûï Th√™m Size
          </button>
          <button
            className="btn btn-success"
            onClick={() => setShowApplyModal(true)}
          >
            ‚úÖ √Åp d·ª•ng Size
          </button>
        </div>
      </div>

      <div className="admin-table">
        <table className="table">
          <thead>
            <tr>
              <th>#</th>
              <th>T√™n</th>
              <th>M√¥ t·∫£</th>
              <th>Lo·∫°i</th>
              <th>Th·ª© t·ª±</th>
              <th>Tr·∫°ng th√°i</th>
              <th>Thao t√°c</th>
            </tr>
          </thead>
          <tbody>
            {sizes.map((size, idx) => (
              <tr key={size.id}>
                <td>{idx + 1}</td>
                <td>{size.name}</td>
                <td>{size.description}</td>
                <td>{size.category_type}</td>
                <td>{size.sort_order}</td>
                <td>
                  {size.status === 1 ? (
                    <span className="badge bg-success">K√≠ch ho·∫°t</span>
                  ) : (
                    <span className="badge bg-secondary">Kh√¥ng k√≠ch ho·∫°t</span>
                  )}
                </td>
                <td>
                  <button
                    className="btn btn-sm btn-outline-primary me-2"
                    onClick={() => handleEdit(size)}
                  >
                    S·ª≠a
                  </button>
                  <button
                    className="btn btn-sm btn-outline-danger"
                    onClick={() => handleDelete(size.id)}
                  >
                    X√≥a
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Modal th√™m/s·ª≠a size */}
      {showModal && (
        <div className="modal show d-block admin-modal" tabIndex="-1">
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">
                  {editingSize ? "S·ª≠a Size" : "Th√™m Size"}
                </h5>
                <button
                  type="button"
                  className="btn-close"
                  onClick={handleModalClose}
                ></button>
              </div>
              <form onSubmit={handleSubmit}>
                <div className="modal-body">
                  {/* T√™n Size */}
                  <div className="mb-3">
                    <label className="form-label">T√™n Size:</label>
                    <input
                      type="text"
                      className="form-control"
                      value={formData.name}
                      onChange={(e) =>
                        setFormData({ ...formData, name: e.target.value })
                      }
                      required
                    />
                  </div>
                  {/* M√¥ t·∫£ */}
                  <div className="mb-3">
                    <label className="form-label">M√¥ t·∫£:</label>
                    <textarea
                      className="form-control"
                      rows="2"
                      value={formData.description}
                      onChange={(e) =>
                        setFormData({
                          ...formData,
                          description: e.target.value,
                        })
                      }
                    />
                  </div>
                  {/* Lo·∫°i */}
                  <div className="mb-3">
                    <label className="form-label">Lo·∫°i:</label>
                    <select
                      className="form-select"
                      value={formData.category_type}
                      onChange={(e) =>
                        setFormData({
                          ...formData,
                          category_type: e.target.value,
                        })
                      }
                    >
                      <option value="shoes">Gi√†y</option>
                      <option value="clothing">Qu·∫ßn √°o</option>
                    </select>
                  </div>
                  {/* Th·ª© t·ª± */}
                  <div className="mb-3">
                    <label className="form-label">Th·ª© t·ª± s·∫Øp x·∫øp:</label>
                    <input
                      type="number"
                      className="form-control"
                      value={formData.sort_order}
                      onChange={(e) =>
                        setFormData({
                          ...formData,
                          sort_order: parseInt(e.target.value),
                        })
                      }
                    />
                  </div>
                  {/* Tr·∫°ng th√°i */}
                  <div className="mb-3">
                    <label className="form-label">Tr·∫°ng th√°i:</label>
                    <select
                      className="form-select"
                      value={formData.status}
                      onChange={(e) =>
                        setFormData({
                          ...formData,
                          status: parseInt(e.target.value),
                        })
                      }
                    >
                      <option value={1}>K√≠ch ho·∫°t</option>
                      <option value={0}>Kh√¥ng k√≠ch ho·∫°t</option>
                    </select>
                  </div>
                </div>

                <div className="modal-footer">
                  <button
                    type="button"
                    className="btn btn-secondary"
                    onClick={handleModalClose}
                  >
                    H·ªßy
                  </button>
                  <button type="submit" className="btn btn-primary">
                    {editingSize ? "C·∫≠p nh·∫≠t" : "Th√™m"}
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}

      {/* Modal √°p d·ª•ng size cho s·∫£n ph·∫©m */}
      {showApplyModal && (
        <div className="modal show d-block admin-modal" tabIndex="-1">
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">√Åp d·ª•ng Size cho s·∫£n ph·∫©m</h5>
                <button
                  type="button"
                  className="btn-close"
                  onClick={() => setShowApplyModal(false)}
                ></button>
              </div>
              <form onSubmit={handleApply}>
                <div className="modal-body">
                  {/* Ch·ªçn s·∫£n ph·∫©m */}
                  <div className="mb-3">
                    <label className="form-label">S·∫£n ph·∫©m:</label>
                    <select
                      className="form-select"
                      value={applyData.product_id}
                      onChange={(e) =>
                        setApplyData({
                          ...applyData,
                          product_id: e.target.value,
                        })
                      }
                      required
                    >
                      <option value="">-- Ch·ªçn s·∫£n ph·∫©m --</option>
                      {products.map((p) => (
                        <option key={p.id} value={p.id}>
                          {p.name}
                        </option>
                      ))}
                    </select>
                  </div>
                  {/* Ch·ªçn size */}
                  <div className="mb-3">
                    <label className="form-label">Size:</label>
                    <select
                      className="form-select"
                      value={applyData.size_id}
                      onChange={(e) =>
                        setApplyData({ ...applyData, size_id: e.target.value })
                      }
                      required
                    >
                      <option value="">-- Ch·ªçn size --</option>
                      {sizes.map((s) => (
                        <option key={s.id} value={s.id}>
                          {s.name}
                        </option>
                      ))}
                    </select>
                  </div>
                  {/* T·ªìn kho */}
                  <div className="mb-3">
                    <label className="form-label">S·ªë l∆∞·ª£ng t·ªìn kho:</label>
                    <input
                      type="number"
                      className="form-control"
                      value={applyData.stock_quantity}
                      onChange={(e) =>
                        setApplyData({
                          ...applyData,
                          stock_quantity: parseInt(e.target.value),
                        })
                      }
                      required
                    />
                  </div>
                </div>
                <div className="modal-footer">
                  <button
                    type="button"
                    className="btn btn-secondary"
                    onClick={() => setShowApplyModal(false)}
                  >
                    H·ªßy
                  </button>
                  <button type="submit" className="btn btn-success">
                    √Åp d·ª•ng
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}

      <Modal
        show={modal.show}
        onClose={hideModal}
        title={modal.title}
        message={modal.message}
        type={modal.type}
        onConfirm={modal.onConfirm}
        confirmText={modal.confirmText}
        cancelText={modal.cancelText}
        showCancel={modal.showCancel}
      />
    </AdminLayout>
  );
};

export default AdminSizes;
