import React, { useEffect, useState } from 'react';

const mockNews = [
  {
    id: 1,
    title: 'Giải cầu lông quốc tế 2024: Việt Nam giành HCV',
    summary: 'Đội tuyển cầu lông Việt Nam đã xuất sắc giành huy chương vàng tại giải quốc tế 2024.',
    image: '/images/news1.jpg',
    date: '2024-06-10'
  },
  {
    id: 2,
    title: 'Top 5 mẫu vợt cầu lông bán chạy nhất tháng 6',
    summary: 'Cùng điểm qua 5 mẫu vợt cầu lông được yêu thích và bán chạy nhất tháng này.',
    image: '/images/news2.jpg',
    date: '2024-06-08'
  },
  {
    id: 3,
    title: 'Hướng dẫn chọn giày cầu lông phù hợp',
    summary: 'Chọn giày cầu lông đúng cách giúp bảo vệ đôi chân và nâng cao thành tích thi đấu.',
    image: '/images/news3.jpg',
    date: '2024-06-05'
  }
];

const News = () => {
  const [news, setNews] = useState([]);

  useEffect(() => {
    // Nếu có API thì fetch ở đây, ví dụ:
    // axios.get('/api/news').then(res => setNews(res.data));
    setNews(mockNews);
  }, []);

  return (
    <div className="container my-5">
      <h2 className="fw-bold mb-4 text-success">Tin tức cầu lông</h2>
      <div className="row g-4">
        {news.map(item => (
          <div className="col-md-4" key={item.id}>
            <div className="card h-100 shadow-sm">
              <img
                src={item.image}
                alt={item.title}
                className="card-img-top"
                style={{ height: 200, objectFit: 'cover' }}
              />
              <div className="card-body d-flex flex-column">
                <h5 className="card-title fw-bold">{item.title}</h5>
                <div className="mb-2 text-muted" style={{ fontSize: '0.95rem' }}>
                  <i className="bi bi-calendar-event"></i> {item.date}
                </div>
                <p className="card-text">{item.summary}</p>
                <div className="mt-auto">
                  <button className="btn btn-outline-success w-100" disabled>
                    Xem chi tiết
                  </button>
                </div>
              </div>
            </div>
          </div>
        ))}
        {news.length === 0 && (
          <div className="col-12 text-center text-muted py-5">
            Chưa có tin tức nào.
          </div>
        )}
      </div>
    </div>
  );
};

export default News;
