import React, { useState, useEffect } from 'react';
import AdminLayout from '../../components/AdminLayout';
import Modal from '../../components/Modal';
import { useModal } from '../../hooks/useModal';
import axios from 'axios';

const AdminReviews = () => {
  const [reviews, setReviews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [statusFilter, setStatusFilter] = useState('all');
  const { modal, hideModal, showSuccess, showError, showConfirm } = useModal();

  useEffect(() => {
    fetchReviews();
  }, [statusFilter]);

  const fetchReviews = async () => {
    try {
      const response = await axios.get('/api/reviews/admin', {
        params: { status: statusFilter }
      });
      setReviews(response.data);
    } catch (error) {
      console.error('Error fetching reviews:', error);
    } finally {
      setLoading(false);
    }
  };

  const updateReviewStatus = async (reviewId, status) => {
    try {
      await axios.put(`/api/reviews/${reviewId}/status`, { status });
      showSuccess('Cập nhật trạng thái thành công!');
      fetchReviews();
    } catch (error) {
      showError('Có lỗi xảy ra!');
    }
  };

  const deleteReview = async (reviewId) => {
    showConfirm(
      'Bạn có chắc muốn xóa đánh giá này?',
      async () => {
        try {
          await axios.delete(`/api/reviews/${reviewId}`);
          showSuccess('Xóa đánh giá thành công!');
          fetchReviews();
        } catch (error) {
          showError('Có lỗi xảy ra!');
        }
      }
    );
  };

  const renderStars = (rating) => {
    return [...Array(5)].map((_, index) => (
      <i
        key={index}
        className={`bi ${index < rating ? 'bi-star-fill text-warning' : 'bi-star text-muted'}`}
      ></i>
    ));
  };

  const getStatusBadge = (status) => {
    const statusConfig = {
      pending: { class: 'bg-warning', text: 'Chờ duyệt' },
      approved: { class: 'bg-success', text: 'Đã duyệt' },
      rejected: { class: 'bg-danger', text: 'Từ chối' }
    };
    const config = statusConfig[status] || { class: 'bg-secondary', text: status };
    return <span className={`badge ${config.class}`}>{config.text}</span>;
  };

  return (
    <>
      <AdminLayout>
        <div className="d-flex justify-content-between align-items-center mb-4">
          <h2>💬 Quản lý đánh giá</h2>
          <select 
            className="form-select w-auto"
            value={statusFilter}
            onChange={(e) => setStatusFilter(e.target.value)}
          >
            <option value="all">Tất cả</option>
            <option value="pending">Chờ duyệt</option>
            <option value="approved">Đã duyệt</option>
            <option value="rejected">Từ chối</option>
          </select>
        </div>

        {loading ? (
          <div className="text-center py-5">
            <div className="spinner-border text-primary" role="status">
              <span className="visually-hidden">Loading...</span>
            </div>
          </div>
        ) : (
          <div className="admin-table">
            <table className="table">
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Sản phẩm</th>
                  <th>Người đánh giá</th>
                  <th>Đánh giá</th>
                  <th>Nội dung</th>
                  <th>Trạng thái</th>
                  <th>Ngày tạo</th>
                  <th>Thao tác</th>
                </tr>
              </thead>
              <tbody>
                {reviews.map(review => (
                  <tr key={review.id}>
                    <td>{review.id}</td>
                    <td>
                      <div className="fw-medium">{review.product_name}</div>
                      {review.verified_purchase && (
                        <small className="text-success">
                          <i className="bi bi-patch-check me-1"></i>
                          Đã mua hàng
                        </small>
                      )}
                    </td>
                    <td>
                      <div>{review.user_name}</div>
                      <small className="text-muted">{review.email}</small>
                    </td>
                    <td>
                      <div className="mb-1">{renderStars(review.rating)}</div>
                      <small className="text-muted">{review.rating}/5</small>
                    </td>
                    <td style={{ maxWidth: '300px' }}>
                      {review.title && (
                        <div className="fw-medium mb-1">{review.title}</div>
                      )}
                      <div className="text-truncate">{review.comment}</div>
                    </td>
                    <td>{getStatusBadge(review.status)}</td>
                    <td>
                      {new Date(review.created_at).toLocaleDateString('vi-VN')}
                    </td>
                    <td>
                      <div className="btn-group">
                        <button 
                          className="btn btn-sm btn-outline-success"
                          onClick={() => updateReviewStatus(review.id, 'approved')}
                          disabled={review.status === 'approved'}
                        >
                          Duyệt
                        </button>
                        <button 
                          className="btn btn-sm btn-outline-warning"
                          onClick={() => updateReviewStatus(review.id, 'rejected')}
                          disabled={review.status === 'rejected'}
                        >
                          Từ chối
                        </button>
                        <button 
                          className="btn btn-sm btn-outline-danger"
                          onClick={() => deleteReview(review.id)}
                        >
                          Xóa
                        </button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </AdminLayout>
      
      <Modal
        show={modal.show}
        onClose={hideModal}
        title={modal.title}
        message={modal.message}
        type={modal.type}
        onConfirm={modal.onConfirm}
        confirmText={modal.confirmText}
        cancelText={modal.cancelText}
        showCancel={modal.showCancel}
      />
    </>
  );
};

export default AdminReviews;
