import React, { createContext, useState, useContext, useEffect } from 'react';

const CartContext = createContext();

export const useCart = () => {
  return useContext(CartContext);
};

export const CartProvider = ({ children }) => {
  const [cartItems, setCartItems] = useState([]);

  useEffect(() => {
    const savedCart = localStorage.getItem('cart');
    if (savedCart) {
      setCartItems(JSON.parse(savedCart));
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('cart', JSON.stringify(cartItems));
  }, [cartItems]);

  const addToCart = (product) => {
    setCartItems(prevItems => {
      // Create unique key for cart item (includes size if applicable)
      const cartKey = `${product.id}_${product.selectedSize?.id || 'no-size'}`;
      
      const existingItem = prevItems.find(item => {
        const itemKey = `${item.id}_${item.selectedSize?.id || 'no-size'}`;
        return itemKey === cartKey;
      });
      
      if (existingItem) {
        return prevItems.map(item => {
          const itemKey = `${item.id}_${item.selectedSize?.id || 'no-size'}`;
          if (itemKey === cartKey) {
            return { ...item, quantity: item.quantity + product.quantity };
          }
          return item;
        });
      } else {
        return [...prevItems, { 
          ...product, 
          cartKey,
          quantity: product.quantity 
        }];
      }
    });
  };

  const removeFromCart = (productId, sizeId = null) => {
    setCartItems(prevItems => {
      return prevItems.filter(item => {
        if (sizeId) {
          return !(item.id === productId && item.selectedSize?.id === sizeId);
        }
        return item.id !== productId;
      });
    });
  };

  const updateQuantity = (productId, newQuantity, sizeId = null) => {
    if (newQuantity <= 0) {
      removeFromCart(productId, sizeId);
      return;
    }
    
    setCartItems(prevItems => {
      return prevItems.map(item => {
        if (sizeId) {
          if (item.id === productId && item.selectedSize?.id === sizeId) {
            return { ...item, quantity: newQuantity };
          }
        } else {
          if (item.id === productId) {
            return { ...item, quantity: newQuantity };
          }
        }
        return item;
      });
    });
  };

  const clearCart = () => {
    setCartItems([]);
  };

  const getCartTotal = () => {
    return cartItems.reduce((total, item) => total + (item.price * item.quantity), 0);
  };

  const getCartItemsCount = () => {
    return cartItems.reduce((total, item) => total + item.quantity, 0);
  };

  const value = {
    cartItems,
    addToCart,
    removeFromCart,
    updateQuantity,
    clearCart,
    getCartTotal,
    getCartItemsCount
  };

  return (
    <CartContext.Provider value={value}>
      {children}
    </CartContext.Provider>
  );
};
