import React, { useState, useEffect } from 'react';
import AdminLayout from '../../components/AdminLayout';
import Modal from '../../components/Modal';
import { useModal } from '../../hooks/useModal';
import axios from 'axios';

const AdminContacts = () => {
  const [contacts, setContacts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedContact, setSelectedContact] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [reply, setReply] = useState('');
  const { modal, hideModal, showSuccess, showError } = useModal();

  useEffect(() => {
    fetchContacts();
  }, []);

  const fetchContacts = async () => {
    try {
      const response = await axios.get('/api/contacts/admin');
      setContacts(response.data);
    } catch (error) {
      console.error('Error fetching contacts:', error);
    } finally {
      setLoading(false);
    }
  };

  const updateContactStatus = async (contactId, status) => {
    try {
      await axios.put(`/api/contacts/${contactId}/status`, { status });
      showSuccess('C·∫≠p nh·∫≠t tr·∫°ng th√°i th√†nh c√¥ng!');
      fetchContacts();
    } catch (error) {
      console.error('Error updating contact status:', error);
      showError(error.response?.data?.message || 'C√≥ l·ªói x·∫£y ra!');
    }
  };

  const handleReply = async () => {
    if (!reply.trim()) {
      showError('Vui l√≤ng nh·∫≠p n·ªôi dung tr·∫£ l·ªùi!');
      return;
    }

    try {
      await axios.put(`/api/contacts/${selectedContact.id}/reply`, { 
        admin_reply: reply,
        status: 'resolved'
      });
      showSuccess('Tr·∫£ l·ªùi th√†nh c√¥ng!');
      setShowModal(false);
      setReply('');
      setSelectedContact(null);
      fetchContacts();
    } catch (error) {
      console.error('Error sending reply:', error);
      showError(error.response?.data?.message || 'C√≥ l·ªói x·∫£y ra!');
    }
  };

  const getStatusBadgeClass = (status) => {
    switch (status) {
      case 'new': return 'bg-primary';
      case 'processing': return 'bg-warning';
      case 'resolved': return 'bg-success';
      default: return 'bg-secondary';
    }
  };

  const getStatusText = (status) => {
    switch (status) {
      case 'new': return 'M·ªõi';
      case 'processing': return 'ƒêang x·ª≠ l√Ω';
      case 'resolved': return 'ƒê√£ gi·∫£i quy·∫øt';
      default: return status;
    }
  };

  return (
    <>
      <AdminLayout>
        <h2 className="mb-4">üìû Qu·∫£n l√Ω li√™n h·ªá</h2>

        {loading ? (
          <div className="text-center py-5">
            <div className="spinner-border text-primary" role="status">
              <span className="visually-hidden">Loading...</span>
            </div>
          </div>
        ) : (
          <div className="admin-table">
            <table className="table">
              <thead>
                <tr>
                  <th>ID</th>
                  <th>H·ªç t√™n</th>
                  <th>Email</th>
                  <th>S·ªë ƒëi·ªán tho·∫°i</th>
                  <th>Ch·ªß ƒë·ªÅ</th>
                  <th>Tr·∫°ng th√°i</th>
                  <th>Ng√†y g·ª≠i</th>
                  <th>Thao t√°c</th>
                </tr>
              </thead>
              <tbody>
                {contacts.map(contact => (
                  <tr key={contact.id}>
                    <td>{contact.id}</td>
                    <td>{contact.name}</td>
                    <td>{contact.email}</td>
                    <td>{contact.phone || 'Ch∆∞a c√≥'}</td>
                    <td>
                      <span className="text-truncate d-inline-block" style={{maxWidth: '200px'}}>
                        {contact.subject}
                      </span>
                    </td>
