import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';

const formatDate = (dateStr) => {
  if (!dateStr) return '';
  const d = new Date(dateStr);
  return d.toLocaleDateString('vi-VN');
};

const NewsDetail = () => {
  const { id } = useParams();
  const [news, setNews] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get(`/api/news/${id}`)
      .then(res => setNews(res.data))
      .catch(() => setNews(null))
      .finally(() => setLoading(false));
  }, [id]);

  if (loading) {
    return (
      <div className="container py-5 text-center">
        <div className="spinner-border" role="status"></div>
        <div className="mt-3 text-muted">Đang tải chi tiết tin tức...</div>
      </div>
    );
  }

  if (!news) {
    return (
      <div className="container py-5 text-center">
        <div className="alert alert-warning">Không tìm thấy tin tức.</div>
        <Link to="/news" className="btn btn-outline-success mt-3">Quay lại danh sách tin tức</Link>
      </div>
    );
  }

  return (
    <div className="container my-5" style={{maxWidth: 800}}>
      <Link to="/news" className="btn btn-link mb-3 px-0">
        <i className="bi bi-arrow-left"></i> Quay lại danh sách tin tức
      </Link>
      <div className="card shadow-sm border-0">
        {news.image && (
          <img
            src={news.image}
            alt={news.title}
            className="card-img-top"
            style={{maxHeight: 350, objectFit: 'cover'}}
          />
        )}
        <div className="card-body">
          <h2 className="fw-bold mb-2">{news.title}</h2>
          <div className="mb-3 text-muted">
            <i className="bi bi-calendar-event"></i> {formatDate(news.date)}
          </div>
          <div className="mb-3">{news.summary}</div>
        </div>
      </div>
    </div>
  );
};

export default NewsDetail;
